overnor()":{"notice":"Returns true if the caller is the current Governor."},"isSupportedAsset(address)":{"notice":"Returns whether the vault supports the asset"},"maxSupplyDiff()":{"notice":"Max difference between total supply and total value of assets. 18 decimals."},"mint(address,uint256,uint256)":{"notice":"Deposit a supported asset and mint OTokens."},"netOusdMintForStrategyThreshold()":{"notice":"How much net total OTokens are allowed to be minted by all strategies"},"netOusdMintedForStrategy()":{"notice":"How much OTokens are currently minted by the strategy"},"ousdMetaStrategy()":{"notice":"Metapool strategy that is allowed to mint/burn OTokens without changing collateral"},"priceProvider()":{"notice":"Address of the Oracle price provider contract"},"priceUnitMint(address)":{"notice":"Returns the total price in 18 digit units for a given asset.      Never goes above 1, since that is how we price mints."},"priceUnitRedeem(address)":{"notice":"Returns the total price in 18 digit unit for a given asset.      Never goes below 1, since that is how we price redeems"},"rebase()":{"notice":"Calculate the total value of assets held by the Vault and all      strategies and update the supply of OTokens."},"rebasePaused()":{"notice":"pause rebasing if true"},"rebaseThreshold()":{"notice":"OToken mints over this amount automatically rebase. 18 decimals."},"redeem(uint256,uint256)":{"notice":"Withdraw a supported asset and burn OTokens."},"redeemAll(uint256)":{"notice":"Withdraw a supported asset and burn all OTokens."},"redeemFeeBps()":{"notice":"Redemption fee in basis points. eg 50 = 0.5%"},"setAdminImpl(address)":{"notice":"set the implementation for the admin, this needs to be in a base class else we cannot set it"},"strategistAddr()":{"notice":"Address of the Strategist"},"totalValue()":{"notice":"Determine the total value of assets held by the vault and its         strategies."},"transferGovernance(address)":{"notice":"Transfers Governance of the contract to a new a