// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "./EchidnaTestMintBurn.sol";
import "./Debugger.sol";

/**
 * @title Mixin for testing approval related functions
 * @author Rappie
 */
contract EchidnaTestApproval is EchidnaTestMintBurn {
    /**
     * @notice Performing `transferFrom` with an amount inside the allowance should not revert
     * @param authorizedAcc The account that is authorized to transfer
     * @param fromAcc The account that is transferring
     * @param toAcc The account that is receiving
     * @param amount The amount to transfer
     */
    function testTransferFromShouldNotRevert(
        uint8 authorizedAcc,
        uint8 fromAcc,
        uint8 toAcc,
        uint256 amount
    ) public {
        address authorized = getAccount(authorizedAcc);
        address from = getAccount(fromAcc);
        address to = getAccount(toAcc);

        require(amount <= ousd.balanceOf(from));
        require(amount <= ousd.allowance(from, authorized));

        hevm.prank(authorized);
        // slither-disable-next-line unchecked-transfer
        try ousd.transferFrom(from, to, amount) {
            // pass
        } catch {
            assert(false);
        }
    }

    /**
     * @notice Performing `transferFrom` with an amount outside the allowance should revert
     * @param authorizedAcc The account that is authorized to transfer
     * @param fromAcc The account that is transferring
     * @param toAcc The account that is receiving
     * @param amount The amount to transfer
     */
    function testTransferFromShouldRevert(
        uint8 authorizedAcc,
        uint8 fromAcc,
        uint8 toAcc,
        uint256 amount
    ) public {
        address authorized = getAccount(authorizedAcc);
        address from = getAccount(fromAcc);
        address to = getAccount(toAcc);

        require(amount > 0);
        require(
            !(amount <= ousd.balanceOf(from) &&
                amount <= ousd.allowance(from, authorized))
        );

        hevm.prank(authorized);
        // slither-disable-next-line unchecked-transfer
        try ousd.transferFrom(from, to, amount) {
            assert(false);
        } catch {
            // pass
        }
    }

    /**
     * @notice Approving an amount should update the allowance and overwrite any previous allowance
     * @param ownerAcc The account that is approving
     * @param spenderAcc The account that is being approved
     * @param amount The amount to approve
     */
    function testApprove(
        uint8 ownerAcc,
        uint8 spenderAcc,
        uint256 amount
    ) public {
        address owner = getAccount(ownerAcc);
        address spender = getAccount(spenderAcc);

        approve(ownerAcc, spenderAcc, amount);
        uint256 allowanceAfter1 = ousd.allowance(owner, spender);

        assert(allowanceAfter1 == amount);

        approve(ownerAcc, spenderAcc, amount / 2);
        uint256 allowanceAfter2 = ousd.allowance(owner, spender);

        assert(allowanceAfter2 == amount / 2);
    }
}

contracts/echidna/EchidnaTestMintBurn.so.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "./EchidnaDebug.sol";
import "./EchidnaTestAccounting.sol";

/**
 * @title Mixin for testing Mint and Burn functions
 * @author Rappie
 */
contract EchidnaTestMintBurn is EchidnaTestAccounting {
    /**
     * @notice Minting 0 tokens should not affect account balance
     * @param targetAcc Account to mint to
     */
    function testMintZeroBalance(uint8 targetAcc) public {
        address target = getAccount(targetAcc);

        uint256 balanceBefore = ousd.balanceOf(target);
        mint(targetAcc, 0);
        uint256 balanceAfter = ousd.balanceOf(target);

        assert(balanceAfter == balanceBefore);
    }

    /**
     * @notice Burning 0 tokens should not affect account balance
     * @param targetAcc Account to burn from
     */
    function testBurnZeroBalance(uint8 targetAcc) public {
        address target = getAccount(targetAcc);

        uint256 balanceBefore = ousd.balanceOf(target);
        burn(targetAcc, 0);
        uint256 balanceAfter = ousd.balanceOf(target);

        assert(balanceAfter == balanceBefore);
    }

    /**
     * @notice Minting tokens must increase the account balance by at least amount
     * @param targetAcc Account to mint to
     * @param amount Amount to mint
     * @custom:error testMintBalance(uint8,uint256): failed!
     *   Call sequence:
     *       changeSupply(1)
     *       testMintBalance(0,1)
     *   Event sequence:
     *       Debug(balanceBefore, 0)
     *       Debug(balanceAfter, 0)
     */
    function testMintBalance(uint8 targetAcc, uint256 amount)
        public
        hasKnownIssue
        hasKnownIssueWithinLimits
    {
        address target = getAccount(targetAcc);

        uint256 balanceBefore = ousd.balanceOf(target);
        uint256 amountMinted = mint(targetAcc, amount);
        uint256 balanceAfter = ousd.balanceOf(target);

        Debugger.log("amountMinted", amountMinted);
        Debugger.log("balanceBefore", balanceBefore);
        Debugger.log("balanceAfter", balanceAfter);

        assert(balanceAfter >= balanceBefore + amountMinted);
    }

    /**
     * @notice Burning tokens must decrease the account balance by at least amount
     * @param targetAcc Account to burn from
     * @param amount Amount to burn
     * @custom:error testBurnBalance(uint8,uint256): failed!
     *   Call sequence:
     *       changeSupply(1)
     *       mint(0,3)
     *       testBurnBalance(0,1)
     *   Event sequence:
     *       Debug(balanceBefore, 2)
     *       Debug(balanceAfter, 2)
     */
    function testBurnBalance(uint8 targetAcc, uint256 amount)
        public
        hasKnownIssue
        hasKnownIssueWithinLimits
    {
        address target = getAccount(targetAcc);

        uint256 balanceBefore = ousd.balanceOf(target);
        burn(targetAcc, amount);
        uint256 balanceAfter = ousd.balanceOf(target);

        Debugger.log("balanceBefore", balanceBefore);
        Debugger.log("balanceAfter", balanceAfter);

        assert(balanceAfter <= balanceBefore - amount);
    }

    /**
     * @notice Minting tokens should not increase the account balance by less than rounding error above amount
     * @param targetAcc Account to mint to
     * @param amount Amount to mint
     */
    function testMintBalanceRounding(uint8 targetAcc, uint256 amount) public {
        address target = getAccount(targetAcc);

        uint256 balanceBefore = ousd.balanceOf(target);
        uint256 amountMinted = mint(targetAcc, amount);
        uint256 balanceAfter = ousd.balanceOf(target);

        int256 delta = int256(balanceAfter) - int256(balanceBefore);

        // delta == amount, if no error
        // delta < amount,  if too little is minted
        // delta > amount,  if too much is minted
        int256 error = int256(amountMinted) - delta;

        assert(error >= 0);
        assert(error <= int256(MINT_ROUNDING_ERROR));
    }

    /**
     * @notice A burn of an account balance must result in a zero balance
     * @param targetAcc Account to burn from
     */
    function testBurnAllBalanceToZero(uint8 targetAcc) public hasKnownIssue {
        address target = getAccount(targetAcc);

        burn(targetAcc, ousd.balanceOf(target));
        assert(ousd.balanceOf(target) == 0);
    }

    /**
     * @notice You should always be able to burn an account's balance
     * @param targetAcc Account to burn from
     */
    function testBurnAllBalanceShouldNotRevert(uint8 targetAcc)
        public
        hasKnownIssue
    {
        address target = getAccount(targetAcc);
        uint256 balance = ousd.balanceOf(target);

        hevm.prank(ADDRESS_VAULT);
        try ousd.burn(target, balance) {
            assert(true);
        } catch {
            assert(false);
        }
    }
}

contracts/echidna/EchidnaTestSupply.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "./EchidnaDebug.sol";
import "./EchidnaTestTransfer.sol";

import { StableMath } from "../utils/StableMath.sol";

/**
 * @title Mixin for testing supply related functions
 * @author Rappie
 */
contract EchidnaTestSupply is EchidnaTestTransfer {
    using StableMath for uint256;

    uint256 prevRebasingCreditsPerToken = type(uint256).max;

    /**
     * @notice After a `changeSupply`, the total supply should exactly
     * match the target total supply. (This is needed to ensure successive
     * rebases are correct).
     * @param supply New total supply
     * @custom:error testChangeSupply(uint256): failed!
     *   Call sequence:
     *       testChangeSupply(1044505275072865171609)
     *   Event sequence:
     *       TotalSupplyUpdatedHighres(1044505275072865171610, 1000000000000000000000000, 957391048054055578595)
     */
    function testChangeSupply(uint256 supply)
        public
        hasKnownIssue
        hasKnownIssueWithinLimits
    {
        hevm.prank(ADDRESS_VAULT);
        ousd.changeSupply(supply);

        assert(ousd.totalSupply() == supply);
    }

    /**
     * @notice The total supply must not be less than the sum of account balances.
     * (The difference will go into future rebases)
     * @custom:error testTotalSupplyLessThanTotalBalance(): failed!
     *   Call sequence:
     *     mint(0,1)
     *     changeSupply(1)
     *     optOut(64)
     *     transfer(0,64,1)
     *     testTotalSupplyLessThanTotalBalance()
     *   Event sequence:
     *     Debug(totalSupply, 1000000000000000001000001)
     *     Debug(totalBalance, 1000000000000000001000002)
     */
    function testTotalSupplyLessThanTotalBalance()
        public
        hasKnownIssue
        hasKnownIssueWithinLimits
    {
        uint256 totalSupply = ousd.totalSupply();
        uint256 totalBalance = getTotalBalance();

        Debugger.log("totalSupply", totalSupply);
        Debugger.log("totalBalance", totalBalance);

        assert(totalSupply >= totalBalance);
    }

    /**
     * @notice Non-rebasing supply should not be larger than total supply
     * @custom:error testNonRebasingSupplyVsTotalSupply(): failed!
     *   Call sequence:
     *     mint(0,2)
     *     changeSupply(3)
     *     burn(0,1)
     *     optOut(0)
     *     testNonRebasingSupplyVsTotalSupply()
     */
    function testNonRebasingSupplyVsTotalSupply() public hasKnownIssue {
        uint256 nonRebasingSupply = ousd.nonRebasingSupply();
        uint256 totalSupply = ousd.totalSupply();

        assert(nonRebasingSupply <= totalSupply);
    }

    /**
     * @notice Global `rebasingCreditsPerToken` should never increase
     * @custom:error testRebasingCreditsPerTokenNotIncreased(): failed!
     *   Call sequence:
     *     testRebasingCreditsPerTokenNotIncreased()
     *     changeSupply(1)
     *     testRebasingCreditsPerTokenNotIncreased()
     */
    function testRebasingCreditsPerTokenNotIncreased() public hasKnownIssue {
        uint256 curRebasingCreditsPerToken = ousd
            .rebasingCreditsPerTokenHighres();

        Debugger.log(
            "prevRebasingCreditsPerToken",
            prevRebasingCreditsPerToken
        );
        Debugger.log("curRebasingCreditsPerToken", curRebasingCreditsPerToken);

        assert(curRebasingCreditsPerToken <= prevRebasingCreditsPerToken);

        prevRebasingCreditsPerToken = curRebasingCreditsPerToken;
    }

    /**
     * @notice The rebasing credits per token ratio must greater than zero
     */
    function testRebasingCreditsPerTokenAboveZero() public {
        assert(ousd.rebasingCreditsPerTokenHighres() > 0);
    }

    /**
     * @notice The sum of all non-rebasing balances should not be larger than
     * non-rebasing supply
     * @custom:error testTotalNonRebasingSupplyLessThanTotalBalance(): failed!
     *   Call sequence
     *     mint(0,2)
     *     changeSupply(1)
     *     optOut(0)
     *     burn(0,1)
     *     testTotalNonRebasingSupplyLessThanTotalBalance()
     *   Event sequence:
     *     Debug(totalNonRebasingSupply, 500000000000000000000001)
     *     Debug(totalNonRebasingBalance, 500000000000000000000002)
     */
    function testTotalNonRebasingSupplyLessThanTotalBalance()
        public
        hasKnownIssue
        hasKnownIssueWithinLimits
    {
        uint256 totalNonRebasingSupply = ousd.nonRebasingSupply();
        uint256 totalNonRebasingBalance = getTotalNonRebasingBalance();

        Debugger.log("totalNonRebasingSupply", totalNonRebasingSupply);
        Debugger.log("totalNonRebasingBalance", totalNonRebasingBalance);

        assert(totalNonRebasingSupply >= totalNonRebasingBalance);
    }

    /**
     * @notice An accounts credits / credits per token should not be larger it's balance
     * @param targetAcc The account to check
     */
    function testCreditsPerTokenVsBalance(uint8 targetAcc) public {
        address target = getAccount(targetAcc);

        (uint256 credits, uint256 creditsPerToken, ) = ousd
            .creditsBalanceOfHighres(target);
        uint256 expectedBalance = credits.divPrecisely(creditsPerToken);

        uint256 balance = ousd.balanceOf(target);

        Debugger.log("credits", credits);
        Debugger.log("creditsPerToken", creditsPerToken);
        Debugger.log("expectedBalance", expectedBalance);
        Debugger.log("balance", balance);

        assert(expectedBalance == balance);
    }
}

contracts/echidna/EchidnaTestTransfer.so.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "./EchidnaDebug.sol";
import "./Debugger.sol";

/**
 * @title Mixin for testing transfer related functions
 * @author Rappie
 */
contract EchidnaTestTransfer is EchidnaDebug {
    /**
     * @notice The receiving account's balance after a transfer must not increase by
     * less than the amount transferred
     * @param fromAcc Account to transfer from
     * @param toAcc Account to transfer to
     * @param amount Amount to transfer
     * @custom:error testTransferBalanceReceivedLess(uint8,uint8,uint256): failed!
     *   Call sequence:
     *     changeSupply(1)
     *     mint(64,2)
     *     testTransferBalanceReceivedLess(64,0,1)
     *   Event sequence:
     *     Debug(totalSupply, 1000000000000000000500002)
     *     Debug(toBalBefore, 0)
     *     Debug(toBalAfter, 0)
     */
    function testTransferBalanceReceivedLess(
        uint8 fromAcc,
        uint8 toAcc,
        uint256 amount
    ) public hasKnownIssue hasKnownIssueWithinLimits {
        address from = getAccount(fromAcc);
        address to = getAccount(toAcc);

        require(from != to);

        uint256 toBalBefore = ousd.balanceOf(to);
        transfer(fromAcc, toAcc, amount);
        uint256 toBalAfter = ousd.balanceOf(to);

        Debugger.log("totalSupply", ousd.totalSupply());
        Debugger.log("toBalBefore", toBalBefore);
        Debugger.log("toBalAfter", toBalAfter);

        assert(toBalAfter >= toBalBefore + amount);
    }

    /**
     * @notice The receiving account's balance after a transfer must not
     * increase by more than the amount transferred
     * @param fromAcc Account to transfer from
     * @param toAcc Account to transfer to
     * @param amount Amount to transfer
     */
    function testTransferBalanceReceivedMore(
        uint8 fromAcc,
        uint8 toAcc,
        uint256 amount
    ) public {
        address from = getAccount(fromAcc);
        address to = getAccount(toAcc);

        require(from != to);

        uint256 toBalBefore = ousd.balanceOf(to);
        transfer(fromAcc, toAcc, amount);
        uint256 toBalAfter = ousd.balanceOf(to);

        Debugger.log("totalSupply", ousd.totalSupply());
        Debugger.log("toBalBefore", toBalBefore);
        Debugger.log("toBalAfter", toBalAfter);

        assert(toBalAfter <= toBalBefore + amount);
    }

    /**
     * @notice The sending account's balance after a transfer must not
     * decrease by less than the amount transferred
     * @param fromAcc Account to transfer from
     * @param toAcc Account to transfer to
     * @param amount Amount to transfer
     * @custom:error testTransferBalanceSentLess(uint8,uint8,uint256): failed!
     *   Call sequence:
     *     mint(0,1)
     *     changeSupply(1)
     *     testTransferBalanceSentLess(0,64,1)
     *   Event sequence:
     *     Debug(totalSupply, 1000000000000000000500001)
     *     Debug(fromBalBefore, 1)
     *     Debug(fromBalAfter, 1)
     */
    function testTransferBalanceSentLess(
        uint8 fromAcc,
        uint8 toAcc,
        uint256 amount
    ) public hasKnownIssue hasKnownIssueWithinLimits {
        address from = getAccount(fromAcc);
        address to = getAccount(toAcc);

        require(from != to);

        uint256 fromBalBefore = ousd.balanceOf(from);
        transfer(fromAcc, toAcc, amount);
        uint256 fromBalAfter = ousd.balanceOf(from);

        Debugger.log("totalSupply", ousd.totalSupply());
        Debugger.log("fromBalBefore", fromBalBefore);
        Debugger.log("fromBalAfter", fromBalAfter);

        assert(fromBalAfter <= fromBalBefore - amount);
    }

    /**
     * @notice The sending account's balance after a transfer must not
     * decrease by more than the amount transferred
     * @param fromAcc Account to transfer from
     * @param toAcc Account to transfer to
     * @param amount Amount to transfer
     */
    function testTransferBalanceSentMore(
        uint8 fromAcc,
        uint8 toAcc,
        uint256 amount
    ) public {
        address from = getAccount(fromAcc);
        address to = getAccount(toAcc);

        require(from != to);

        uint256 fromBalBefore = ousd.balanceOf(from);
        transfer(fromAcc, toAcc, amount);
        uint256 fromBalAfter = ousd.balanceOf(from);

        Debugger.log("totalSupply", ousd.totalSupply());
        Debugger.log("fromBalBefore", fromBalBefore);
        Debugger.log("fromBalAfter", fromBalAfter);

        assert(fromBalAfter >= fromBalBefore - amount);
    }

    /**
     * @notice The receiving account's balance after a transfer must not
     * increase by less than the amount transferred (minus rounding error)
     * @param fromAcc Account to transfer from
     * @param toAcc Account to transfer to
     * @param amount Amount to transfer
     */
    function testTransferBalanceReceivedLessRounding(
        uint8 fromAcc,
        uint8 toAcc,
        uint256 amount
    ) public {
        address from = getAccount(fromAcc);
        address to = getAccount(toAcc);

        require(from != to);

        uint256 toBalBefore = ousd.balanceOf(to);
        transfer(fromAcc, toAcc, amount);
        uint256 toBalAfter = ousd.balanceOf(to);

        int256 toDelta = int256(toBalAfter) - int256(toBalBefore);

        // delta == amount, if no error
        // delta < amount,  if too little is sent
        // delta > amount,  if too much is sent
        int256 error = int256(amount) - toDelta;

        Debugger.log("totalSupply", ousd.totalSupply());
        Debugger.log("toBalBefore", toBalBefore);
        Debugger.log("toBalAfter", toBalAfter);
        Debugger.log("toDelta", toDelta);
        Debugger.log("error", error);

        assert(error >= 0);
        assert(error <= int256(TRANSFER_ROUNDING_ERROR));
    }

    /**
     * @notice The sending account's balance after a transfer must
     * not decrease by less than the amount transferred (minus rounding error)
     * @param fromAcc Account to transfer from
     * @param toAcc Account to transfer to
     * @param amount Amount to transfer
     */
    function testTransferBalanceSentLessRounding(
        uint8 fromAcc,
        uint8 toAcc,
        uint256 amount
    ) public {
        address from = getAccount(fromAcc);
        address to = getAccount(toAcc);

        require(from != to);

        uint256 fromBalBefore = ousd.balanceOf(from);
        transfer(fromAcc, toAcc, amount);
        uint256 fromBalAfter = ousd.balanceOf(from);

        int256 fromDelta = int256(fromBalAfter) - int256(fromBalBefore);

        // delta == -amount, if no error
        // delta < -amount,  if too much is sent
        // delta > -amount,  if too little is sent
        int256 error = int256(amount) + fromDelta;

        Debugger.log("totalSupply", ousd.totalSupply());
        Debugger.log("fromBalBefore", fromBalBefore);
        Debugger.log("fromBalAfter", fromBalAfter);
        Debugger.log("fromDelta", fromDelta);
        Debugger.log("error", error);

        assert(error >= 0);
        assert(error <= int256(TRANSFER_ROUNDING_ERROR));
    }

    /**
     * @notice An account should always be able to successfully transfer
     * an amount within its balance.
     * @param fromAcc Account to transfer from
     * @param toAcc Account to transfer to
     * @param amount Amount to transfer
     * @custom:error testTransferWithinBalanceDoesNotRevert(uint8,uint8,uint8): failed!
     *   Call sequence:
     *       mint(0,1)
     *       changeSupply(3)
     *       optOut(0)
     *       testTransferWithinBalanceDoesNotRevert(0,128,2)
     *       optIn(0)
     *       testTransferWithinBalanceDoesNotRevert(128,0,1)
     *   Event sequence:
     *       error Revert Panic(17): SafeMath over-/under-flows
     */
    function testTransferWithinBalanceDoesNotRevert(
        uint8 fromAcc,
        uint8 toAcc,
        uint256 amount
    ) public hasKnownIssue {
        address from = getAccount(fromAcc);
        address to = getAccount(toAcc);

        require(amount > 0);
        amount = amount % ousd.balanceOf(from);

        Debugger.log("Total supply", ousd.totalSupply());

        hevm.prank(from);
        // slither-disable-next-line unchecked-transfer
        try ousd.transfer(to, amount) {
            assert(true);
        } catch {
            assert(false);
        }
    }

    /**
     * @notice An account should never be able to successfully transfer
     * an amount greater than their balance.
     * @param fromAcc Account to transfer from
     * @param toAcc Account to transfer to
     * @param amount Amount to transfer
     */
    function testTransferExceedingBalanceReverts(
        uint8 fromAcc,
        uint8 toAcc,
        uint256 amount
    ) public {
        address from = getAccount(fromAcc);
        address to = getAccount(toAcc);

        amount = ousd.balanceOf(from) + 1 + amount;

        hevm.prank(from);
        // slither-disable-next-line unchecked-transfer
        try ousd.transfer(to, amount) {
            assert(false);
        } catch {
            assert(true);
        }
    }

    /**
     * @notice A transfer to the same account should not change that account's balance
     * @param targetAcc Account to transfer to
     * @param amount Amount to transfer
     */
    function testTransferSelf(uint8 targetAcc, uint256 amount) public {
        address target = getAccount(targetAcc);

        uint256 balanceBefore = ousd.balanceOf(target);
        transfer(targetAcc, targetAcc, amount);
        uint256 balanceAfter = ousd.balanceOf(target);

        assert(balanceBefore == balanceAfter);
    }

    /**
     * @notice Transfers to the zero account revert
     * @param fromAcc Account to transfer from
     * @param amount Amount to transfer
     */
    function testTransferToZeroAddress(uint8 fromAcc, uint256 amount) public {
        address from = getAccount(fromAcc);

        hevm.prank(from);
        // slither-disable-next-line unchecked-transfer
        try ousd.transfer(address(0), amount) {
            assert(false);
        } catch {
            assert(true);
        }
    }
}

