// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "../interfaces/IPriceOracle.sol";
import "../interfaces/IMinMaxOracle.sol";

/**
 * Mock of both price Oracle and min max oracles
 */
contract MockOracle is IPriceOracle, IMinMaxOracle {
    mapping(bytes32 => uint256) prices;
    mapping(bytes32 => uint256[]) pricesMinMax;
    uint256 ethMin;
    uint256 ethMax;

    /**
     * @dev returns the asset price in USD, 6 decimal digits.
     * Compatible with the Open Price Feed.
     */
    function price(string calldata symbol)
        external
        view
        override
        returns (uint256)
    {
        return prices[keccak256(abi.encodePacked(symbol))];
    }

    /**
     * @dev sets the price of the asset in USD, 6 decimal digits
     *
     */
    function setPrice(string calldata symbol, uint256 _price) external {
        prices[keccak256(abi.encodePacked(symbol))] = _price;
    }

    /**
     * @dev sets the min and max price of ETH in USD, 6 decimal digits
     *
     */
    function setEthPriceMinMax(uint256 _min, uint256 _max) external {
        ethMin = _min;
        ethMax = _max;
    }

    /**
     * @dev sets the prices Min Max for a specific symbol in ETH, 8 decimal digits
     *
     */
    function setTokPriceMinMax(
        string calldata symbol,
        uint256 _min,
        uint256 _max
    ) external {
        pricesMinMax[keccak256(abi.encodePacked(symbol))] = [_min, _max];
    }

    /**
     * @dev get the price of asset in ETH, 8 decimal digits.
     */
    function priceMin(string calldata symbol)
        external
        view
        override
        returns (uint256)
    {
        uint256[] storage pMinMax = pricesMinMax[
            keccak256(abi.encodePacked(symbol))
        ];
        return (pMinMax[0] * ethMin) / 1e6;
    }

    /**
     * @dev get the price of asset in USD, 8 decimal digits.
     * Not needed for now
     */
    function priceMax(string calldata symbol)
        external
        view
        override
        returns (uint256)
    {
        uint256[] storage pMinMax = pricesMinMax[
            keccak256(abi.encodePacked(symbol))
        ];
        return (pMinMax[1] * ethMax) / 1e6;
    }
}

contracts/mocks/MockOracleRouter.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import "../interfaces/chainlink/AggregatorV3Interface.sol";
import { IOracle } from "../interfaces/IOracle.sol";
import { Helpers } from "../utils/Helpers.sol";
import { StableMath } from "../utils/StableMath.sol";
import { SafeCast } from "@openzeppelin/contracts/utils/math/SafeCast.sol";
import { AbstractOracleRouter } from "../oracle/AbstractOracleRouter.sol";

// @notice Oracle Router required for testing environment
contract MockOracleRouter is AbstractOracleRouter {
    struct FeedMetadata {
        address feedAddress;
        uint256 maxStaleness;
    }

    mapping(address => FeedMetadata) public assetToFeedMetadata;

    /* @dev Override feed and maxStaleness information for a particular asset
     * @param _asset the asset to override feed for
     * @param _feed new feed
     * @param _maxStaleness new maximum time allowed for feed data to be stale
     */
    function setFeed(
        address _asset,
        address _feed,
        uint256 _maxStaleness
    ) external {
        assetToFeedMetadata[_asset] = FeedMetadata(_feed, _maxStaleness);
    }

    /*
     * The dev version of the Oracle doesn't need to gas optimize and cache the decimals
     */
    function getDecimals(address _feed) internal view override returns (uint8) {
        require(_feed != address(0), "Asset not available");
        require(_feed != FIXED_PRICE, "Fixed price feeds not supported");

        return AggregatorV3Interface(_feed).decimals();
    }

    /**
     * @dev The price feed contract to use for a particular asset along with
     *      maximum data staleness
     * @param asset address of the asset
     * @return feedAddress address of the price feed for the asset
     * @return maxStaleness maximum acceptable data staleness duration
     */
    function feedMetadata(address asset)
        internal
        view
        override
        returns (address feedAddress, uint256 maxStaleness)
    {
        FeedMetadata storage fm = assetToFeedMetadata[asset];
        feedAddress = fm.feedAddress;
        maxStaleness = fm.maxStaleness;
    }
}

