contracts/strategies/MorphoAaveStrategy..content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

/**
 * @title OUSD Morpho Aave Strategy
 * @notice Investment strategy for investing stablecoins via Morpho (Aave)
 * @author Origin Protocol Inc
 */
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20, InitializableAbstractStrategy } from "../utils/InitializableAbstractStrategy.sol";
import { IMorpho } from "../interfaces/morpho/IMorpho.sol";
import { ILens } from "../interfaces/morpho/ILens.sol";
import { StableMath } from "../utils/StableMath.sol";

contract MorphoAaveStrategy is InitializableAbstractStrategy {
    address public constant MORPHO = 0x777777c9898D384F785Ee44Acfe945efDFf5f3E0;
    address public constant LENS = 0x507fA343d0A90786d86C7cd885f5C49263A91FF4;

    using SafeERC20 for IERC20;
    using StableMath for uint256;

    constructor(BaseStrategyConfig memory _stratConfig)
        InitializableAbstractStrategy(_stratConfig)
    {}

    /**
     * @dev Initialize function, to set up initial internal state
     * @param _rewardTokenAddresses Address of reward token for platform
     * @param _assets Addresses of initial supported assets
     * @param _pTokens Platform Token corresponding addresses
     */
    function initialize(
        address[] calldata _rewardTokenAddresses,
        address[] calldata _assets,
        address[] calldata _pTokens
    ) external onlyGovernor initializer {
        InitializableAbstractStrategy._initialize(
            _rewardTokenAddresses,
            _assets,
            _pTokens
        );
    }

    /**
     * @dev Approve the spending of all assets by main Morpho contract,
     *      if for some reason is it necessary.
     */
    function safeApproveAllTokens()
        external
        override
        onlyGovernor
        nonReentrant
    {
        uint256 assetCount = assetsMapped.length;
        for (uint256 i = 0; i < assetCount; i++) {
            address asset = assetsMapped[i];

            // Safe approval
            IERC20(asset).safeApprove(MORPHO, 0);
            IERC20(asset).safeApprove(MORPHO, type(uint256).max);
        }
    }

    /**
     * @dev Internal method to respond to the addition of new asset
     *      We need to approve and allow Morpho to move them
     * @param _asset Address of the asset to approve
     * @param _pToken The pToken for the approval
     */
    // solhint-disable-next-line no-unused-vars
    function _abstractSetPToken(address _asset, address _pToken)
        internal
        override
    {
        IERC20(_asset).safeApprove(MORPHO, 0);
        IERC20(_asset).safeApprove(MORPHO, type(uint256).max);
    }

    /**
     * @dev Collect accumulated rewards and send them to Harvester.
     */
    function collectRewardTokens()
        external
        override
        onlyHarvester
        nonReentrant
    {
        // Morpho Aave-v2 doesn't distribute reward tokens
        // solhint-disable-next-line max-line-length
        // Ref: https://developers.morpho.xyz/interact-with-morpho/get-started/interact-with-morpho/claim-rewards#morpho-aave-v2
    }

    /**
     * @dev Get the amount of rewards pending to be collected from the protocol
     */
    function getPendingRewards() external view returns (uint256 balance) {
        // Morpho Aave-v2 doesn't distribute reward tokens
        // solhint-disable-next-line max-line-length
        // Ref: https://developers.morpho.xyz/interact-with-morpho/get-started/interact-with-morpho/claim-rewards#morpho-aave-v2
        return 0;
    }

    /**
     * @dev Deposit asset into Morpho
     * @param _asset Address of asset to deposit
     * @param _amount Amount of asset to deposit
     */
    function deposit(address _asset, uint256 _amount)
        external
        override
        onlyVault
        nonReentrant
    {
        _deposit(_asset, _amount);
    }

    /**
     * @dev Deposit asset into Morpho
     * @param _asset Address of asset to deposit
     * @param _amount Amount of asset to deposit
     */
    function _deposit(address _asset, uint256 _amount) internal {
        require(_amount > 0, "Must deposit something");

        address pToken = address(_getPTokenFor(_asset));

        IMorpho(MORPHO).supply(
            pToken,
            address(this), // the address of the user you want to supply on behalf of
            _amount
        );
        emit Deposit(_asset, pToken, _amount);
    }

    /**
     * @dev Deposit the entire balance of any supported asset into Morpho
     */
    function depositAll() external override onlyVault nonReentrant {
        for (uint256 i = 0; i < assetsMapped.length; i++) {
            uint256 balance = IERC20(assetsMapped[i]).balanceOf(address(this));
            if (balance > 0) {
                _deposit(assetsMapped[i], balance);
            }
        }
    }

    /**
     * @dev Withdraw asset from Morpho
     * @param _recipient Address to receive withdrawn asset
     * @param _asset Address of asset to withdraw
     * @param _amount Amount of asset to withdraw
     */
    function withdraw(
        address _recipient,
        address _asset,
        uint256 _amount
    ) external override onlyVault nonReentrant {
        _withdraw(_recipient, _asset, _amount);
    }

    function _withdraw(
        address _recipient,
        address _asset,
        uint256 _amount
    ) internal {
        require(_amount > 0, "Must withdraw something");
        require(_recipient != address(0), "Must specify recipient");

        address pToken = address(_getPTokenFor(_asset));

        IMorpho(MORPHO).withdraw(pToken, _amount);
        emit Withdrawal(_asset, pToken, _amount);
        IERC20(_asset).safeTransfer(_recipient, _amount);
    }

    /**
     * @dev Remove all assets from platform and send them to Vault contract.
     */
    function withdrawAll() external override onlyVaultOrGovernor nonReentrant {
        for (uint256 i = 0; i < assetsMapped.length; i++) {
            uint256 balance = _checkBalance(assetsMapped[i]);
            if (balance > 0) {
                _withdraw(vaultAddress, assetsMapped[i], balance);
            }
        }
    }

    /**
     * @dev Return total value of an asset held in the platform
     * @param _asset      Address of the asset
     * @return balance    Total value of the asset in the platform
     */
    function checkBalance(address _asset)
        external
        view
        override
        returns (uint256 balance)
    {
        return _checkBalance(_asset);
    }

    function _checkBalance(address _asset)
        internal
        view
        returns (uint256 balance)
    {
        address pToken = address(_getPTokenFor(_asset));

        // Total value represented by decimal position of underlying token
        (, , balance) = ILens(LENS).getCurrentSupplyBalanceInOf(
            pToken,
            address(this)
        );
    }

    /**
     * @dev Retuns bool indicating whether asset is supported by strategy
     * @param _asset Address of the asset
     */
    function supportsAsset(address _asset) public view override returns (bool) {
        return assetToPToken[_asset] != address(0);
    }

    /**
     * @dev Get the pToken wrapped in the IERC20 interface for this asset.
     *      Fails if the pToken doesn't exist in our mappings.
     * @param _asset Address of the asset
     * @return pToken Corresponding pToken to this asset
     */
    function _getPTokenFor(address _asset) internal view returns (IERC20) {
        address pToken = assetToPToken[_asset];
        require(pToken != address(0), "pToken does not exist");
        return IERC20(pToken);
    }
}

contracts/strategies/MorphoCompoundStrat.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

/**
 * @title OUSD Morpho Compound Strategy
 * @notice Investment strategy for investing stablecoins via Morpho (Compound)
 * @author Origin Protocol Inc
 */
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20, AbstractCompoundStrategy, InitializableAbstractStrategy } from "./AbstractCompoundStrategy.sol";
import { IMorpho } from "../interfaces/morpho/IMorpho.sol";
import { ILens } from "../interfaces/morpho/ILens.sol";
import { StableMath } from "../utils/StableMath.sol";
import "../utils/Helpers.sol";

contract MorphoCompoundStrategy is AbstractCompoundStrategy {
    address public constant MORPHO = 0x8888882f8f843896699869179fB6E4f7e3B58888;
    address public constant LENS = 0x930f1b46e1D081Ec1524efD95752bE3eCe51EF67;
    using SafeERC20 for IERC20;
    using StableMath for uint256;

    constructor(BaseStrategyConfig memory _stratConfig)
        InitializableAbstractStrategy(_stratConfig)
    {}

    /**
     * @dev Initialize function, to set up initial internal state
     * @param _rewardTokenAddresses Address of reward token for platform
     * @param _assets Addresses of initial supported assets
     * @param _pTokens Platform Token corresponding addresses
     */
    function initialize(
        address[] calldata _rewardTokenAddresses,
        address[] calldata _assets,
        address[] calldata _pTokens
    ) external onlyGovernor initializer {
        InitializableAbstractStrategy._initialize(
            _rewardTokenAddresses,
            _assets,
            _pTokens
        );
    }

    /**
     * @dev Approve the spending of all assets by main Morpho contract,
     *      if for some reason is it necessary.
     */
    function safeApproveAllTokens()
        external
        override
        onlyGovernor
        nonReentrant
    {
        uint256 assetCount = assetsMapped.length;
        for (uint256 i = 0; i < assetCount; i++) {
            address asset = assetsMapped[i];

            // Safe approval
            IERC20(asset).safeApprove(MORPHO, 0);
            IERC20(asset).safeApprove(MORPHO, type(uint256).max);
        }
    }

    /**
     * @dev Internal method to respond to the addition of new asset
     *      We need to approve and allow Morpho to move them
     * @param _asset Address of the asset to approve
     * @param _pToken The pToken for the approval
     */
    // solhint-disable-next-line no-unused-vars
    function _abstractSetPToken(address _asset, address _pToken)
        internal
        override
    {
        IERC20(_asset).safeApprove(MORPHO, 0);
        IERC20(_asset).safeApprove(MORPHO, type(uint256).max);
    }

    /**
     * @dev Collect accumulated rewards and send them to Harvester.
     */
    function collectRewardTokens()
        external
        override
        onlyHarvester
        nonReentrant
    {
        /**
         * Gas considerations. We could query Morpho LENS's `getUserUnclaimedRewards` for each
         * cToken separately and only claimRewards where it is economically feasible. Each call
         * (out of 3) costs ~60k gas extra.
         *
         * Each extra cToken in the `poolTokens` of `claimRewards` function makes that call
         * 89-120k more expensive gas wise.
         *
         * With Lens query in case where:
         *  - there is only 1 reward token to collect. Net gas usage in best case would be
         *    3*60 - 2*120 = -60k -> saving 60k gas
         *  - there are 2 reward tokens to collect. Net gas usage in best case would be
         *    3*60 - 120 = 60k -> more expensive for 60k gas
         *  - there are 3 reward tokens to collect. Net gas usage in best case would be
         *    3*60 = 180k -> more expensive for 180k gas
         *
         * For the above reasoning such "optimization" is not implemented
         */

        address[] memory poolTokens = new address[](assetsMapped.length);
        for (uint256 i = 0; i < assetsMapped.length; i++) {
            poolTokens[i] = assetToPToken[assetsMapped[i]];
        }

        // slither-disable-next-line unused-return
        IMorpho(MORPHO).claimRewards(
            poolTokens, // The addresses of the underlying protocol's pools to claim rewards from
            false // Whether to trade the accrued rewards for MORPHO token, with a premium
        );

        // Transfer COMP to Harvester
        IERC20 rewardToken = IERC20(rewardTokenAddresses[0]);
        uint256 balance = rewardToken.balanceOf(address(this));
        emit RewardTokenCollected(
            harvesterAddress,
            rewardTokenAddresses[0],
            balance
        );
        rewardToken.safeTransfer(harvesterAddress, balance);
    }

    /**
     * @dev Get the amount of rewards pending to be collected from the protocol
     */
    function getPendingRewards() external view returns (uint256 balance) {
        address[] memory poolTokens = new address[](assetsMapped.length);
        for (uint256 i = 0; i < assetsMapped.length; i++) {
            poolTokens[i] = assetToPToken[assetsMapped[i]];
        }

        return ILens(LENS).getUserUnclaimedRewards(poolTokens, address(this));
    }

    /**
     * @dev Deposit asset into Morpho
     * @param _asset Address of asset to deposit
     * @param _amount Amount of asset to deposit
     */
    function deposit(address _asset, uint256 _amount)
        external
        override
        onlyVault
        nonReentrant
    {
        _deposit(_asset, _amount);
    }

    /**
     * @dev Deposit asset into Morpho
     * @param _asset Address of asset to deposit
     * @param _amount Amount of asset to deposit
     */
    function _deposit(address _asset, uint256 _amount) internal {
        require(_amount > 0, "Must deposit something");

        IMorpho(MORPHO).supply(
            address(_getCTokenFor(_asset)),
            address(this), // the address of the user you want to supply on behalf of
            _amount
        );
        emit Deposit(_asset, address(_getCTokenFor(_asset)), _amount);
    }

    /**
     * @dev Deposit the entire balance of any supported asset into Morpho
     */
    function depositAll() external override onlyVault nonReentrant {
        for (uint256 i = 0; i < assetsMapped.length; i++) {
            uint256 balance = IERC20(assetsMapped[i]).balanceOf(address(this));
            if (balance > 0) {
                _deposit(assetsMapped[i], balance);
            }
        }
    }

    /**
     * @dev Withdraw asset from Morpho
     * @param _recipient Address to receive withdrawn asset
     * @param _asset Address of asset to withdraw
     * @param _amount Amount of asset to withdraw
     */
    function withdraw(
        address _recipient,
        address _asset,
        uint256 _amount
    ) external override onlyVault nonReentrant {
        _withdraw(_recipient, _asset, _amount);
    }

    function _withdraw(
        address _recipient,
        address _asset,
        uint256 _amount
    ) internal {
        require(_amount > 0, "Must withdraw something");
        require(_recipient != address(0), "Must specify recipient");

        address pToken = assetToPToken[_asset];

        IMorpho(MORPHO).withdraw(pToken, _amount);
        emit Withdrawal(_asset, address(_getCTokenFor(_asset)), _amount);
        IERC20(_asset).safeTransfer(_recipient, _amount);
    }

    /**
     * @dev Remove all assets from platform and send them to Vault contract.
     */
    function withdrawAll() external override onlyVaultOrGovernor nonReentrant {
        for (uint256 i = 0; i < assetsMapped.length; i++) {
            uint256 balance = _checkBalance(assetsMapped[i]);
            if (balance > 0) {
                _withdraw(vaultAddress, assetsMapped[i], balance);
            }
        }
    }

    /**
     * @dev Return total value of an asset held in the platform
     * @param _asset      Address of the asset
     * @return balance    Total value of the asset in the platform
     */
    function checkBalance(address _asset)
        external
        view
        override
        returns (uint256 balance)
    {
        return _checkBalance(_asset);
    }

    function _checkBalance(address _asset)
        internal
        view
        returns (uint256 balance)
    {
        address pToken = assetToPToken[_asset];

        // Total value represented by decimal position of underlying token
        (, , balance) = ILens(LENS).getCurrentSupplyBalanceInOf(
            pToken,
            address(this)
        );
    }
}

contracts/strategies/NativeStaking/Compo.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import { InitializableAbstractStrategy } from "../../utils/InitializableAbstractStrategy.sol";
import { IWETH9 } from "../../interfaces/IWETH9.sol";
import { CompoundingValidatorManager } from "./CompoundingValidatorManager.sol";

/// @title Compounding Staking SSV Strategy
/// @notice Strategy to deploy funds into DVT validators powered by the SSV Network
/// @author Origin Protocol Inc
contract CompoundingStakingSSVStrategy is
    CompoundingValidatorManager,
    InitializableAbstractStrategy
{
    /// @notice SSV ERC20 token that serves as a payment for operating SSV validators
    address public immutable SSV_TOKEN;

    // For future use
    uint256[50] private __gap;

    /// @param _baseConfig Base strategy config with
    ///   `platformAddress` not used so empty address
    ///   `vaultAddress` the address of the OETH Vault contract
    /// @param _wethAddress Address of the WETH Token contract
    /// @param _ssvToken Address of the SSV Token contract
    /// @param _ssvNetwork Address of the SSV Network contract
    /// @param _beaconChainDepositContract Address of the beacon chain deposit contract
    /// @param _beaconProofs Address of the Beacon Proofs contract that verifies beacon chain data
    /// @param _beaconGenesisTimestamp The timestamp of the Beacon chain's genesis.
    constructor(
        BaseStrategyConfig memory _baseConfig,
        address _wethAddress,
        address _ssvToken,
        address _ssvNetwork,
        address _beaconChainDepositContract,
        address _beaconProofs,
        uint64 _beaconGenesisTimestamp
    )
        InitializableAbstractStrategy(_baseConfig)
        CompoundingValidatorManager(
            _wethAddress,
            _baseConfig.vaultAddress,
            _beaconChainDepositContract,
            _ssvNetwork,
            _beaconProofs,
            _beaconGenesisTimestamp
        )
    {
        SSV_TOKEN = _ssvToken;
        _disableInitializers();
    }

    /// @notice Set up initial internal state including
    /// 1. approving the SSVNetwork to transfer SSV tokens from this strategy contract
    /// @param _rewardTokenAddresses Not used so empty array
    /// @param _assets Not used so empty array
    /// @param _pTokens Not used so empty array
    function initialize(
        address[] memory _rewardTokenAddresses,
        address[] memory _assets,
        address[] memory _pTokens
    ) external onlyGovernor initializer {
        InitializableAbstractStrategy._initialize(
            _rewardTokenAddresses,
            _assets,
            _pTokens
        );

        safeApproveAllTokens();
    }

    /// @notice Unlike other strategies, this does not deposit assets into the underlying platform.
    /// It just checks the asset is WETH and emits the Deposit event.
    /// To deposit WETH into validators, `registerSsvValidator` and `stakeEth` must be used.
    /// @param _asset Address of the WETH token.
    /// @param _amount Amount of WETH that was transferred to the strategy by the vault.
    function deposit(address _asset, uint256 _amount)
        external
        override
        onlyVault
        nonReentrant
    {
        require(_asset == WETH, "Unsupported asset");
        require(_amount > 0, "Must deposit something");

        // Account for the new WETH
        depositedWethAccountedFor += _amount;

        emit Deposit(_asset, address(0), _amount);
    }

    /// @notice Unlike other strategies, this does not deposit assets into the underlying platform.
    /// It just emits the Deposit event.
    /// To deposit WETH into validators `registerSsvValidator` and `stakeEth` must be used.
    function depositAll() external override onlyVault nonReentrant {
        uint256 wethBalance = IERC20(WETH).balanceOf(address(this));
        uint256 newWeth = wethBalance - depositedWethAccountedFor;

        if (newWeth > 0) {
            // Account for the new WETH
            depositedWethAccountedFor = wethBalance;

            emit Deposit(WETH, address(0), newWeth);
        }
    }

    /// @notice Withdraw ETH and WETH from this strategy contract.
    /// @param _recipient Address to receive withdrawn assets.
    /// @param _asset Address of the WETH token.
    /// @param _amount Amount of WETH to withdraw.
    function withdraw(
        address _recipient,
        address _asset,
        uint256 _amount
    ) external override onlyVault nonReentrant {
        require(_asset == WETH, "Unsupported asset");

        _withdraw(_recipient, _amount, address(this).balance);
    }

    function _withdraw(
        address _recipient,
        uint256 _withdrawAmount,
        uint256 _ethBalance
    ) internal {
        require(_withdrawAmount > 0, "Must withdraw something");
        require(_recipient != address(0), "Must specify recipient");

        // Convert any ETH from validator partial withdrawals, exits
        // or execution rewards to WETH and do the necessary accounting.
        if (_ethBalance > 0) _convertEthToWeth(_ethBalance);

        // Transfer WETH to the recipient and do the necessary accounting.
        _transferWeth(_withdrawAmount, _recipient);

        emit Withdrawal(WETH, address(0), _withdrawAmount);
    }

    /// @notice Transfer all WETH deposits, ETH from validator withdrawals and ETH from
    /// execution rewards in this strategy to the vault.
    /// This does not withdraw from the validators. That has to be done separately with the
    /// `validatorWithdrawal` operation.
    function withdrawAll() external override onlyVaultOrGovernor nonReentrant {
        uint256 ethBalance = address(this).balance;
        uint256 withdrawAmount = IERC20(WETH).balanceOf(address(this)) +
            ethBalance;

        if (withdrawAmount > 0) {
            _withdraw(vaultAddress, withdrawAmount, ethBalance);
        }
    }

    /// @notice Accounts for all the assets managed by this strategy which includes:
    /// 1. The current WETH in this strategy contract
    /// 2. The last verified ETH balance, total deposits and total validator balances
    /// @param _asset      Address of WETH asset.
    /// @return balance    Total value in ETH
    function checkBalance(address _asset)
        external
        view
        override
        returns (uint256 balance)
    {
        require(_asset == WETH, "Unsupported asset");

        // Load the last verified balance from the storage
        // and add to the latest WETH balance of this strategy.
        balance =
            lastVerifiedEthBalance +
            IWETH9(WETH).balanceOf(address(this));
    }

    /// @notice Returns bool indicating whether asset is supported by the strategy.
    /// @param _asset The address of the WETH token.
    function supportsAsset(address _asset) public view override returns (bool) {
        return _asset == WETH;
    }

    /// @notice Approves the SSV Network contract to transfer SSV tokens for validator registration.
    function safeApproveAllTokens() public override {
        // Approves the SSV Network contract to transfer SSV tokens when validators are registered
        IERC20(SSV_TOKEN).approve(SSV_NETWORK, type(uint256).max);
    }

    /**
     * @notice We can accept ETH directly to this contract from anyone as it does not impact our accounting
     * like it did in the legacy NativeStakingStrategy.
     * The new ETH will be accounted for in `checkBalance` after the next snapBalances and verifyBalances txs.
     */
    receive() external payable {}

    /***************************************
                Internal functions
    ****************************************/

    /// @notice is not supported for this strategy as there is no platform token.
    function setPTokenAddress(address, address) external pure override {
        revert("Unsupported function");
    }

    /// @notice is not supported for this strategy as there is no platform token.
    function removePToken(uint256) external pure override {
        revert("Unsupported function");
    }

    /// @dev This strategy does not use a platform token like the old Aave and Compound strategies.
    function _abstractSetPToken(address _asset, address) internal override {}

    /// @dev Consensus rewards are compounded to the validator's balance instead of being
    /// swept to this strategy contract.
    /// Execution rewards from MEV and tx priority accumulate as ETH in this strategy contract.
    /// Withdrawals from validators also accumulate as ETH in this strategy contract.
    /// It's too complex to separate the rewards from withdrawals so this function is not implemented.
    /// Besides, ETH rewards are not sent to the Dripper any more. The Vault can now regulate
    /// the increase in assets.
    function _collectRewardTokens() internal pure override {
        revert("Unsupported function");
    }
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { SafeCast } from "@openzeppelin/contracts/utils/math/SafeCast.sol";
import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { Governable } from "../../governance/Governable.sol";
import { IDepositContract } from "../../interfaces/IDepositContract.sol";
import { IWETH9 } from "../../interfaces/IWETH9.sol";
import { ISSVNetwork, Cluster } from "../../interfaces/ISSVNetwork.sol";
import { BeaconRoots } from "../../beacon/BeaconRoots.sol";
import { PartialWithdrawal } from "../../beacon/PartialWithdrawal.sol";
import { IBeaconProofs } from "../../interfaces/IBeaconProofs.sol";

/**
 * @title Validator lifecycle management contract
 * @notice This contract implements all the required functionality to
 * register, deposit, withdraw, exit and remove validators.
 * @author Origin Protocol Inc
 */
abstract contract CompoundingValidatorManager is Governable {
    using SafeERC20 for IERC20;

    /// @notice The amount of ETH in wei that is required for a deposit to a new validator.
    /// Initially this is 32 ETH, but will be reduced to 1 ETH after P2P's APIs have been updated
    /// to support deposits of 1 ETH.
    uint256 internal constant DEPOSIT_AMOUNT_WEI = 32 ether;
    uint64 internal constant FAR_FUTURE_EPOCH = type(uint64).max;
    uint64 internal constant SLOT_DURATION = 12;
    uint64 internal constant SLOTS_PER_EPOCH = 32;

    /// @notice The address of the Wrapped ETH (WETH) token contract
    address public immutable WETH;
    /// @notice The address of the beacon chain deposit contract
    address public immutable BEACON_CHAIN_DEPOSIT_CONTRACT;
    /// @notice The address of the SSV Network contract used to interface with
    address public immutable SSV_NETWORK;
    /// @notice Address of the OETH Vault proxy contract
    address public immutable VAULT_ADDRESS;
    /// @notice Address of the Beacon Proofs contract that verifies beacon chain data
    address public immutable BEACON_PROOFS;
    /// @notice The timestamp of the Beacon chain genesis.
    /// @dev this is different on Testnets like Hoodi so is set at deployment time.
    uint64 public immutable BEACON_GENESIS_TIMESTAMP;

    /// @notice Address of the registrator - allowed to register, withdraw, exit and remove validators
    address public validatorRegistrator;

    /// Deposit data for new compounding validators.

    enum DepositStatus {
        UNKNOWN, // default value
        PENDING, // deposit is pending and waiting to be  verified
        VERIFIED // deposit has been verified and is ready to be staked
    }
    /// @param pubKeyHash Hash of validator's public key using the Beacon Chain's format
    /// @param amountWei Amount of ETH in wei that has been deposited to the beacon chain deposit contract
    /// @param blockNumber Block number when the deposit was made
    /// @param depositIndex The index of the deposit in the list of active deposits
    /// @param status The status of the deposit, either PENDING or PROVEN
    struct DepositData {
        bytes32 pubKeyHash;
        uint64 amountGwei;
        uint64 slot;
        uint32 depositIndex;
        DepositStatus status;
        uint64 withdrawableEpoch;
    }
    /// @notice Unique identifier of the next validator deposit.
    uint256 public nextDepositID;
    /// @notice Mapping of the deposit ID to the deposit data
    mapping(uint256 => DepositData) public deposits;
    /// @notice List of strategy deposit IDs to a validator.
    /// The list can be for deposits waiting to be verified as processed on the beacon chain,
    /// or deposits that have been verified to an exiting validator and is now waiting for the
    /// validator's balance to be swept.
    /// The list may not be ordered by time of deposit.
    /// Removed deposits will move the last deposit to the removed index.
    uint256[] internal depositList;

    // Validator data

    enum VALIDATOR_STATE {
        NON_REGISTERED, // validator is not registered on the SSV network
        REGISTERED, // validator is registered on the SSV network
        STAKED, // validator has funds staked
        VERIFIED, // validator has been verified to exist on the beacon chain
        EXITING, // The validator has been requested to exit or has been verified as forced exit
        EXITED, // The validator has been verified to have a zero balance
        REMOVED, // validator has funds withdrawn to the EigenPod and is removed from the SSV
        INVALID // The validator has been front-run and the withdrawal address is not this strategy
    }
    struct ValidatorData {
        VALIDATOR_STATE state;
        uint64 index; // The index of the validator on the beacon chain
    }
    /// @notice List of validator public key hashes that have been verified to exist on the beacon chain.
    /// These have had a deposit processed and the validator's balance increased.
    /// Validators will be removed from this list when its verified they have a zero balance.
    bytes32[] internal verifiedValidators;
    /// @notice Mapping of the hash of the validator's public key to the validator state and index.
    /// Uses the Beacon chain hashing for BLSPubkey which is sha256(abi.encodePacked(validator.pubkey, bytes16(0)))
    mapping(bytes32 => ValidatorData) public validator;

    /// @param timestamp Timestamp of the snapshot
    /// @param ethBalance The balance of ETH in the strategy contract at the snapshot
    struct Balances {
        uint64 timestamp;
        uint128 ethBalance;
    }
    /// @notice Mapping of the block root to the balances at that slot
    mapping(bytes32 => Balances) internal snappedBalances;
    uint64 public lastSnapTimestamp;
    uint128 public lastVerifiedEthBalance;

    /// @dev This contract receives WETH as the deposit asset, but unlike other strategies doesn't immediately
    /// deposit it to an underlying platform. Rather a special privilege account stakes it to the validators.
    /// For that reason calling WETH.balanceOf(this) in a deposit function can contain WETH that has just been
    /// deposited and also WETH that has previously been deposited. To keep a correct count we need to keep track
    /// of WETH that has already been accounted for.
    /// This value represents the amount of WETH balance of this contract that has already been accounted for by the
    /// deposit events.
    /// It is important to note that this variable is not concerned with WETH that is a result of full/partial
    /// withdrawal of the validators. It is strictly concerned with WETH that has been deposited and is waiting to
    /// be staked.
    uint256 public depositedWethAccountedFor;

    // For future use
    uint256[50] private __gap;

    event RegistratorChanged(address indexed newAddress);
    event StakingMonitorChanged(address indexed newAddress);
    event SSVValidatorRegistered(
        bytes32 indexed pubKeyHash,
        uint64[] operatorIds
    );
    event SSVValidatorRemoved(bytes32 indexed pubKeyHash, uint64[] operatorIds);
    event ETHStaked(
        bytes32 indexed pubKeyHash,
        uint256 indexed depositID,
        bytes pubKey,
        uint256 amountWei
    );
    event ValidatorVerified(
        bytes32 indexed pubKeyHash,
        uint64 indexed validatorIndex
    );
    event ValidatorInvalid(bytes32 indexed pubKeyHash);
    event DepositVerified(uint256 indexed depositID, uint256 amountWei);
    event DepositToValidatorExiting(
        uint256 indexed depositID,
        uint256 amountWei,
        uint64 withdrawableEpoch
    );
    event DepositValidatorExited(uint256 indexed depositID, uint256 amountWei);
    event ValidatorWithdraw(bytes32 indexed pubKeyHash, uint256 amountWei);
    event BalancesSnapped(
        uint256 indexed timestamp,
        bytes32 indexed blockRoot,
        uint256 ethBalance
    );
    event BalancesVerified(
        uint64 indexed timestamp,
        uint256 totalDepositsWei,
        uint256 totalValidatorBalance,
        uint256 ethBalance
    );

    /// @dev Throws if called by any account other than the Registrator
    modifier onlyRegistrator() {
        require(msg.sender == validatorRegistrator, "Not Registrator");
        _;
    }

    /// @param _wethAddress Address of the Erc20 WETH Token contract
    /// @param _vaultAddress Address of the Vault
    /// @param _beaconChainDepositContract Address of the beacon chain deposit contract
    /// @param _ssvNetwork Address of the SSV Network contract
    /// @param _beaconProofs Address of the Beacon Proofs contract that verifies beacon chain data
    /// @param _beaconGenesisTimestamp The timestamp of the Beacon chain's genesis.
    constructor(
        address _wethAddress,
        address _vaultAddress,
        address _beaconChainDepositContract,
        address _ssvNetwork,
        address _beaconProofs,
        uint64 _beaconGenesisTimestamp
    ) {
        WETH = _wethAddress;
        BEACON_CHAIN_DEPOSIT_CONTRACT = _beaconChainDepositContract;
        SSV_NETWORK = _ssvNetwork;
        VAULT_ADDRESS = _vaultAddress;
        BEACON_PROOFS = _beaconProofs;
        BEACON_GENESIS_TIMESTAMP = _beaconGenesisTimestamp;

        require(
            block.timestamp > _beaconGenesisTimestamp,
            "Invalid genesis timestamp"
        );
    }

    /***************************************
                Admin Functions
    ****************************************/

    /// @notice Set the address of the registrator which can register, exit and remove validators
    function setRegistrator(address _address) external onlyGovernor {
        validatorRegistrator = _address;
        emit RegistratorChanged(_address);
    }

    /***************************************
                Validator Management
    ****************************************/

    /// @notice Registers a single validator in a SSV Cluster.
    /// Only the Registrator can call this function.
    /// @param publicKey The public key of the validator
    /// @param operatorIds The operator IDs of the SSV Cluster
    /// @param sharesData The shares data for the validator
    /// @param ssvAmount The amount of SSV tokens to be deposited to the SSV cluster
    /// @param cluster The SSV cluster details including the validator count and SSV balance
    // slither-disable-start reentrancy-no-eth
    function registerSsvValidator(
        bytes calldata publicKey,
        uint64[] calldata operatorIds,
        bytes calldata sharesData,
        uint256 ssvAmount,
        Cluster calldata cluster
    ) external onlyRegistrator {
        // Hash the public key using the Beacon Chain's format
        bytes32 pubKeyHash = _hashPubKey(publicKey);
        // Check each public key has not already been used
        require(
            validator[pubKeyHash].state == VALIDATOR_STATE.NON_REGISTERED,
            "Validator already registered"
        );

        // Store the validator state as registered
        validator[pubKeyHash].state = VALIDATOR_STATE.REGISTERED;

        ISSVNetwork(SSV_NETWORK).registerValidator(
            publicKey,
            operatorIds,
            sharesData,
            ssvAmount,
            cluster
        );

        emit SSVValidatorRegistered(pubKeyHash, operatorIds);
    }

    // slither-disable-end reentrancy-no-eth

    struct ValidatorStakeData {
        bytes pubkey;
        bytes signature;
        bytes32 depositDataRoot;
    }

    /// @notice Stakes WETH in this strategy to a compounding validator.
    /// Does not convert any ETH sitting in this strategy to WETH.
    /// @param validatorStakeData validator data needed to stake.
    /// The `ValidatorStakeData` struct contains the pubkey, signature and depositDataRoot.
    /// Only the registrator can call this function.
    /// @param depositAmountGwei The amount of WETH to stake to the validator in Gwei.
    // slither-disable-start reentrancy-eth
    function stakeEth(
        ValidatorStakeData calldata validatorStakeData,
        uint64 depositAmountGwei
    ) external onlyRegistrator {
        uint256 depositAmountWei = uint256(depositAmountGwei) * 1 gwei;
        // Check there is enough WETH from the deposits sitting in this strategy contract
        // There could be ETH from withdrawals but we'll ignore that. If it's really needed
        // the ETH can be withdrawn and then deposited back to the strategy.
        require(
            depositAmountWei <= IWETH9(WETH).balanceOf(address(this)),
            "Insufficient WETH"
        );

        // Convert required ETH from WETH and do the necessary accounting
        _convertWethToEth(depositAmountWei);

        // Hash the public key using the Beacon Chain's hashing for BLSPubkey
        bytes32 pubKeyHash = _hashPubKey(validatorStakeData.pubkey);
        VALIDATOR_STATE currentState = validator[pubKeyHash].state;
        // Can only stake to a validator has have been registered or verified.
        // Can not stake to a validator that has been staked but not yet verified.
        require(
            (currentState == VALIDATOR_STATE.REGISTERED ||
                currentState == VALIDATOR_STATE.VERIFIED),
            "Not registered or verified"
        );
        require(
            currentState == VALIDATOR_STATE.VERIFIED ||
                depositAmountWei == DEPOSIT_AMOUNT_WEI,
            "Invalid first deposit amount"
        );
        require(depositAmountWei >= 1 ether, "Deposit too small");

        /* 0x02 to indicate that withdrawal credentials are for a compounding validator
         * that was introduced with the Pectra upgrade.
         * bytes11(0) to fill up the required zeros
         * remaining bytes20 are for the address
         */
        bytes memory withdrawalCredentials = abi.encodePacked(
            bytes1(0x02),
            bytes11(0),
            address(this)
        );

        // Deposit to the Beacon Chain deposit contract.
        // This will create a deposit in the beacon chain's pending deposit queue.
        IDepositContract(BEACON_CHAIN_DEPOSIT_CONTRACT).deposit{
            value: depositAmountWei
        }(
            validatorStakeData.pubkey,
            withdrawalCredentials,
            validatorStakeData.signature,
            validatorStakeData.depositDataRoot
        );

        //// Update contract storage
        // Store the validator state if needed
        if (currentState == VALIDATOR_STATE.REGISTERED) {
            validator[pubKeyHash].state = VALIDATOR_STATE.STAKED;
        }

        /// After the Pectra upgrade the validators have a new restriction when proposing
        /// blocks. The timestamps are at strict intervals of 12 seconds from the genesis block
        /// forward. Each slot is created at strict 12 second intervals and those slots can
        /// either have blocks attached to them or not. This way using the block.timestamp
        /// the slot number can easily be calculated.
        uint64 depositSlot = (SafeCast.toUint64(block.timestamp) -
            BEACON_GENESIS_TIMESTAMP) / SLOT_DURATION;

        // Store the deposit data for verifyDeposit and verifyBalances
        uint256 depositID = nextDepositID++;
        deposits[depositID] = DepositData({
            pubKeyHash: pubKeyHash,
            amountGwei: depositAmountGwei,
            slot: depositSlot,
            depositIndex: SafeCast.toUint32(depositList.length),
            status: DepositStatus.PENDING,
            withdrawableEpoch: FAR_FUTURE_EPOCH
        });
        depositList.push(depositID);

        emit ETHStaked(
            pubKeyHash,
            depositID,
            validatorStakeData.pubkey,
            depositAmountWei
        );
    }

    // slither-disable-end reentrancy-eth

    /// @notice Request a full or partial withdrawal from a validator.
    /// A zero amount will trigger a full withdrawal.
    /// If the remaining balance is < 32 ETH then only the amount in excess of 32 ETH will be withdrawn.
    /// Only the Registrator can call this function.
    /// 1 wei of value should be sent with the tx to pay for the withdrawal request fee.
    /// If no value sent, 1 wei will be taken from the strategy's ETH balance if it has any.
    /// If no ETH balance, the tx will revert.
    /// @param publicKey The public key of the validator
    /// @param amountGwei The amount of ETH to be withdrawn from the validator in Gwei.
    /// A zero amount will trigger a full withdrawal.
    // slither-disable-start reentrancy-no-eth
    function validatorWithdrawal(bytes calldata publicKey, uint64 amountGwei)
        external
        payable
        onlyRegistrator
    {
        // Hash the public key using the Beacon Chain's format
        bytes32 pubKeyHash = _hashPubKey(publicKey);
        VALIDATOR_STATE currentState = validator[pubKeyHash].state;
        require(
            currentState == VALIDATOR_STATE.VERIFIED,
            "Validator not verified"
        );

        PartialWithdrawal.request(publicKey, amountGwei);

        // If a full withdrawal (validator exit)
        if (amountGwei == 0) {
            // Store the validator state as exiting so no more deposits can be made to it.
            validator[pubKeyHash].state = VALIDATOR_STATE.EXITING;
        }

        // Do not remove from the list of verified validators.
        // This is done in the verifyBalances function once the validator's balance has been verified to be zero.
        // The validator state will be set to EXITED in the verifyBalances function.

        emit ValidatorWithdraw(pubKeyHash, uint256(amountGwei) * 1 gwei);
    }

    // slither-disable-end reentrancy-no-eth

    /// @notice Remove the validator from the SSV Cluster after:
    /// - the validator has been exited from `validatorWithdrawal` or slashed
    /// - the validator has incorrectly registered and can not be staked to
    /// - the initial deposit was front-run and the withdrawal address is not this strategy's address.
    /// Make sure `validatorWithdrawal` is called with a zero amount and the validator has exited the Beacon chain.
    /// If removed before the validator has exited the beacon chain will result in the validator being slashed.
    /// Only the registrator can call this function.
    /// @param publicKey The public key of the validator
    /// @param operatorIds The operator IDs of the SSV Cluster
    /// @param cluster The SSV cluster details including the validator count and SSV balance
    // slither-disable-start reentrancy-no-eth
    function removeSsvValidator(
        bytes calldata publicKey,
        uint64[] calldata operatorIds,
        Cluster calldata cluster
    ) external onlyRegistrator {
        // Hash the public key using the Beacon Chain's format
        bytes32 pubKeyHash = _hashPubKey(publicKey);
        VALIDATOR_STATE currentState = validator[pubKeyHash].state;
        // Can remove SSV validators that were incorrectly registered and can not be deposited to.
        require(
            currentState == VALIDATOR_STATE.REGISTERED ||
                currentState == VALIDATOR_STATE.EXITED ||
                currentState == VALIDATOR_STATE.INVALID,
            "Validator not regd or exited"
        );

        ISSVNetwork(SSV_NETWORK).removeValidator(
            publicKey,
            operatorIds,
            cluster
        );

        validator[pubKeyHash].state = VALIDATOR_STATE.REMOVED;

        emit SSVValidatorRemoved(pubKeyHash, operatorIds);
    }

    /***************************************
                SSV Management
    ****************************************/

    // slither-disable-end reentrancy-no-eth

    /// `depositSSV` has been removed as `deposit` on the SSVNetwork contract can be called directly
    /// by the Strategist which is already holding SSV tokens.

    /// @notice Withdraws excess SSV Tokens from the SSV Network contract which was used to pay the SSV Operators.
    /// @dev A SSV cluster is defined by the SSVOwnerAddress and the set of operatorIds.
    /// @param operatorIds The operator IDs of the SSV Cluster
    /// @param ssvAmount The amount of SSV tokens to be deposited to the SSV cluster
    /// @param cluster The SSV cluster details including the validator count and SSV balance
    function withdrawSSV(
        uint64[] memory operatorIds,
        uint256 ssvAmount,
        Cluster memory cluster
    ) external onlyGovernor {
        ISSVNetwork(SSV_NETWORK).withdraw(operatorIds, ssvAmount, cluster);
    }

    /***************************************
                Beacon Chain Proofs
    ****************************************/

    /// @notice Verifies a validator's index to its public key.
    /// Adds to the list of verified validators if the validator's withdrawal address is this strategy's address.
    /// Marks the validator as invalid and removes the deposit if the withdrawal address is not this strategy's address.
    /// @param nextBlockTimestamp The timestamp of the execution layer block after the beacon chain slot
    /// we are verifying.
    /// The next one is needed as the Beacon Oracle returns the parent beacon block root for a block timestamp,
    /// which is the beacon block root of the previous block.
    /// @param validatorIndex The index of the validator on the beacon chain.
    /// @param pubKeyHash The hash of the validator's public key using the Beacon Chain's format
    /// @param withdrawalAddress The withdrawal address of the validator which should be this strategy's address.
    /// If the withdrawal address is not this strategy's address, the initial deposit was front-run
    /// and the validator is marked as invalid.
    /// @param validatorPubKeyProof The merkle proof for the validator public key to the beacon block root.
    /// This is 53 witness hashes of 32 bytes each concatenated together starting from the leaf node.
    /// BeaconBlock.state.validators[validatorIndex].pubkey
    function verifyValidator(
        uint64 nextBlockTimestamp,
        uint64 validatorIndex,
        bytes32 pubKeyHash,
        address withdrawalAddress,
        bytes calldata validatorPubKeyProof
    ) external {
        require(
            validator[pubKeyHash].state == VALIDATOR_STATE.STAKED,
            "Validator not staked"
        );
        // Get the beacon block root of the slot we are verifying the validator in.
        // The parent beacon block root of the next block is the beacon block root of the slot we are verifying.
        bytes32 blockRoot = BeaconRoots.parentBlockRoot(nextBlockTimestamp);

        // Verify the validator index is for the validator with the given public key.
        // Also verify the validator's withdrawal credential points to the `withdrawalAddress`.
        IBeaconProofs(BEACON_PROOFS).verifyValidator(
            blockRoot,
            pubKeyHash,
            validatorPubKeyProof,
            validatorIndex,
            withdrawalAddress
        );

        // If the initial deposit was front-run and the withdrawal address is not this strategy
        if (withdrawalAddress != address(this)) {
            validator[pubKeyHash] = ValidatorData({
                state: VALIDATOR_STATE.INVALID,
                index: validatorIndex
            });

            // Find and remove the deposit as the funds can not be recovered
            uint256 depositCount = depositList.length;
            for (uint256 i = 0; i < depositCount; i++) {
                DepositData memory deposit = deposits[depositList[i]];
                if (deposit.pubKeyHash == pubKeyHash) {
                    _removeDeposit(depositList[i], deposit);
                    break;
                }
            }

            emit ValidatorInvalid(pubKeyHash);
            return;
        }

        // Store the validator state as verified
        validator[pubKeyHash] = ValidatorData({
            state: VALIDATOR_STATE.VERIFIED,
            index: validatorIndex
        });

        // Add the new validator to the list of verified validators
        verifiedValidators.push(pubKeyHash);

        emit ValidatorVerified(pubKeyHash, validatorIndex);
    }

    struct FirstPendingDepositProofData {
        uint64 slot;
        uint64 validatorIndex;
        bytes32 pubKeyHash;
        bytes pendingDepositPubKeyProof;
        bytes withdrawableEpochProof;
        bytes validatorPubKeyProof;
    }

    struct DepositValidatorProofData {
        uint64 withdrawableEpoch;
        bytes withdrawableEpochProof;
    }

    /// @notice Verifies a deposit on the execution layer has been processed by the beacon chain.
    /// This means the accounting of the strategy's ETH moves from a pending deposit to a validator balance.
    ///
    /// Important: this function has a limitation where the `verificationSlot` that is passed by the off-chain
    /// verifier requires a slot immediately after it to propose a block otherwise the `BeaconRoots.parentBlockRoot`
    /// will fail. This shouldn't be a problem, since by the current behaviour of beacon chain only 1%-3% slots
    /// don't propose a block.
    /// @param depositID The deposit ID emitted in `ETHStaked` from the `stakeEth` function.
    /// @param depositProcessedSlot Any slot on or after the strategy's deposit was processed on the beacon chain.
    /// Can not be a slot with pending deposits with the same slot as the deposit being verified.
    /// Can not be a slot before a missed slot as the Beacon Root contract will have the parent block root
    /// set for the next block timestamp in 12 seconds time.
    /// @param firstDepositValidatorCreatedSlot The slot on or after when the validator of the first pending deposit
    /// was created on the beacon chain. This is used to verify the validator has not exited.
    // slither-disable-start reentrancy-no-eth
    function verifyDeposit(
        uint256 depositID,
        uint64 depositProcessedSlot,
        uint64 firstDepositValidatorCreatedSlot,
        FirstPendingDepositProofData calldata firstPendingDeposit,
        DepositValidatorProofData calldata strategyValidatorData
    ) external {
        // Load into memory the previously saved deposit data
        DepositData memory deposit = deposits[depositID];
        ValidatorData memory strategyValidator = validator[deposit.pubKeyHash];
        require(deposit.status == DepositStatus.PENDING, "Deposit not pending");
        require(
            strategyValidator.state == VALIDATOR_STATE.VERIFIED,
            "Validator not verified"
        );
        // The verification slot must be after the deposit's slot.
        // This is needed for when the deposit queue is empty.
        require(deposit.slot < depositProcessedSlot, "Slot not after deposit");
        require(
            depositProcessedSlot <= firstDepositValidatorCreatedSlot,
            "Invalid verification slots"
        );

        // Get the parent beacon block root of the next block which is the block root of the deposit verification slot.
        // This will revert if the slot after the verification slot was missed.
        bytes32 depositBlockRoot = BeaconRoots.parentBlockRoot(
            _calcNextBlockTimestamp(depositProcessedSlot)
        );

        // Verify the slot of the first pending deposit matches the beacon chain
        bool isDepositQueueEmpty = IBeaconProofs(BEACON_PROOFS)
            .verifyFirstPendingDeposit(
                depositBlockRoot,
                firstPendingDeposit.slot,
                firstPendingDeposit.pubKeyHash,
                firstPendingDeposit.pendingDepositPubKeyProof
            );

        // If the deposit queue is not empty
        if (!isDepositQueueEmpty) {
            // Get the parent beacon block root of the next block which is
            // the block root of the validator verification slot.
            // This will revert if the slot after the verification slot was missed.
            bytes32 validatorBlockRoot = BeaconRoots.parentBlockRoot(
                _calcNextBlockTimestamp(firstDepositValidatorCreatedSlot)
            );

            // Verify the validator of the first pending deposit is not exiting.
            // If it is exiting we can't be sure this deposit has not been postponed in the deposit queue.
            // Hence we can not verify if the strategy's deposit has been processed or not.
            IBeaconProofs(BEACON_PROOFS).verifyValidatorWithdrawable(
                validatorBlockRoot,
                firstPendingDeposit.validatorIndex,
                firstPendingDeposit.pubKeyHash,
                FAR_FUTURE_EPOCH,
                firstPendingDeposit.withdrawableEpochProof,
                firstPendingDeposit.validatorPubKeyProof
            );
        }

        // Verify the withdrawableEpoch on the validator of the strategy's deposit
        IBeaconProofs(BEACON_PROOFS).verifyValidatorWithdrawable(
            depositBlockRoot,
            strategyValidator.index,
            strategyValidatorData.withdrawableEpoch,
            strategyValidatorData.withdrawableEpochProof
        );

        // If the validator is exiting because it has been slashed
        if (strategyValidatorData.withdrawableEpoch != FAR_FUTURE_EPOCH) {
            // Store the exit epoch in the deposit data
            deposit.withdrawableEpoch = strategyValidatorData.withdrawableEpoch;

            emit DepositToValidatorExiting(
                depositID,
                uint256(deposit.amountGwei) * 1 gwei,
                strategyValidatorData.withdrawableEpoch
            );

            // Leave the deposit status as PENDING
            return;
        }

        // solhint-disable max-line-length
        // Check the deposit slot is before the first pending deposit's slot on the beacon chain.
        // If this is not true then we can't guarantee the deposit has been processed by the beacon chain.
        // The deposit's slot can not be the same slot as the first pending deposit as there could be
        // many deposits in the same block, hence have the same pending deposit slot.
        // If the deposit queue is empty then our deposit must have been processed on the beacon chain.
        // The deposit slot can be zero for validators consolidating to a compounding validator or 0x01 validator
        // being promoted to a compounding one. Reference:
        // - [switch_to_compounding_validator](https://ethereum.github.io/consensus-specs/specs/electra/beacon-chain/#new-switch_to_compounding_validator
        // - [queue_excess_active_balance](https://ethereum.github.io/consensus-specs/specs/electra/beacon-chain/#new-queue_excess_active_balance)
        // - [process_consolidation_request](https://ethereum.github.io/consensus-specs/specs/electra/beacon-chain/#new-process_consolidation_request)
        // We can not guarantee that the deposit has been processed in that case.
        // solhint-enable max-line-length
        require(
            deposit.slot < firstPendingDeposit.slot || isDepositQueueEmpty,
            "Deposit likely not processed"
        );

        // Remove the deposit now it has been verified as processed on the beacon chain.
        _removeDeposit(depositID, deposit);

        emit DepositVerified(depositID, uint256(deposit.amountGwei) * 1 gwei);
    }

    function _removeDeposit(uint256 depositID, DepositData memory deposit)
        internal
    {
        // After verifying the proof, update the contract storage
        deposits[depositID].status = DepositStatus.VERIFIED;
        // Move the last deposit to the index of the verified deposit
        uint256 lastDeposit = depositList[depositList.length - 1];
        depositList[deposit.depositIndex] = lastDeposit;
        deposits[lastDeposit].depositIndex = deposit.depositIndex;
        // Delete the last deposit from the list
        depositList.pop();
    }

    /// @dev Calculates the timestamp of the next execution block from the given slot.
    /// @param slot The beacon chain slot number used for merkle proof verification.
    function _calcNextBlockTimestamp(uint64 slot)
        internal
        view
        returns (uint64)
    {
        // Calculate the next block timestamp from the slot.
        return SLOT_DURATION * slot + BEACON_GENESIS_TIMESTAMP + SLOT_DURATION;
    }

    // slither-disable-end reentrancy-no-eth

    /// @notice Stores the current ETH balance at the current block and beacon block root
    ///         of the slot that is associated with the previous block.
    ///
    /// When snapping / verifying balance it is of a high importance that there is no
    /// miss-match in respect to ETH that is held by the contract and balances that are
    /// verified on the validators.
    ///
    /// First some context on the beacon-chain block building behaviour. Relevant parts of
    /// constructing a block on the beacon chain consist of:
    ///  - process_withdrawals: ETH is deducted from the validator's balance
    ///  - process_execution_payload: immediately after the previous step executing all the
    ///    transactions
    ///  - apply the withdrawals: adding ETH to the recipient which is the withdrawal address
    ///    contained in the withdrawal credentials of the exited validators
    ///
    /// That means that balance increases which are part of the post-block execution state are
    /// done within the block, but the transaction that are contained within that block can not
    /// see / interact with the balance from the exited validators. Only transactions in the
    /// next block can do that.
    ///
    /// When snap balances is performed the state of the chain is snapped across 2 separate
    /// chain states:
    ///  - ETH balance of the contract is recorded on block X -> and corresponding slot Y
    ///  - beacon chain block root is recorded of block X - 1 -> and corresponding slot Y - 1
    ///    given there were no missed slots. It could also be Y - 2, Y - 3 depending on how
    ///    many slots have not managed to propose a block. For the sake of simplicity this slot
    ///    will be referred to as Y - 1 as it makes no difference in the argument
    ///
    /// Given these 2 separate chain states it is paramount that verify balances can not experience
    /// miss-counting ETH or much more dangerous double counting of the ETH.
    ///
    /// When verifyBalances is called it is performed on the current block Z where Z > X. Verify
    /// balances adds up all the ETH (omitting WETH) controlled by this contract:
    ///  - ETH balance in the contract on block X
    ///  - ETH balance in Deposits on block Z that haven't been yet processed in slot Y - 1
    ///  - ETH balance in validators that are active in slot Y - 1
    ///  - skips the ETH balance in validators that have withdrawn in slot Y - 1 (or sooner)
    ///    and have their balance visible to transactions in slot Y and corresponding block X
    ///    (or sooner)
    ///
    /// Lets verify the correctness of ETH accounting given the above described behaviour.
    ///
    /// *ETH balance in the contract on block X*
    ///
    /// This is an ETH balance of the contract on a non current X block. Any ETH leaving the
    /// contract as a result of a withdrawal subtracts from the ETH accounted for on block X
    /// if `verifyBalances` has already been called. It also invalidates a `snapBalances` in
    /// case `verifyBalances` has not been called yet. Not performing this would result in not
    /// accounting for the withdrawn ETH that has happened anywhere in the block interval [X + 1, Z].
    ///
    /// Similarly to withdrawals any `stakeEth` deposits to the deposit contract adds to the ETH
    /// accounted for since the last `verifyBalances` has been called. And it invalidates the
    /// `snapBalances` in case `verifyBalances` hasn't been yet called. Not performing this
    /// would result in double counting the `stakedEth` since it would be present once in the
    /// snapped contract balance and the second time in deposit storage variables.
    ///
    /// This behaviour is correct.
    ///
    /// *ETH balance in Deposits on block Z that haven't been yet processed in slot Y - 1*
    ///
    /// The contract sums up all the ETH that has been deposited to the Beacon chain deposit
    /// contract at block Z. The execution layer doesn't have direct access to the state of
    /// deposits on the beacon chain. And if it is to sum up all the ETH that is marked to be
    /// deposited it needs to be sure to not double count ETH that is in deposits (storage vars)
    /// and could also be part of the validator balances. It does that by verifying that at
    /// slot Y - 1 none of the deposits visible on block Z have been processed. Meaning since
    /// the last snap till now all are still in queue. Which ensures they can not be part of
    /// the validator balances in later steps.
    ///
    /// This behaviour is correct.
    ///
    /// *ETH balance in validators that are active in slot Y - 1*
    ///
    /// The contract is verifying none of the deposits on Y - 1 slot have been processed and
    /// for that reason it checks the validator balances in the same slot. Ensuring accounting
    /// correctness.
    ///
    /// This behaviour is correct.
    ///
    /// *The withdrawn validators*
    ///
    /// The withdrawn validators could have their balances deducted in any slot before slot
    /// Y - 1 and the execution layer sees the balance increase in the subsequent slot. Lets
    /// look at the "worst case scenario" where the validator withdrawal is processed in the
    /// slot Y - 1 (snapped slot) and see their balance increase (in execution layer) in slot
    /// Y -> block X. The ETH balance on the contract is snapped at block X meaning that
    /// even if the validator exits at the latest possible time it is paramount that the ETH
    /// balance on the execution layer is recorded in the next block. Correctly accounting
    /// for the withdrawn ETH.
    ///
    /// Worth mentioning if the validator exit is processed by the slot Y and balance increase
    /// seen on the execution layer on block X + 1 the withdrawal is ignored by both the
    /// validator balance verification as well as execution layer contract balance snap.
    ///
    /// This behaviour is correct.
    ///
    /// The validator balances on the beacon chain can then be proved with `verifyBalances`.
    /// Can only be called by the registrator.
    function snapBalances() external onlyRegistrator {
        _snapBalances();
    }

    function _snapBalances() internal {
        bytes32 blockRoot = BeaconRoots.parentBlockRoot(
            SafeCast.toUint64(block.timestamp)
        );
        // Get the current ETH balance
        uint256 ethBalance = address(this).balance;

        // Store the balances in the mapping
        snappedBalances[blockRoot] = Balances({
            timestamp: SafeCast.toUint64(block.timestamp),
            ethBalance: SafeCast.toUint128(ethBalance)
        });

        // Store the snapped timestamp
        lastSnapTimestamp = SafeCast.toUint64(block.timestamp);

        emit BalancesSnapped(block.timestamp, blockRoot, ethBalance);
    }

    // A struct is used to avoid stack too deep errors
    struct BalanceProofs {
        // BeaconBlock.state.balances
        bytes32 balancesContainerRoot;
        bytes balancesContainerProof;
        // BeaconBlock.state.balances[validatorIndex]
        bytes32[] validatorBalanceLeaves;
        bytes[] validatorBalanceProofs;
    }

    /// @notice Verifies the balances of all active validators on the beacon chain
    /// and checks no pending deposits have been processed by the beacon chain.
    /// @param snapBlockRoot The beacon block root emitted from `snapBalance` in `BalancesSnapped`.
    /// @param balanceProofs a `BalanceProofs` struct containing the following:
    /// balancesContainerRoot - the merkle root of the balances container
    /// balancesContainerProof - The merkle proof for the balances container to the beacon block root.
    ///   This is 9 witness hashes of 32 bytes each concatenated together starting from the leaf node.
    /// validatorBalanceLeaves - Array of leaf nodes containing the validator balance with three other balances.
    /// validatorBalanceProofs -  Array of merkle proofs for the validator balance to the Balances container root.
    ///   This is 39 witness hashes of 32 bytes each concatenated together starting from the leaf node.
    // slither-disable-start reentrancy-no-eth
    function verifyBalances(
        bytes32 snapBlockRoot,
        uint64 validatorVerificationBlockTimestamp,
        FirstPendingDepositProofData calldata firstPendingDeposit,
        BalanceProofs calldata balanceProofs
    ) external {
        // Load previously snapped balances for the given block root
        Balances memory balancesMem = snappedBalances[snapBlockRoot];
        // Check the balances are the latest
        require(lastSnapTimestamp > 0, "No snapped balances");
        require(balancesMem.timestamp == lastSnapTimestamp, "Stale snap");

        uint256 verifiedValidatorsCount = verifiedValidators.length;
        uint256 totalValidatorBalance = 0;

        // If there are no verified validators then we can skip the balance verification
        if (verifiedValidatorsCount > 0) {
            require(
                balanceProofs.validatorBalanceProofs.length ==
                    verifiedValidatorsCount,
                "Invalid balance proofs"
            );
            require(
                balanceProofs.validatorBalanceLeaves.length ==
                    verifiedValidatorsCount,
                "Invalid balance leaves"
            );
            // verify beaconBlock.state.balances root to beacon block root
            IBeaconProofs(BEACON_PROOFS).verifyBalancesContainer(
                snapBlockRoot,
                balanceProofs.balancesContainerRoot,
                balanceProofs.balancesContainerProof
            );

            // for each validator in reserve order so we can pop off exited validators at the end
            for (uint256 i = verifiedValidatorsCount; i > 0; ) {
                --i;
                // verify validator's balance in beaconBlock.state.balances to the
                // beaconBlock.state.balances container root
                uint256 validatorBalanceGwei = IBeaconProofs(BEACON_PROOFS)
                    .verifyValidatorBalance(
                        balanceProofs.balancesContainerRoot,
                        balanceProofs.validatorBalanceLeaves[i],
                        balanceProofs.validatorBalanceProofs[i],
                        validator[verifiedValidators[i]].index
                    );

                // If the validator balance is zero
                if (validatorBalanceGwei == 0) {
                    // Store the validator state as exited
                    // This could have been in VERIFIED or EXITING state
                    validator[verifiedValidators[i]].state = VALIDATOR_STATE
                        .EXITED;

                    // Remove the validator with a zero balance from the list of verified validators

                    // Reduce the count of verified validators which is the last index before the pop removes it.
                    verifiedValidatorsCount -= 1;

                    // Move the last validator that has already been verified to the current index.
                    // There's an extra SSTORE if i is the last active validator but that's fine,
                    // It's not a common case and the code is simpler this way.
                    verifiedValidators[i] = verifiedValidators[
                        verifiedValidatorsCount
                    ];
                    // Delete the last validator from the list
                    verifiedValidators.pop();

                    // The validator balance is zero so not need to add to totalValidatorBalance
                    continue;
                }

                // convert Gwei balance to Wei and add to the total validator balance
                totalValidatorBalance += uint256(validatorBalanceGwei) * 1 gwei;
            }
        }

        uint256 depositsCount = depositList.length;
        uint256 totalDepositsWei = 0;

        // If there are no deposits then we can skip the deposit verification.
        // This section is after the validator balance verifications so an exited validator will be marked
        // as EXITED before the deposits are verified. If there was a deposit to an exited validator
        // then the deposit can only be removed once the validator is fully exited.
        if (depositsCount > 0) {
            // Verify the slot of the first pending deposit matches the beacon chain
            bool isDepositQueueEmpty = IBeaconProofs(BEACON_PROOFS)
                .verifyFirstPendingDeposit(
                    snapBlockRoot,
                    firstPendingDeposit.slot,
                    firstPendingDeposit.pubKeyHash,
                    firstPendingDeposit.pendingDepositPubKeyProof
                );

            // If there are no deposits in the beacon chain queue then our deposits must have been processed.
            // If the deposits have been processed, each deposit will need to be verified with `verifyDeposit`
            require(!isDepositQueueEmpty, "Deposits have been processed");

            // The verification of the validator the first pending deposit is for must be on or after when
            // `snapBalances` was called.
            require(
                balancesMem.timestamp <= validatorVerificationBlockTimestamp,
                "Invalid validator timestamp"
            );

            // Verify the validator of the first pending deposit is not exiting by checking
            // the withdrawable epoch is far into the future.
            // If it is exiting we can't be sure this deposit has not been postponed in the deposit queue.
            // Hence we can not verify if the strategy's deposit has been processed or not.
            IBeaconProofs(BEACON_PROOFS).verifyValidatorWithdrawable(
                // Get the parent beacon block root of the next block which is
                // the block root of the validator verification slot.
                // This will revert if the slot after the verification slot was missed.
                BeaconRoots.parentBlockRoot(
                    validatorVerificationBlockTimestamp
                ),
                firstPendingDeposit.validatorIndex,
                firstPendingDeposit.pubKeyHash,
                // Validator is not exiting
                FAR_FUTURE_EPOCH,
                firstPendingDeposit.withdrawableEpochProof,
                firstPendingDeposit.validatorPubKeyProof
            );

            // solhint-disable max-line-length
            // If a validator is converted from a sweeping validator to a compounding validator, any balance in excess
            // of the min 32 ETH is put in the pending deposit queue. Reference:
            // - [switch_to_compounding_validator](https://ethereum.github.io/consensus-specs/specs/electra/beacon-chain/#new-switch_to_compounding_validator
            // - [queue_excess_active_balance](https://ethereum.github.io/consensus-specs/specs/electra/beacon-chain/#new-queue_excess_active_balance)
            // - [process_consolidation_request](https://ethereum.github.io/consensus-specs/specs/electra/beacon-chain/#new-process_consolidation_request)
            // This will have a slot value of zero unfortunately.
            // We can not prove the strategy's deposits are still pending with a zero slot value so revert the tx.
            // Another snapBalances will need to be taken that does not have consolidation deposits at the front of the
            // beacon chain deposit queue.
            // solhint-enable max-line-length
            require(
                firstPendingDeposit.slot > 0,
                "Invalid first pending deposit"
            );

            // Calculate the epoch at the time of the snapBalances
            uint64 verificationEpoch = (SafeCast.toUint64(
                balancesMem.timestamp
            ) - BEACON_GENESIS_TIMESTAMP) / (SLOT_DURATION * SLOTS_PER_EPOCH);

            // For each staking strategy's deposits
            for (uint256 i = 0; i < depositsCount; ++i) {
                uint256 depositID = depositList[i];
                DepositData memory depositData = deposits[depositID];

                // Check the stored deposit is still waiting to be processed on the beacon chain.
                // That is, the first pending deposit slot is before the slot of the staking strategy's deposit.
                // If the deposit has been processed, it will need to be verified with `verifyDeposit`.
                // OR the deposit is to an exiting validator so check it is still not withdrawable.
                // If the validator is not withdrawable, then the deposit can not have been processed yet.
                // If the validator is now withdrawable, then the deposit may have been processed. The strategy
                // now has to wait until the validator's balance is verified to be zero.
                require(
                    firstPendingDeposit.slot < depositData.slot ||
                        (validator[depositData.pubKeyHash].state ==
                            VALIDATOR_STATE.EXITED &&
                            verificationEpoch < depositData.withdrawableEpoch),
                    "Deposit likely processed"
                );

                // Convert the deposit amount from Gwei to Wei and add to the total
                totalDepositsWei += uint256(depositData.amountGwei) * 1 gwei;

                // Remove the deposit if the validator has exited.
                if (
                    validator[depositData.pubKeyHash].state ==
                    VALIDATOR_STATE.EXITED
                ) {
                    _removeDeposit(depositID, depositData);

                    emit DepositValidatorExited(
                        depositID,
                        uint256(depositData.amountGwei) * 1 gwei
                    );
                }
            }
        }

        // Store the verified balance in storage
        lastVerifiedEthBalance = SafeCast.toUint128(
            totalDepositsWei + totalValidatorBalance + balancesMem.ethBalance
        );
        // Reset the last snap timestamp so a new snapBalances has to be made
        lastSnapTimestamp = 0;

        emit BalancesVerified(
            balancesMem.timestamp,
            totalDepositsWei,
            totalValidatorBalance,
            balancesMem.ethBalance
        );
    }

    // slither-disable-end reentrancy-no-eth

    /// @notice Hash a validator public key using the Beacon Chain's format
    function _hashPubKey(bytes memory pubKey) internal pure returns (bytes32) {
        require(pubKey.length == 48, "Invalid public key length");
        return sha256(abi.encodePacked(pubKey, bytes16(0)));
    }

    /***************************************
            WETH and ETH Accounting
    ****************************************/

    /// @dev Called when WETH is transferred out of the strategy so
    /// the strategy knows how much WETH it has on deposit.
    /// This is so it can emit the correct amount in the Deposit event in depositAll().
    function _transferWeth(uint256 _amount, address _recipient) internal {
        IERC20(WETH).safeTransfer(_recipient, _amount);

        // The min is required as more WETH can be withdrawn than deposited
        // as the strategy earns consensus and execution rewards.
        uint256 deductAmount = Math.min(_amount, depositedWethAccountedFor);
        depositedWethAccountedFor -= deductAmount;

        // No change in ETH balance so no need to snapshot the balances
    }

    /// @dev Converts ETH to WETH and updates the accounting.
    /// @param _ethAmount The amount of ETH in wei.
    function _convertEthToWeth(uint256 _ethAmount) internal {
        // slither-disable-next-line arbitrary-send-eth
        IWETH9(WETH).deposit{ value: _ethAmount }();

        depositedWethAccountedFor += _ethAmount;

        // Store the reduced ETH balance.
        // The ETH balance in this strategy contract can be more than the last verified ETH balance
        // due to partial withdrawals or full exits being processed by the beacon chain since the last snapBalances.
        // It can also happen from execution rewards (MEV) or ETH donations.
        lastVerifiedEthBalance -= SafeCast.toUint128(
            Math.min(uint256(lastVerifiedEthBalance), _ethAmount)
        );

        // The ETH balance was decreased to WETH so we need to invalidate the last balances snap.
        lastSnapTimestamp = 0;
    }

    /// @dev Converts WETH to ETH and updates the accounting.
    /// @param _wethAmount The amount of WETH in wei.
    function _convertWethToEth(uint256 _wethAmount) internal {
        IWETH9(WETH).withdraw(_wethAmount);

        uint256 deductAmount = Math.min(_wethAmount, depositedWethAccountedFor);
        depositedWethAccountedFor -= deductAmount;

        // Store the increased ETH balance
        lastVerifiedEthBalance += SafeCast.toUint128(_wethAmount);

        // The ETH balance was increased from WETH so we need to invalidate the last balances snap.
        lastSnapTimestamp = 0;
    }

    /***************************************
                View Functions
    ****************************************/

    struct ValidatorView {
        bytes32 pubKeyHash;
        uint64 index;
        VALIDATOR_STATE state;
    }

    /// @notice Returns the strategy's active validators.
    /// These are the ones that have been verified and have a non-zero balance.
    /// @return validators An array of `ValidatorView` containing the public key hash, validator index and state.
    function getVerifiedValidators()
        external
        view
        returns (ValidatorView[] memory validators)
    {
        uint256 validatorCount = verifiedValidators.length;
        validators = new ValidatorView[](validatorCount);
        for (uint256 i = 0; i < validatorCount; ++i) {
            bytes32 pubKeyHash = verifiedValidators[i];
            ValidatorData memory validatorData = validator[pubKeyHash];
            validators[i] = ValidatorView({
                pubKeyHash: pubKeyHash,
                index: validatorData.index,
                state: validatorData.state
            });
        }
    }

    struct DepositView {
        uint256 depositID;
        bytes32 pubKeyHash;
        uint64 amountGwei;
        uint64 slot;
        uint256 withdrawableEpoch;
    }

    /// @notice Returns the deposits that are still to be verified.
    /// These may or may not have been processed by the beacon chain.
    /// @return pendingDeposits An array of `DepositView` containing the deposit ID, public key hash,
    /// amount in Gwei and the slot of the deposit.
    function getPendingDeposits()
        external
        view
        returns (DepositView[] memory pendingDeposits)
    {
        uint256 depositsCount = depositList.length;
        pendingDeposits = new DepositView[](depositsCount);
        for (uint256 i = 0; i < depositsCount; ++i) {
            DepositData memory deposit = deposits[depositList[i]];
            pendingDeposits[i] = DepositView({
                depositID: depositList[i],
                pubKeyHash: deposit.pubKeyHash,
                amountGwei: deposit.amountGwei,
                slot: deposit.slot,
                withdrawableEpoch: deposit.withdrawableEpoch
            });
        }
    }
}

contracts/strategies/NativeStaking/FeeAc.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import { Address } from "@openzeppelin/contracts/utils/Address.sol";

/**
 * @title Fee Accumulator for Native Staking SSV Strategy
 * @notice Receives execution rewards which includes tx fees and
 * MEV rewards like tx priority and tx ordering.
 * It does NOT include swept ETH from beacon chain consensus rewards or full validator withdrawals.
 * @author Origin Protocol Inc
 */
contract FeeAccumulator {
    /// @notice The address of the Native Staking Strategy
    address public immutable STRATEGY;

    event ExecutionRewardsCollected(address indexed strategy, uint256 amount);

    /**
     * @param _strategy Address of the Native Staking Strategy
     */
    constructor(address _strategy) {
        STRATEGY = _strategy;
    }

    /**
     * @notice sends all ETH in this FeeAccumulator contract to the Native Staking Strategy.
     * @return eth The amount of execution rewards that were sent to the Native Staking Strategy
     */
    function collect() external returns (uint256 eth) {
        require(msg.sender == STRATEGY, "Caller is not the Strategy");

        eth = address(this).balance;
        if (eth > 0) {
            // Send the ETH to the Native Staking Strategy
            Address.sendValue(payable(STRATEGY), eth);

            emit ExecutionRewardsCollected(STRATEGY, eth);
        }
    }

    /**
     * @dev Accept ETH
     */
    receive() external payable {}
}

contracts/strategies/NativeStaking/Nativ.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";

import { InitializableAbstractStrategy } from "../../utils/InitializableAbstractStrategy.sol";
import { IWETH9 } from "../../interfaces/IWETH9.sol";
import { FeeAccumulator } from "./FeeAccumulator.sol";
import { ValidatorAccountant } from "./ValidatorAccountant.sol";
import { ISSVNetwork } from "../../interfaces/ISSVNetwork.sol";

struct ValidatorStakeData {
    bytes pubkey;
    bytes signature;
    bytes32 depositDataRoot;
}

/// @title Native Staking SSV Strategy
/// @notice Strategy to deploy funds into DVT validators powered by the SSV Network
/// @author Origin Protocol Inc
/// @dev This contract handles WETH and ETH and in some operations interchanges between the two. Any WETH that
/// is on the contract across multiple blocks (and not just transitory within a transaction) is considered an
/// asset. Meaning deposits increase the balance of the asset and withdrawal decrease it. As opposed to all
/// our other strategies the WETH doesn't immediately get deposited into an underlying strategy and can be present
/// across multiple blocks waiting to be unwrapped to ETH and staked to validators. This separation of WETH and ETH is
/// required since the rewards (reward token) is also in ETH.
///
/// To simplify the accounting of WETH there is another difference in behavior compared to the other strategies.
/// To withdraw WETH asset - exit message is posted to validators and the ETH hits this contract with multiple days
/// delay. In order to simplify the WETH accounting upon detection of such an event the ValidatorAccountant
/// immediately wraps ETH to WETH and sends it to the Vault.
///
/// On the other hand any ETH on the contract (across multiple blocks) is there either:
///  - as a result of already accounted for consensus rewards
///  - as a result of not yet accounted for consensus rewards
///  - as a results of not yet accounted for full validator withdrawals (or validator slashes)
///
/// Even though the strategy assets and rewards are a very similar asset the consensus layer rewards and the
/// execution layer rewards are considered rewards and those are dripped to the Vault over a configurable time
/// interval and not immediately.
contract NativeStakingSSVStrategy is
    ValidatorAccountant,
    InitializableAbstractStrategy
{
    using SafeERC20 for IERC20;

    /// @notice SSV ERC20 token that serves as a payment for operating SSV validators
    address public immutable SSV_TOKEN;
    /// @notice Fee collector address
    /// @dev this address will receive maximal extractable value (MEV) rewards. These are
    /// rewards for arranging transactions in a way that benefits the validator.
    address payable public immutable FEE_ACCUMULATOR_ADDRESS;

    /// @dev This contract receives WETH as the deposit asset, but unlike other strategies doesn't immediately
    /// deposit it to an underlying platform. Rather a special privilege account stakes it to the validators.
    /// For that reason calling WETH.balanceOf(this) in a deposit function can contain WETH that has just been
    /// deposited and also WETH that has previously been deposited. To keep a correct count we need to keep track
    /// of WETH that has already been accounted for.
    /// This value represents the amount of WETH balance of this contract that has already been accounted for by the
    /// deposit events.
    /// It is important to note that this variable is not concerned with WETH that is a result of full/partial
    /// withdrawal of the validators. It is strictly concerned with WETH that has been deposited and is waiting to
    /// be staked.
    uint256 public depositedWethAccountedFor;

    // For future use
    uint256[49] private __gap;

    /// @param _baseConfig Base strategy config with platformAddress (ERC-4626 Vault contract), eg sfrxETH or sDAI,
    /// and vaultAddress (OToken Vault contract), eg VaultProxy or OETHVaultProxy
    /// @param _wethAddress Address of the Erc20 WETH Token contract
    /// @param _ssvToken Address of the Erc20 SSV Token contract
    /// @param _ssvNetwork Address of the SSV Network contract
    /// @param _maxValidators Maximum number of validators that can be registered in the strategy
    /// @param _feeAccumulator Address of the fee accumulator receiving execution layer validator rewards
    /// @param _beaconChainDepositContract Address of the beacon chain deposit contract
    constructor(
        BaseStrategyConfig memory _baseConfig,
        address _wethAddress,
        address _ssvToken,
        address _ssvNetwork,
        uint256 _maxValidators,
        address _feeAccumulator,
        address _beaconChainDepositContract
    )
        InitializableAbstractStrategy(_baseConfig)
        ValidatorAccountant(
            _wethAddress,
            _baseConfig.vaultAddress,
            _beaconChainDepositContract,
            _ssvNetwork,
            _maxValidators
        )
    {
        SSV_TOKEN = _ssvToken;
        FEE_ACCUMULATOR_ADDRESS = payable(_feeAccumulator);
    }

    /// @notice Set up initial internal state including
    /// 1. approving the SSVNetwork to transfer SSV tokens from this strategy contract
    /// 2. setting the recipient of SSV validator MEV rewards to the FeeAccumulator contract.
    /// @param _rewardTokenAddresses Address of reward token for platform
    /// @param _assets Addresses of initial supported assets
    /// @param _pTokens Platform Token corresponding addresses
    function initialize(
        address[] memory _rewardTokenAddresses,
        address[] memory _assets,
        address[] memory _pTokens
    ) external onlyGovernor initializer {
        InitializableAbstractStrategy._initialize(
            _rewardTokenAddresses,
            _assets,
            _pTokens
        );

        // Approves the SSV Network contract to transfer SSV tokens for deposits
        IERC20(SSV_TOKEN).approve(SSV_NETWORK, type(uint256).max);

        // Set the FeeAccumulator as the address for SSV validators to send MEV rewards to
        ISSVNetwork(SSV_NETWORK).setFeeRecipientAddress(
            FEE_ACCUMULATOR_ADDRESS
        );
    }

    /// @notice Unlike other strategies, this does not deposit assets into the underlying platform.
    /// It just checks the asset is WETH and emits the Deposit event.
    /// To deposit WETH into validators `registerSsvValidator` and `stakeEth` must be used.
    /// Will NOT revert if the strategy is paused from an accounting failure.
    /// @param _asset Address of asset to deposit. Has to be WETH.
    /// @param _amount Amount of assets that were transferred to the strategy by the vault.
    function deposit(address _asset, uint256 _amount)
        external
        override
        onlyVault
        nonReentrant
    {
        require(_asset == WETH, "Unsupported asset");
        depositedWethAccountedFor += _amount;
        _deposit(_asset, _amount);
    }

    /// @dev Deposit WETH to this strategy so it can later be staked into a validator.
    /// @param _asset Address of WETH
    /// @param _amount Amount of WETH to deposit
    function _deposit(address _asset, uint256 _amount) internal {
        require(_amount > 0, "Must deposit something");
        /*
         * We could do a check here that would revert when "_amount % 32 ether != 0". With the idea of
         * not allowing deposits that will result in WETH sitting on the strategy after all the possible batches
         * of 32ETH have been staked.
         * But someone could mess with our strategy by sending some WETH to it. And we might want to deposit just
         * enough WETH to add it up to 32 so it can be staked. For that reason the check is left out.
         *
         * WETH sitting on the strategy won't interfere with the accounting since accounting only operates on ETH.
         */
        emit Deposit(_asset, address(0), _amount);
    }

    /// @notice Unlike other strategies, this does not deposit assets into the underlying platform.
    /// It just emits the Deposit event.
    /// To deposit WETH into validators `registerSsvValidator` and `stakeEth` must be used.
    /// Will NOT revert if the strategy is paused from an accounting failure.
    function depositAll() external override onlyVault nonReentrant {
        uint256 wethBalance = IERC20(WETH).balanceOf(address(this));
        uint256 newWeth = wethBalance - depositedWethAccountedFor;

        if (newWeth > 0) {
            depositedWethAccountedFor = wethBalance;

            _deposit(WETH, newWeth);
        }
    }

    /// @notice Withdraw WETH from this contract. Used only if some WETH for is lingering on the contract.
    /// That can happen when:
    ///   - after mints if the strategy is the default
    ///   - time between depositToStrategy and stakeEth
    ///   - the deposit was not a multiple of 32 WETH
    ///   - someone sent WETH directly to this contract
    /// Will NOT revert if the strategy is paused from an accounting failure.
    /// @param _recipient Address to receive withdrawn assets
    /// @param _asset WETH to withdraw
    /// @param _amount Amount of WETH to withdraw
    function withdraw(
        address _recipient,
        address _asset,
        uint256 _amount
    ) external override onlyVault nonReentrant {
        require(_asset == WETH, "Unsupported asset");
        _withdraw(_recipient, _asset, _amount);
    }

    function _withdraw(
        address _recipient,
        address _asset,
        uint256 _amount
    ) internal {
        require(_amount > 0, "Must withdraw something");
        require(_recipient != address(0), "Must specify recipient");

        _wethWithdrawn(_amount);

        IERC20(_asset).safeTransfer(_recipient, _amount);
        emit Withdrawal(_asset, address(0), _amount);
    }

    /// @notice transfer all WETH deposits back to the vault.
    /// This does not withdraw from the validators. That has to be done separately with the
    /// `exitSsvValidator` and `removeSsvValidator` operations.
    /// This does not withdraw any execution rewards from the FeeAccumulator or
    /// consensus rewards in this strategy.
    /// Any ETH in this strategy that was swept from a full validator withdrawal will not be withdrawn.
    /// ETH from full validator withdrawals is sent to the Vault using `doAccounting`.
    /// Will NOT revert if the strategy is paused from an accounting failure.
    function withdrawAll() external override onlyVaultOrGovernor nonReentrant {
        uint256 wethBalance = IERC20(WETH).balanceOf(address(this));
        if (wethBalance > 0) {
            _withdraw(vaultAddress, WETH, wethBalance);
        }
    }

    /// @notice Returns the total value of (W)ETH that is staked to the validators
    /// and WETH deposits that are still to be staked.
    /// This does not include ETH from consensus rewards sitting in this strategy
    /// or ETH from MEV rewards in the FeeAccumulator. These rewards are harvested
    /// and sent to the Dripper so will eventually be sent to the Vault as WETH.
    /// @param _asset      Address of weth asset
    /// @return balance    Total value of (W)ETH
    function checkBalance(address _asset)
        external
        view
        override
        returns (uint256 balance)
    {
        require(_asset == WETH, "Unsupported asset");

        balance =
            // add the ETH that has been staked in validators
            activeDepositedValidators *
            FULL_STAKE +
            // add the WETH in the strategy from deposits that are still to be staked
            IERC20(WETH).balanceOf(address(this));
    }

    function pause() external onlyStrategist {
        _pause();
    }

    /// @notice Returns bool indicating whether asset is supported by strategy.
    /// @param _asset The address of the asset token.
    function supportsAsset(address _asset) public view override returns (bool) {
        return _asset == WETH;
    }

    /// @notice Approves the SSV Network contract to transfer SSV tokens for deposits
    function safeApproveAllTokens() external override {
        // Approves the SSV Network contract to transfer SSV tokens for deposits
        IERC20(SSV_TOKEN).approve(SSV_NETWORK, type(uint256).max);
    }

    /// @notice Set the FeeAccumulator as the address for SSV validators to send MEV rewards to
    function setFeeRecipient() external {
        ISSVNetwork(SSV_NETWORK).setFeeRecipientAddress(
            FEE_ACCUMULATOR_ADDRESS
        );
    }

    /**
     * @notice Only accept ETH from the FeeAccumulator and the WETH contract - required when
     * unwrapping WETH just before staking it to the validator.
     * The strategy will also receive ETH from the priority fees of transactions when producing blocks
     * as defined in EIP-1559.
     * The tx fees come from the Beacon chain so do not need any EVM level permissions to receive ETH.
     * The tx fees are paid with each block produced. They are not included in the consensus rewards
     * which are periodically swept from the validators to this strategy.
     * For accounting purposes, the priority fees of transactions will be considered consensus rewards
     * and will be included in the AccountingConsensusRewards event.
     * @dev don't want to receive donations from anyone else as donations over the fuse limits will
     * mess with the accounting of the consensus rewards and validator full withdrawals.
     */
    receive() external payable {
        require(
            msg.sender == FEE_ACCUMULATOR_ADDRESS || msg.sender == WETH,
            "Eth not from allowed contracts"
        );
    }

    /***************************************
                Internal functions
    ****************************************/

    function _abstractSetPToken(address _asset, address) internal override {}

    /// @dev Convert accumulated ETH to WETH and send to the Harvester.
    /// Will revert if the strategy is paused for accounting.
    function _collectRewardTokens() internal override whenNotPaused {
        // collect ETH from execution rewards from the fee accumulator
        uint256 executionRewards = FeeAccumulator(FEE_ACCUMULATOR_ADDRESS)
            .collect();

        // total ETH rewards to be harvested = execution rewards + consensus rewards
        uint256 ethRewards = executionRewards + consensusRewards;

        require(
            address(this).balance >= ethRewards,
            "Insufficient eth balance"
        );

        if (ethRewards > 0) {
            // reset the counter keeping track of beacon chain consensus rewards
            consensusRewards = 0;

            // Convert ETH rewards to WETH
            IWETH9(WETH).deposit{ value: ethRewards }();

            IERC20(WETH).safeTransfer(harvesterAddress, ethRewards);
            emit RewardTokenCollected(harvesterAddress, WETH, ethRewards);
        }
    }

    /// @dev emits Withdrawal event from NativeStakingSSVStrategy
    function _wethWithdrawnToVault(uint256 _amount) internal override {
        emit Withdrawal(WETH, address(0), _amount);
    }

    /// @dev Called when WETH is withdrawn from the strategy or staked to a validator so
    /// the strategy knows how much WETH it has on deposit.
    /// This is so it can emit the correct amount in the Deposit event in depositAll().
    function _wethWithdrawn(uint256 _amount) internal override {
        /* In an ideal world we wouldn't need to reduce the deduction amount when the
         * depositedWethAccountedFor is smaller than the _amount.
         *
         * The reason this is required is that a malicious actor could sent WETH directly
         * to this contract and that would circumvent the increase of depositedWethAccountedFor
         * property. When the ETH would be staked the depositedWethAccountedFor amount could
         * be deducted so much that it would be negative.
         */
        uint256 deductAmount = Math.min(_amount, depositedWethAccountedFor);
        depositedWethAccountedFor -= deductAmount;
    }
}

contracts/strategies/NativeStaking/Valid.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import { ValidatorRegistrator } from "./ValidatorRegistrator.sol";
import { IWETH9 } from "../../interfaces/IWETH9.sol";

/// @title Validator Accountant
/// @notice Attributes the ETH swept from beacon chain validators to this strategy contract
/// as either full or partial withdrawals. Partial withdrawals being consensus rewards.
/// Full withdrawals are from exited validators.
/// @author Origin Protocol Inc
abstract contract ValidatorAccountant is ValidatorRegistrator {
    /// @notice The minimum amount of blocks that need to pass between two calls to manuallyFixAccounting
    uint256 public constant MIN_FIX_ACCOUNTING_CADENCE = 7200; // 1 day

    /// @notice Keeps track of the total consensus rewards swept from the beacon chain
    uint256 public consensusRewards;

    /// @notice start of fuse interval
    uint256 public fuseIntervalStart;
    /// @notice end of fuse interval
    uint256 public fuseIntervalEnd;
    /// @notice last block number manuallyFixAccounting has been called
    uint256 public lastFixAccountingBlockNumber;

    uint256[49] private __gap;

    event FuseIntervalUpdated(uint256 start, uint256 end);
    event AccountingFullyWithdrawnValidator(
        uint256 noOfValidators,
        uint256 remainingValidators,
        uint256 wethSentToVault
    );
    event AccountingValidatorSlashed(
        uint256 remainingValidators,
        uint256 wethSentToVault
    );
    event AccountingConsensusRewards(uint256 amount);

    event AccountingManuallyFixed(
        int256 validatorsDelta,
        int256 consensusRewardsDelta,
        uint256 wethToVault
    );

    /// @param _wethAddress Address of the Erc20 WETH Token contract
    /// @param _vaultAddress Address of the Vault
    /// @param _beaconChainDepositContract Address of the beacon chain deposit contract
    /// @param _ssvNetwork Address of the SSV Network contract
    /// @param _maxValidators Maximum number of validators that can be registered in the strategy
    constructor(
        address _wethAddress,
        address _vaultAddress,
        address _beaconChainDepositContract,
        address _ssvNetwork,
        uint256 _maxValidators
    )
        ValidatorRegistrator(
            _wethAddress,
            _vaultAddress,
            _beaconChainDepositContract,
            _ssvNetwork,
            _maxValidators
        )
    {}

    /// @notice set fuse interval values
    function setFuseInterval(
        uint256 _fuseIntervalStart,
        uint256 _fuseIntervalEnd
    ) external onlyGovernor {
        require(
            _fuseIntervalStart < _fuseIntervalEnd &&
                _fuseIntervalEnd < 32 ether &&
                _fuseIntervalEnd - _fuseIntervalStart >= 4 ether,
            "Incorrect fuse interval"
        );

        fuseIntervalStart = _fuseIntervalStart;
        fuseIntervalEnd = _fuseIntervalEnd;

        emit FuseIntervalUpdated(_fuseIntervalStart, _fuseIntervalEnd);
    }

    /* solhint-disable max-line-length */
    /// This notion page offers a good explanation of how the accounting functions
    /// https://www.notion.so/originprotocol/Limited-simplified-native-staking-accounting-67a217c8420d40678eb943b9da0ee77d
    /// In short, after dividing by 32, if the ETH remaining on the contract falls between 0 and fuseIntervalStart,
    /// the accounting function will treat that ETH as Beacon chain consensus rewards.
    /// On the contrary, if after dividing by 32, the ETH remaining on the contract falls between fuseIntervalEnd and 32,
    /// the accounting function will treat that as a validator slashing.
    /// @notice Perform the accounting attributing beacon chain ETH to either full or partial withdrawals. Returns true when
    /// accounting is valid and fuse isn't "blown". Returns false when fuse is blown.
    /// @dev This function could in theory be permission-less but lets allow only the Registrator (Defender Action) to call it
    /// for now.
    /// @return accountingValid true if accounting was successful, false if fuse is blown
    /* solhint-enable max-line-length */
    function doAccounting()
        external
        onlyRegistrator
        whenNotPaused
        nonReentrant
        returns (bool accountingValid)
    {
        // pause the accounting on failure
        accountingValid = _doAccounting(true);
    }

    // slither-disable-start reentrancy-eth
    function _doAccounting(bool pauseOnFail)
        internal
        returns (bool accountingValid)
    {
        if (address(this).balance < consensusRewards) {
            return _failAccounting(pauseOnFail);
        }

        // Calculate all the new ETH that has been swept to the contract since the last accounting
        uint256 newSweptETH = address(this).balance - consensusRewards;
        accountingValid = true;

        // send the ETH that is from fully withdrawn validators to the Vault
        if (newSweptETH >= FULL_STAKE) {
            uint256 fullyWithdrawnValidators;
            // explicitly cast to uint256 as we want to round to a whole number of validators
            fullyWithdrawnValidators = uint256(newSweptETH / FULL_STAKE);
            activeDepositedValidators -= fullyWithdrawnValidators;

            uint256 wethToVault = FULL_STAKE * fullyWithdrawnValidators;
            IWETH9(WETH).deposit{ value: wethToVault }();
            // slither-disable-next-line unchecked-transfer
            IWETH9(WETH).transfer(VAULT_ADDRESS, wethToVault);
            _wethWithdrawnToVault(wethToVault);

            emit AccountingFullyWithdrawnValidator(
                fullyWithdrawnValidators,
                activeDepositedValidators,
                wethToVault
            );
        }

        uint256 ethRemaining = address(this).balance - consensusRewards;
        // should be less than a whole validator stake
        require(ethRemaining < FULL_STAKE, "Unexpected accounting");

        // If no Beacon chain consensus rewards swept
        if (ethRemaining == 0) {
            // do nothing
            return accountingValid;
        } else if (ethRemaining < fuseIntervalStart) {
            // Beacon chain consensus rewards swept (partial validator withdrawals)
            // solhint-disable-next-line reentrancy
            consensusRewards += ethRemaining;
            emit AccountingConsensusRewards(ethRemaining);
        } else if (ethRemaining > fuseIntervalEnd) {
            // Beacon chain consensus rewards swept but also a slashed validator fully exited
            IWETH9(WETH).deposit{ value: ethRemaining }();
            // slither-disable-next-line unchecked-transfer
            IWETH9(WETH).transfer(VAULT_ADDRESS, ethRemaining);
            activeDepositedValidators -= 1;

            _wethWithdrawnToVault(ethRemaining);

            emit AccountingValidatorSlashed(
                activeDepositedValidators,
                ethRemaining
            );
        }
        // Oh no... Fuse is blown. The Strategist needs to adjust the accounting values.
        else {
            return _failAccounting(pauseOnFail);
        }
    }

    // slither-disable-end reentrancy-eth

    /// @dev pause any further accounting if required and return false
    function _failAccounting(bool pauseOnFail)
        internal
        returns (bool accountingValid)
    {
        // pause if not already
        if (pauseOnFail) {
            _pause();
        }
        // fail the accounting
        accountingValid = false;
    }

    /// @notice Allow the Strategist to fix the accounting of this strategy and unpause.
    /// @param _validatorsDelta adjust the active validators by up to plus three or minus three
    /// @param _consensusRewardsDelta adjust the accounted for consensus rewards up or down
    /// @param _ethToVaultAmount the amount of ETH that gets wrapped into WETH and sent to the Vault
    /// @dev There is a case when a validator(s) gets slashed so much that the eth swept from
    /// the beacon chain enters the fuse area and there are no consensus rewards on the contract
    /// to "dip into"/use. To increase the amount of unaccounted ETH over the fuse end interval
    /// we need to reduce the amount of active deposited validators and immediately send WETH
    /// to the vault, so it doesn't interfere with further accounting.
    function manuallyFixAccounting(
        int256 _validatorsDelta,
        int256 _consensusRewardsDelta,
        uint256 _ethToVaultAmount
    ) external onlyStrategist whenPaused nonReentrant {
        require(
            lastFixAccountingBlockNumber + MIN_FIX_ACCOUNTING_CADENCE <
                block.number,
            "Fix accounting called too soon"
        );
        require(
            _validatorsDelta >= -3 &&
                _validatorsDelta <= 3 &&
                // new value must be positive
                int256(activeDepositedValidators) + _validatorsDelta >= 0,
            "Invalid validatorsDelta"
        );
        require(
            _consensusRewardsDelta >= -332 ether &&
                _consensusRewardsDelta <= 332 ether &&
                // new value must be positive
                int256(consensusRewards) + _consensusRewardsDelta >= 0,
            "Invalid consensusRewardsDelta"
        );
        require(_ethToVaultAmount <= 32 ether * 3, "Invalid wethToVaultAmount");

        activeDepositedValidators = uint256(
            int256(activeDepositedValidators) + _validatorsDelta
        );
        consensusRewards = uint256(
            int256(consensusRewards) + _consensusRewardsDelta
        );
        lastFixAccountingBlockNumber = block.number;
        if (_ethToVaultAmount > 0) {
            IWETH9(WETH).deposit{ value: _ethToVaultAmount }();
            // slither-disable-next-line unchecked-transfer
            IWETH9(WETH).transfer(VAULT_ADDRESS, _ethToVaultAmount);
            _wethWithdrawnToVault(_ethToVaultAmount);
        }

        emit AccountingManuallyFixed(
            _validatorsDelta,
            _consensusRewardsDelta,
            _ethToVaultAmount
        );

        // rerun the accounting to see if it has now been fixed.
        // Do not pause the accounting on failure as it is already paused
        require(_doAccounting(false), "Fuse still blown");

        // unpause since doAccounting was successful
        _unpause();
    }

    /***************************************
                 Abstract
    ****************************************/

    /// @dev allows for NativeStakingSSVStrategy contract to emit the Withdrawal event
    function _wethWithdrawnToVault(uint256 _amount) internal virtual;
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import { Pausable } from "@openzeppelin/contracts/security/Pausable.sol";
import { Governable } from "../../governance/Governable.sol";
import { IDepositContract } from "../../interfaces/IDepositContract.sol";
import { IVault } from "../../interfaces/IVault.sol";
import { IWETH9 } from "../../interfaces/IWETH9.sol";
import { ISSVNetwork, Cluster } from "../../interfaces/ISSVNetwork.sol";

struct ValidatorStakeData {
    bytes pubkey;
    bytes signature;
    bytes32 depositDataRoot;
}

/**
 * @title Registrator of the validators
 * @notice This contract implements all the required functionality to register, exit and remove validators.
 * @author Origin Protocol Inc
 */
abstract contract ValidatorRegistrator is Governable, Pausable {
    /// @notice The maximum amount of ETH that can be staked by a validator
    /// @dev this can change in the future with EIP-7251, Increase the MAX_EFFECTIVE_BALANCE
    uint256 public constant FULL_STAKE = 32 ether;

    /// @notice The address of the Wrapped ETH (WETH) token contract
    address public immutable WETH;
    /// @notice The address of the beacon chain deposit contract
    address public immutable BEACON_CHAIN_DEPOSIT_CONTRACT;
    /// @notice The address of the SSV Network contract used to interface with
    address public immutable SSV_NETWORK;
    /// @notice Address of the OETH Vault proxy contract
    address public immutable VAULT_ADDRESS;
    /// @notice Maximum number of validators that can be registered in this strategy
    uint256 public immutable MAX_VALIDATORS;

    /// @notice Address of the registrator - allowed to register, exit and remove validators
    address public validatorRegistrator;
    /// @notice The number of validators that have 32 (!) ETH actively deposited. When a new deposit
    /// to a validator happens this number increases, when a validator exit is detected this number
    /// decreases.
    uint256 public activeDepositedValidators;
    /// @notice State of the validators keccak256(pubKey) => state
    mapping(bytes32 => VALIDATOR_STATE) public validatorsStates;
    /// @notice The account that is allowed to modify stakeETHThreshold and reset stakeETHTally
    address public stakingMonitor;
    /// @notice Amount of ETH that can be staked before staking on the contract is suspended
    /// and the `stakingMonitor` needs to approve further staking by calling `resetStakeETHTally`
    uint256 public stakeETHThreshold;
    /// @notice Amount of ETH that has been staked since the `stakingMonitor` last called `resetStakeETHTally`.
    /// This can not go above `stakeETHThreshold`.
    uint256 public stakeETHTally;

    // For future use
    uint256[47] private __gap;

    enum VALIDATOR_STATE {
        NON_REGISTERED, // validator is not registered on the SSV network
        REGISTERED, // validator is registered on the SSV network
        STAKED, // validator has funds staked
        EXITING, // exit message has been posted and validator is in the process of exiting
        EXIT_COMPLETE // validator has funds withdrawn to the EigenPod and is removed from the SSV
    }

    event RegistratorChanged(address indexed newAddress);
    event StakingMonitorChanged(address indexed newAddress);
    event ETHStaked(bytes32 indexed pubKeyHash, bytes pubKey, uint256 amount);
    event SSVValidatorRegistered(
        bytes32 indexed pubKeyHash,
        bytes pubKey,
        uint64[] operatorIds
    );
    event SSVValidatorExitInitiated(
        bytes32 indexed pubKeyHash,
        bytes pubKey,
        uint64[] operatorIds
    );
    event SSVValidatorExitCompleted(
        bytes32 indexed pubKeyHash,
        bytes pubKey,
        uint64[] operatorIds
    );
    event StakeETHThresholdChanged(uint256 amount);
    event StakeETHTallyReset();

    /// @dev Throws if called by any account other than the Registrator
    modifier onlyRegistrator() {
        require(
            msg.sender == validatorRegistrator,
            "Caller is not the Registrator"
        );
        _;
    }

    /// @dev Throws if called by any account other than the Staking monitor
    modifier onlyStakingMonitor() {
        require(msg.sender == stakingMonitor, "Caller is not the Monitor");
        _;
    }

    /// @dev Throws if called by any account other than the Strategist
    modifier onlyStrategist() {
        require(
            msg.sender == IVault(VAULT_ADDRESS).strategistAddr(),
            "Caller is not the Strategist"
        );
        _;
    }

    /// @param _wethAddress Address of the Erc20 WETH Token contract
    /// @param _vaultAddress Address of the Vault
    /// @param _beaconChainDepositContract Address of the beacon chain deposit contract
    /// @param _ssvNetwork Address of the SSV Network contract
    /// @param _maxValidators Maximum number of validators that can be registered in the strategy
    constructor(
        address _wethAddress,
        address _vaultAddress,
        address _beaconChainDepositContract,
        address _ssvNetwork,
        uint256 _maxValidators
    ) {
        WETH = _wethAddress;
        BEACON_CHAIN_DEPOSIT_CONTRACT = _beaconChainDepositContract;
        SSV_NETWORK = _ssvNetwork;
        VAULT_ADDRESS = _vaultAddress;
        MAX_VALIDATORS = _maxValidators;
    }

    /// @notice Set the address of the registrator which can register, exit and remove validators
    function setRegistrator(address _address) external onlyGovernor {
        validatorRegistrator = _address;
        emit RegistratorChanged(_address);
    }

    /// @notice Set the address of the staking monitor that is allowed to reset stakeETHTally
    function setStakingMonitor(address _address) external onlyGovernor {
        stakingMonitor = _address;
        emit StakingMonitorChanged(_address);
    }

    /// @notice Set the amount of ETH that can be staked before staking monitor
    // needs to a approve further staking by resetting the stake ETH tally
    function setStakeETHThreshold(uint256 _amount) external onlyGovernor {
        stakeETHThreshold = _amount;
        emit StakeETHThresholdChanged(_amount);
    }

    /// @notice Reset the stakeETHTally
    function resetStakeETHTally() external onlyStakingMonitor {
        stakeETHTally = 0;
        emit StakeETHTallyReset();
    }

    /// @notice Stakes WETH to the node validators
    /// @param validators A list of validator data needed to stake.
    /// The `ValidatorStakeData` struct contains the pubkey, signature and depositDataRoot.
    /// Only the registrator can call this function.
    // slither-disable-start reentrancy-eth
    function stakeEth(ValidatorStakeData[] calldata validators)
        external
        onlyRegistrator
        whenNotPaused
        nonReentrant
    {
        uint256 requiredETH = validators.length * FULL_STAKE;

        // Check there is enough WETH from the deposits sitting in this strategy contract
        require(
            requiredETH <= IWETH9(WETH).balanceOf(address(this)),
            "Insufficient WETH"
        );
        require(
            activeDepositedValidators + validators.length <= MAX_VALIDATORS,
            "Max validators reached"
        );

        require(
            stakeETHTally + requiredETH <= stakeETHThreshold,
            "Staking ETH over threshold"
        );
        stakeETHTally += requiredETH;

        // Convert required ETH from WETH
        IWETH9(WETH).withdraw(requiredETH);
        _wethWithdrawn(requiredETH);

        /* 0x01 to indicate that withdrawal credentials will contain an EOA address that the sweeping function
         * can sweep funds to.
         * bytes11(0) to fill up the required zeros
         * remaining bytes20 are for the address
         */
        bytes memory withdrawalCredentials = abi.encodePacked(
            bytes1(0x01),
            bytes11(0),
            address(this)
        );

        // For each validator
        for (uint256 i = 0; i < validators.length; ++i) {
            bytes32 pubKeyHash = keccak256(validators[i].pubkey);

            require(
                validatorsStates[pubKeyHash] == VALIDATOR_STATE.REGISTERED,
                "Validator not registered"
            );

            IDepositContract(BEACON_CHAIN_DEPOSIT_CONTRACT).deposit{
                value: FULL_STAKE
            }(
                validators[i].pubkey,
                withdrawalCredentials,
                validators[i].signature,
                validators[i].depositDataRoot
            );

            validatorsStates[pubKeyHash] = VALIDATOR_STATE.STAKED;

            emit ETHStaked(pubKeyHash, validators[i].pubkey, FULL_STAKE);
        }
        // save gas by changing this storage variable only once rather each time in the loop.
        activeDepositedValidators += validators.length;
    }

    // slither-disable-end reentrancy-eth

    /// @notice Registers a new validator in the SSV Cluster.
    /// Only the registrator can call this function.
    /// @param publicKeys The public keys of the validators
    /// @param operatorIds The operator IDs of the SSV Cluster
    /// @param sharesData The shares data for each validator
    /// @param ssvAmount The amount of SSV tokens to be deposited to the SSV cluster
    /// @param cluster The SSV cluster details including the validator count and SSV balance
    function registerSsvValidators(
        bytes[] calldata publicKeys,
        uint64[] calldata operatorIds,
        bytes[] calldata sharesData,
        uint256 ssvAmount,
        Cluster calldata cluster
    ) external onlyRegistrator whenNotPaused {
        require(
            publicKeys.length == sharesData.length,
            "Pubkey sharesData mismatch"
        );
        // Check each public key has not already been used
        bytes32 pubKeyHash;
        VALIDATOR_STATE currentState;
        for (uint256 i = 0; i < publicKeys.length; ++i) {
            pubKeyHash = keccak256(publicKeys[i]);
            currentState = validatorsStates[pubKeyHash];
            require(
                currentState == VALIDATOR_STATE.NON_REGISTERED,
                "Validator already registered"
            );

            validatorsStates[pubKeyHash] = VALIDATOR_STATE.REGISTERED;

            emit SSVValidatorRegistered(pubKeyHash, publicKeys[i], operatorIds);
        }

        ISSVNetwork(SSV_NETWORK).bulkRegisterValidator(
            publicKeys,
            operatorIds,
            sharesData,
            ssvAmount,
            cluster
        );
    }

    /// @notice Exit validators from the Beacon chain.
    /// The staked ETH will eventually swept to this native staking strategy.
    /// Only the registrator can call this function.
    /// @param publicKeys List of SSV validator public keys
    /// @param operatorIds The operator IDs of the SSV Cluster
    function exitSsvValidators(
        bytes[] calldata publicKeys,
        uint64[] calldata operatorIds
    ) external onlyRegistrator whenNotPaused nonReentrant {
        ISSVNetwork(SSV_NETWORK).bulkExitValidator(publicKeys, operatorIds);

        bytes32 pubKeyHash;
        VALIDATOR_STATE currentState;
        for (uint256 i = 0; i < publicKeys.length; ++i) {
            pubKeyHash = keccak256(publicKeys[i]);
            currentState = validatorsStates[pubKeyHash];

            // Check each validator has not already been staked.
            // This would normally be done before the external call but is after
            // so only one for loop of the validators is needed.
            require(
                currentState == VALIDATOR_STATE.STAKED,
                "Validator not staked"
            );

            // Store the new validator state
            validatorsStates[pubKeyHash] = VALIDATOR_STATE.EXITING;

            emit SSVValidatorExitInitiated(
                pubKeyHash,
                publicKeys[i],
                operatorIds
            );
        }
    }

    /// @notice Remove validators from the SSV Cluster.
    /// Make sure `exitSsvValidator` is called before and the validate has exited the Beacon chain.
    /// If removed before the validator has exited the beacon chain will result in the validator being slashed.
    /// Only the registrator can call this function.
    /// @param publicKeys List of SSV validator public keys
    /// @param operatorIds The operator IDs of the SSV Cluster
    /// @param cluster The SSV cluster details including the validator count and SSV balance
    function removeSsvValidators(
        bytes[] calldata publicKeys,
        uint64[] calldata operatorIds,
        Cluster calldata cluster
    ) external onlyRegistrator whenNotPaused nonReentrant {
        ISSVNetwork(SSV_NETWORK).bulkRemoveValidator(
            publicKeys,
            operatorIds,
            cluster
        );

        bytes32 pubKeyHash;
        VALIDATOR_STATE currentState;
        for (uint256 i = 0; i < publicKeys.length; ++i) {
            pubKeyHash = keccak256(publicKeys[i]);
            currentState = validatorsStates[pubKeyHash];

            // Check each validator is either registered or exited.
            // This would normally be done before the external call but is after
            // so only one for loop of the validators is needed.
            require(
                currentState == VALIDATOR_STATE.EXITING ||
                    currentState == VALIDATOR_STATE.REGISTERED,
                "Validator not regd or exiting"
            );

            // Store the new validator state
            validatorsStates[pubKeyHash] = VALIDATOR_STATE.EXIT_COMPLETE;

            emit SSVValidatorExitCompleted(
                pubKeyHash,
                publicKeys[i],
                operatorIds
            );
        }
    }

    /// @notice Deposits more SSV Tokens to the SSV Network contract which is used to pay the SSV Operators.
    /// @dev A SSV cluster is defined by the SSVOwnerAddress and the set of operatorIds.
    /// uses "onlyStrategist" modifier so continuous front-running can't DOS our maintenance service
    /// that tries to top up SSV tokens.
    /// @param operatorIds The operator IDs of the SSV Cluster
    /// @param ssvAmount The amount of SSV tokens to be deposited to the SSV cluster
    /// @param cluster The SSV cluster details including the validator count and SSV balance
    function depositSSV(
        uint64[] memory operatorIds,
        uint256 ssvAmount,
        Cluster memory cluster
    ) external onlyStrategist {
        ISSVNetwork(SSV_NETWORK).deposit(
            address(this),
            operatorIds,
            ssvAmount,
            cluster
        );
    }

    /// @notice Withdraws excess SSV Tokens from the SSV Network contract which was used to pay the SSV Operators.
    /// @dev A SSV cluster is defined by the SSVOwnerAddress and the set of operatorIds.
    /// @param operatorIds The operator IDs of the SSV Cluster
    /// @param ssvAmount The amount of SSV tokens to be deposited to the SSV cluster
    /// @param cluster The SSV cluster details including the validator count and SSV balance
    function withdrawSSV(
        uint64[] memory operatorIds,
        uint256 ssvAmount,
        Cluster memory cluster
    ) external onlyGovernor {
        ISSVNetwork(SSV_NETWORK).withdraw(operatorIds, ssvAmount, cluster);
    }

    /***************************************
                 Abstract
    ****************************************/

    /// @dev Called when WETH is withdrawn from the strategy or staked to a validator so
    /// the strategy knows how much WETH it has on deposit.
    /// This is so it can emit the correct amount in the Deposit event in depositAll().
    function _wethWithdrawn(uint256 _amount) internal virtual;
}

contracts/strategies/plume/RoosterAMOStr.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

/**
 * @title Rooster AMO strategy
 * @author Origin Protocol Inc
 * @custom:security-contact security@originprotocol.com
 */
import { Math as MathRooster } from "../../../lib/rooster/v2-common/libraries/Math.sol";
import { Math as Math_v5 } from "../../../lib/rooster/openzeppelin-custom/contracts/utils/math/Math.sol";
import { StableMath } from "../../utils/StableMath.sol";

import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { SafeCast } from "@openzeppelin/contracts/utils/math/SafeCast.sol";

import { IERC20, InitializableAbstractStrategy } from "../../utils/InitializableAbstractStrategy.sol";
import { IVault } from "../../interfaces/IVault.sol";
import { IMaverickV2Pool } from "../../interfaces/plume/IMaverickV2Pool.sol";
import { IMaverickV2Quoter } from "../../interfaces/plume/IMaverickV2Quoter.sol";
import { IMaverickV2LiquidityManager } from "../../interfaces/plume/IMaverickV2LiquidityManager.sol";
import { IMaverickV2PoolLens } from "../../interfaces/plume/IMaverickV2PoolLens.sol";
import { IMaverickV2Position } from "../../interfaces/plume/IMaverickV2Position.sol";
import { IVotingDistributor } from "../../interfaces/plume/IVotingDistributor.sol";
import { IPoolDistributor } from "../../interfaces/plume/IPoolDistributor.sol";
// importing custom version of rooster TickMath because of dependency collision. Maverick uses
// a newer OpenZepplin Math library with functionality that is not present in 4.4.2 (the one we use)
import { TickMath } from "../../../lib/rooster/v2-common/libraries/TickMath.sol";

contract RoosterAMOStrategy is InitializableAbstractStrategy {
    using StableMath for uint256;
    using SafeERC20 for IERC20;
    using SafeCast for uint256;

    /***************************************
            Storage slot members
    ****************************************/

    /// @notice NFT tokenId of the liquidity position
    ///
    /// @dev starts with value of 1 and can not be 0
    //  solhint-disable-next-line max-line-length
    ///      https://github.com/rooster-protocol/rooster-contracts/blob/fbfecbc519e4495b12598024a42630b4a8ea4489/v2-common/contracts/base/Nft.sol#L14
    uint256 public tokenId;
    /// @dev Minimum amount of tokens the strategy would be able to withdraw from the pool.
    ///      minimum amount of tokens are withdrawn at a 1:1 price
    ///      Important: Underlying assets contains only assets that are deposited in the underlying Rooster pool.
    ///      WETH or OETH held by this contract is not accounted for in underlying assets
    uint256 public underlyingAssets;
    /// @notice Marks the start of the interval that defines the allowed range of WETH share in
    /// the pre-configured pool's liquidity ticker
    uint256 public allowedWethShareStart;
    /// @notice Marks the end of the interval that defines the allowed range of WETH share in
    /// the pre-configured pool's liquidity ticker
    uint256 public allowedWethShareEnd;
    /// @dev reserved for inheritance
    int256[46] private __reserved;

    /***************************************
          Constants, structs and events
    ****************************************/

    /// @notice The address of the Wrapped ETH (WETH) token contract
    address public immutable WETH;
    /// @notice The address of the OETH token contract
    address public immutable OETH;
    /// @notice the underlying AMO Maverick (Rooster) pool
    IMaverickV2Pool public immutable mPool;
    /// @notice the Liquidity manager used to add liquidity to the pool
    IMaverickV2LiquidityManager public immutable liquidityManager;
    /// @notice the Maverick V2 poolLens
    ///
    /// @dev only used to provide the pool's current sqrtPrice
    IMaverickV2PoolLens public immutable poolLens;
    /// @notice the Maverick V2 position
    ///
    /// @dev provides details of the NFT LP position and offers functions to
    /// remove the liquidity.
    IMaverickV2Position public immutable maverickPosition;
    /// @notice the Maverick Quoter
    IMaverickV2Quoter public immutable quoter;
    /// @notice the Maverick Voting Distributor
    IVotingDistributor public immutable votingDistributor;
    /// @notice the Maverick Pool Distributor
    IPoolDistributor public immutable poolDistributor;

    /// @notice sqrtPriceTickLower
    /// @dev tick lower represents the lower price of OETH priced in WETH. Meaning the pool
    /// offers more than 1 OETH for 1 WETH. In other terms to get 1 OETH the swap needs to offer 0.9999 WETH
    /// this is where purchasing OETH with WETH within the liquidity position is the cheapest.
    ///
    ///            _____________________
    ///            |      |            |
    ///            | WETH |    OETH   |
    ///            |      |            |
    ///            |      |            |
    ///  --------- * ---- * ---------- * ---------
    ///               currentPrice
    ///                          sqrtPriceHigher-(1:1 parity)
    ///      sqrtPriceLower
    ///
    ///
    /// Price is defined as price of token1 in terms of token0. (token1 / token0)
    /// @notice sqrtPriceTickLower - OETH is priced 0.9999 WETH
    uint256 public immutable sqrtPriceTickLower;
    /// @notice sqrtPriceTickHigher
    /// @dev tick higher represents 1:1 price parity of WETH to OETH
    uint256 public immutable sqrtPriceTickHigher;
    /// @dev price at parity (in OETH this is equal to sqrtPriceTickHigher)
    uint256 public immutable sqrtPriceAtParity;
    /// @notice The tick where the strategy deploys the liquidity to
    int32 public constant TICK_NUMBER = -1;
    /// @notice Minimum liquidity that must be exceeded to continue with the action
    /// e.g. deposit, add liquidity
    uint256 public constant ACTION_THRESHOLD = 1e12;
    /// @notice Maverick pool static liquidity bin type
    uint8 public constant MAV_STATIC_BIN_KIND = 0;
    /// @dev a threshold under which the contract no longer allows for the protocol to rebalance. Guarding
    ///      against a strategist / guardian being taken over and with multiple transactions draining the
    ///      protocol funds.
    uint256 public constant SOLVENCY_THRESHOLD = 0.998 ether;
    /// @notice Emitted when the allowed interval within which the strategy contract is allowed to deposit
    /// liquidity to the underlying pool is updated.
    /// @param allowedWethShareStart The start of the interval
    /// @param allowedWethShareEnd The end of the interval
    event PoolWethShareIntervalUpdated(
        uint256 allowedWethShareStart,
        uint256 allowedWethShareEnd
    );
    /// @notice Emitted when liquidity is removed from the underlying pool
    /// @param withdrawLiquidityShare Share of strategy's liquidity that has been removed
    /// @param removedWETHAmount The amount of WETH removed
    /// @param removedOETHAmount The amount of OETH removed
    /// @param underlyingAssets Updated amount of strategy's underlying assets
    event LiquidityRemoved(
        uint256 withdrawLiquidityShare,
        uint256 removedWETHAmount,
        uint256 removedOETHAmount,
        uint256 underlyingAssets
    );

    /// @notice Emitted when the underlying pool is rebalanced
    /// @param currentPoolWethShare The resulting share of strategy's liquidity
    /// in the TICK_NUMBER
    event PoolRebalanced(uint256 currentPoolWethShare);

    /// @notice Emitted when the amount of underlying assets the strategy hold as
    /// liquidity in the pool is updated.
    /// @param underlyingAssets Updated amount of strategy's underlying assets
    event UnderlyingAssetsUpdated(uint256 underlyingAssets);

    /// @notice Emitted when liquidity is added to the underlying pool
    /// @param wethAmountDesired Amount of WETH desired to be deposited
    /// @param oethAmountDesired Amount of OETH desired to be deposited
    /// @param wethAmountSupplied Amount of WETH deposited
    /// @param oethAmountSupplied Amount of OETH deposited
    /// @param tokenId NFT liquidity token id
    /// @param underlyingAssets Updated amount of underlying assets
    event LiquidityAdded(
        uint256 wethAmountDesired,
        uint256 oethAmountDesired,
        uint256 wethAmountSupplied,
        uint256 oethAmountSupplied,
        uint256 tokenId,
        uint256 underlyingAssets
    ); // 0x1530ec74

    error PoolRebalanceOutOfBounds(
        uint256 currentPoolWethShare,
        uint256 allowedWethShareStart,
        uint256 allowedWethShareEnd
    ); // 0x3681e8e0

    error NotEnoughWethForSwap(uint256 wethBalance, uint256 requiredWeth); // 0x989e5ca8
    error NotEnoughWethLiquidity(uint256 wethBalance, uint256 requiredWeth); // 0xa6737d87
    error OutsideExpectedTickRange(); // 0xa6e1bad2
    error SlippageCheck(uint256 tokenReceived); // 0x355cdb78

    /// @notice the constructor
    /// @dev This contract is intended to be used as a proxy. To prevent the
    ///      potential confusion of having a functional implementation contract
    ///      the constructor has the `initializer` modifier. This way the
    ///      `initialize` function can not be called on the implementation contract.
    ///      For the same reason the implementation contract also has the governor
    ///      set to a zero address.
    /// @param _stratConfig the basic strategy configuration
    /// @param _wethAddress Address of the Erc20 WETH Token contract
    /// @param _oethAddress Address of the Erc20 OETH Token contract
    /// @param _liquidityManager Address of liquidity manager to add
    ///         the liquidity
    /// @param _poolLens Address of the pool lens contract
    /// @param _maverickPosition Address of the Maverick's position contract
    /// @param _maverickQuoter Address of the Maverick's Quoter contract
    /// @param _mPool Address of the Rooster concentrated liquidity pool
    /// @param _upperTickAtParity Bool when true upperTick is the one where the
    ///        price of OETH and WETH are at parity
    constructor(
        BaseStrategyConfig memory _stratConfig,
        address _wethAddress,
        address _oethAddress,
        address _liquidityManager,
        address _poolLens,
        address _maverickPosition,
        address _maverickQuoter,
        address _mPool,
        bool _upperTickAtParity,
        address _votingDistributor,
        address _poolDistributor
    ) initializer InitializableAbstractStrategy(_stratConfig) {
        require(
            address(IMaverickV2Pool(_mPool).tokenA()) == _wethAddress,
            "WETH not TokenA"
        );
        require(
            address(IMaverickV2Pool(_mPool).tokenB()) == _oethAddress,
            "OETH not TokenB"
        );
        require(
            _liquidityManager != address(0),
            "LiquidityManager zero address not allowed"
        );
        require(
            _maverickQuoter != address(0),
            "Quoter zero address not allowed"
        );
        require(_poolLens != address(0), "PoolLens zero address not allowed");
        require(
            _maverickPosition != address(0),
            "Position zero address not allowed"
        );
        require(
            _votingDistributor != address(0),
            "Voting distributor zero address not allowed"
        );
        require(
            _poolDistributor != address(0),
            "Pool distributor zero address not allowed"
        );

        uint256 _tickSpacing = IMaverickV2Pool(_mPool).tickSpacing();
        require(_tickSpacing == 1, "Unsupported tickSpacing");

        // tickSpacing == 1
        (sqrtPriceTickLower, sqrtPriceTickHigher) = TickMath.tickSqrtPrices(
            _tickSpacing,
            TICK_NUMBER
        );
        sqrtPriceAtParity = _upperTickAtParity
            ? sqrtPriceTickHigher
            : sqrtPriceTickLower;

        WETH = _wethAddress;
        OETH = _oethAddress;
        liquidityManager = IMaverickV2LiquidityManager(_liquidityManager);
        poolLens = IMaverickV2PoolLens(_poolLens);
        maverickPosition = IMaverickV2Position(_maverickPosition);
        quoter = IMaverickV2Quoter(_maverickQuoter);
        mPool = IMaverickV2Pool(_mPool);
        votingDistributor = IVotingDistributor(_votingDistributor);
        poolDistributor = IPoolDistributor(_poolDistributor);

        // prevent implementation contract to be governed
        _setGovernor(address(0));
    }

    /**
     * @notice initialize function, to set up initial internal state
     */
    function initialize() external onlyGovernor initializer {
        // Read reward
        address[] memory _rewardTokens = new address[](1);
        _rewardTokens[0] = poolDistributor.rewardToken();

        require(_rewardTokens[0] != address(0), "No reward token configured");

        InitializableAbstractStrategy._initialize(
            _rewardTokens,
            new address[](0),
            new address[](0)
        );
    }

    /***************************************
                  Configuration 
    ****************************************/

    /**
     * @notice Set allowed pool weth share interval. After the rebalance happens
     * the share of WETH token in the ticker needs to be within the specifications
     * of the interval.
     *
     * @param _allowedWethShareStart Start of WETH share interval expressed as 18 decimal amount
     * @param _allowedWethShareEnd End of WETH share interval expressed as 18 decimal amount
     */
    function setAllowedPoolWethShareInterval(
        uint256 _allowedWethShareStart,
        uint256 _allowedWethShareEnd
    ) external onlyGovernor {
        require(
            _allowedWethShareStart < _allowedWethShareEnd,
            "Invalid interval"
        );
        // can not go below 1% weth share
        require(_allowedWethShareStart > 0.01 ether, "Invalid interval start");
        // can not go above 95% weth share
        require(_allowedWethShareEnd < 0.95 ether, "Invalid interval end");

        allowedWethShareStart = _allowedWethShareStart;
        allowedWethShareEnd = _allowedWethShareEnd;
        emit PoolWethShareIntervalUpdated(
            _allowedWethShareStart,
            _allowedWethShareEnd
        );
    }

    /***************************************
               Strategy overrides 
    ****************************************/

    /**
     * @notice Deposits funds to the strategy which deposits them to the
     * underlying Rooster pool if the pool price is within the expected interval.
     * @param _asset   Address for the asset
     * @param _amount  Units of asset to deposit
     */
    function deposit(address _asset, uint256 _amount)
        external
        override
        onlyVault
        nonReentrant
    {
        _deposit(_asset, _amount);
    }

    /**
     * @notice Deposits all the funds to the strategy which deposits them to the
     * underlying Rooster pool if the pool price is within the expected interval.
     */
    function depositAll() external override onlyVault nonReentrant {
        uint256 _wethBalance = IERC20(WETH).balanceOf(address(this));
        _deposit(WETH, _wethBalance);
    }

    /**
     * @dev Deposits funds to the strategy which deposits them to the
     * underlying Rooster pool if the pool price is within the expected interval.
     * Before this function can be called the initial pool position needs to already
     * be minted.
     * @param _asset Address of the asset to deposit
     * @param _amount Amount of assets to deposit
     */
    function _deposit(address _asset, uint256 _amount) internal {
        require(_asset == WETH, "Unsupported asset");
        require(_amount > 0, "Must deposit something");
        require(tokenId > 0, "Initial position not minted");
        emit Deposit(_asset, address(0), _amount);

        // if the pool price is not within the expected interval leave the WETH on the contract
        // as to not break the mints - in case it would be configured as a default asset strategy
        (bool _isExpectedRange, ) = _checkForExpectedPoolPrice(false);
        if (_isExpectedRange) {
            // deposit funds into the underlying pool. Because no swap is performed there is no
            // need to remove any of the liquidity beforehand.
            _rebalance(0, false, 0, 0);
        }
    }

    /**
     * @notice Withdraw an `amount` of WETH from the platform and
     *         send to the `_recipient`.
     * @param _recipient  Address to which the asset should be sent
     * @param _asset      WETH address
     * @param _amount     Amount of WETH to withdraw
     */
    function withdraw(
        address _recipient,
        address _asset,
        uint256 _amount
    ) external override onlyVault nonReentrant {
        require(_asset == WETH, "Unsupported asset");
        require(_amount > 0, "Must withdraw something");
        require(_recipient == vaultAddress, "Only withdraw to vault allowed");

        _ensureWETHBalance(_amount);

        _withdraw(_recipient, _amount);
    }

    /**
     * @notice Withdraw WETH and sends it to the Vault.
     */
    function withdrawAll() external override onlyVault nonReentrant {
        if (tokenId != 0) {
            _removeLiquidity(1e18);
        }

        uint256 _balance = IERC20(WETH).balanceOf(address(this));
        if (_balance > 0) {
            _withdraw(vaultAddress, _balance);
        }
    }

    function _withdraw(address _recipient, uint256 _amount) internal {
        IERC20(WETH).safeTransfer(_recipient, _amount);
        emit Withdrawal(WETH, address(0), _amount);
    }

    /**
     * @dev Retuns bool indicating whether asset is supported by strategy
     * @param _asset   Address of the asset
     * @return bool    True when the _asset is WETH
     */
    function supportsAsset(address _asset) public view override returns (bool) {
        return _asset == WETH;
    }

    /**
     * @dev Approve the spending amounts for the assets
     */
    function _approveTokenAmounts(
        uint256 _wethAllowance,
        uint256 _oethAllowance
    ) internal {
        IERC20(WETH).approve(address(liquidityManager), _wethAllowance);
        IERC20(OETH).approve(address(liquidityManager), _oethAllowance);
    }

    /***************************************
              Liquidity management
    ****************************************/
    /**
     * @dev Add liquidity into the pool in the pre-configured WETH to OETH share ratios
     * defined by the allowedPoolWethShareStart|End interval.
     *
     * Normally a PoolLens contract is used to prepare the parameters to add liquidity to the
     * Rooster pools. It has some errors when doing those calculation and for that reason a
     * much more accurate Quoter contract is used. This is possible due to our requirement of
     * adding liquidity only to one tick - PoolLens supports adding liquidity into multiple ticks
     * using different distribution ratios.
     */
    function _addLiquidity() internal {
        uint256 _wethBalance = IERC20(WETH).balanceOf(address(this));
        uint256 _oethBalance = IERC20(OETH).balanceOf(address(this));
        // don't deposit small liquidity amounts
        if (_wethBalance <= ACTION_THRESHOLD) {
            return;
        }

        (
            bytes memory packedSqrtPriceBreaks,
            bytes[] memory packedArgs,
            uint256 WETHRequired,
            uint256 OETHRequired
        ) = _getAddLiquidityParams(_wethBalance, 1e30);

        if (OETHRequired > _oethBalance) {
            IVault(vaultAddress).mintForStrategy(OETHRequired - _oethBalance);
        }

        _approveTokenAmounts(WETHRequired, OETHRequired);

        (
            uint256 _wethAmount,
            uint256 _oethAmount,
            uint32[] memory binIds
        ) = liquidityManager.addPositionLiquidityToSenderByTokenIndex(
                mPool,
                0, // NFT token index
                packedSqrtPriceBreaks,
                packedArgs
            );

        require(binIds.length == 1, "Unexpected binIds length");

        // burn remaining OETH
        _burnOethOnTheContract();
        _updateUnderlyingAssets();

        // needs to be called after _updateUnderlyingAssets so the updated amount
        // is reflected in the event
        emit LiquidityAdded(
            _wethBalance, // wethAmountDesired
            OETHRequired, // oethAmountDesired
            _wethAmount, // wethAmountSupplied
            _oethAmount, // oethAmountSupplied
            tokenId, // tokenId
            underlyingAssets
        );
    }

    /**
     * @dev The function creates liquidity parameters required to be able to add liquidity to the pool.
     * The function needs to handle the 3 different cases of the way liquidity is added:
     *  - only WETH present in the tick
     *  - only OETH present in the tick
     *  - both tokens present in the tick
     *
     */
    function _getAddLiquidityParams(uint256 _maxWETH, uint256 _maxOETH)
        internal
        returns (
            bytes memory packedSqrtPriceBreaks,
            bytes[] memory packedArgs,
            uint256 WETHRequired,
            uint256 OETHRequired
        )
    {
        IMaverickV2Pool.AddLiquidityParams[]
            memory addParams = new IMaverickV2Pool.AddLiquidityParams[](1);
        int32[] memory ticks = new int32[](1);
        uint128[] memory amounts = new uint128[](1);
        ticks[0] = TICK_NUMBER;
        // arbitrary LP amount
        amounts[0] = 1e24;

        // construct value for Quoter with arbitrary LP amount
        IMaverickV2Pool.AddLiquidityParams memory addParam = IMaverickV2Pool
            .AddLiquidityParams({
                kind: MAV_STATIC_BIN_KIND,
                ticks: ticks,
                amounts: amounts
            });

        // get the WETH and OETH required to get the proportion of tokens required
        // given the arbitrary liquidity
        (WETHRequired, OETHRequired, ) = quoter.calculateAddLiquidity(
            mPool,
            addParam
        );

        /**
         * If either token required is 0 then the tick consists only of the other token. In that
         * case the liquidity calculations need to be done using the non 0 token. By setting the
         * tokenRequired from 0 to 1 the `min` in next step will ignore that (the bigger) value.
         */
        WETHRequired = WETHRequired == 0 ? 1 : WETHRequired;
        OETHRequired = OETHRequired == 0 ? 1 : OETHRequired;

        addParam.amounts[0] = Math_v5
            .min(
                ((_maxWETH - 1) * 1e24) / WETHRequired,
                ((_maxOETH - 1) * 1e24) / OETHRequired
            )
            .toUint128();

        // update the quotes with the actual amounts
        (WETHRequired, OETHRequired, ) = quoter.calculateAddLiquidity(
            mPool,
            addParam
        );

        require(_maxWETH >= WETHRequired, "More WETH required than specified");
        require(_maxOETH >= OETHRequired, "More OETH required than specified");

        // organize values to be used by manager
        addParams[0] = addParam;
        packedArgs = liquidityManager.packAddLiquidityArgsArray(addParams);
        // price can stay 0 if array only has one element
        packedSqrtPriceBreaks = liquidityManager.packUint88Array(
            new uint88[](1)
        );
    }

    /**
     * @dev Check that the Rooster pool price is within the expected
     *      parameters.
     *      This function works whether the strategy contract has liquidity
     *      position in the pool or not. The function returns _wethSharePct
     *      as a gas optimization measure.
     * @param _throwException  when set to true the function throws an exception
     *                         when pool's price is not within expected range.
     * @return _isExpectedRange  Bool expressing price is within expected range
     * @return _wethSharePct  Share of WETH owned by this strategy contract in the
     *                        configured ticker.
     */
    function _checkForExpectedPoolPrice(bool _throwException)
        internal
        view
        returns (bool _isExpectedRange, uint256 _wethSharePct)
    {
        require(
            allowedWethShareStart != 0 && allowedWethShareEnd != 0,
            "Weth share interval not set"
        );

        uint256 _currentPrice = getPoolSqrtPrice();

        /**
         * First check pool price is in expected tick range
         *
         * A revert is issued even though price being equal to the lower bound as that can not
         * be within the approved tick range.
         */
        if (
            _currentPrice <= sqrtPriceTickLower ||
            _currentPrice >= sqrtPriceTickHigher
        ) {
            if (_throwException) {
                revert OutsideExpectedTickRange();
            }

            return (false, _currentPrice <= sqrtPriceTickLower ? 0 : 1e18);
        }

        // 18 decimal number expressed WETH tick share
        _wethSharePct = _getWethShare(_currentPrice);

        if (
            _wethSharePct < allowedWethShareStart ||
            _wethSharePct > allowedWethShareEnd
        ) {
            if (_throwException) {
                revert PoolRebalanceOutOfBounds(
                    _wethSharePct,
                    allowedWethShareStart,
                    allowedWethShareEnd
                );
            }
            return (false, _wethSharePct);
        }

        return (true, _wethSharePct);
    }

    /**
     * @notice Rebalance the pool to the desired token split and Deposit any WETH on the contract to the
     * underlying rooster pool. Print the required amount of corresponding OETH. After the rebalancing is
     * done burn any potentially remaining OETH tokens still on the strategy contract.
     *
     * This function has a slightly different behaviour depending on the status of the underlying Rooster
     * pool. The function consists of the following 3 steps:
     * 1. withdrawLiquidityOption -> this is a configurable option where either only part of the liquidity
     *                               necessary for the swap is removed, or all of it. This way the rebalance
     *                               is able to optimize for volume, for efficiency or anything in between
     * 2. swapToDesiredPosition   -> move active trading price in the pool to be able to deposit WETH & OETH
     *                               tokens with the desired pre-configured ratios
     * 3. addLiquidity            -> add liquidity into the pool respecting ratio split configuration
     *
     *
     * Exact _amountToSwap, _swapWeth & _minTokenReceived parameters shall be determined by simulating the
     * transaction off-chain. The strategy checks that after the swap the share of the tokens is in the
     * expected ranges.
     *
     * @param _amountToSwap The amount of the token to swap
     * @param _swapWeth Swap using WETH when true, use OETH when false
     * @param _minTokenReceived Slippage check -> minimum amount of token expected in return
     * @param _liquidityToRemovePct Percentage of liquidity to remove -> the percentage amount of liquidity to
     *        remove before performing the swap. 1e18 denominated
     */
    function rebalance(
        uint256 _amountToSwap,
        bool _swapWeth,
        uint256 _minTokenReceived,
        uint256 _liquidityToRemovePct
    ) external nonReentrant onlyGovernorOrStrategist {
        _rebalance(
            _amountToSwap,
            _swapWeth,
            _minTokenReceived,
            _liquidityToRemovePct
        );
    }

    // slither-disable-start reentrancy-no-eth
    function _rebalance(
        uint256 _amountToSwap,
        bool _swapWeth,
        uint256 _minTokenReceived,
        uint256 _liquidityToRemovePct
    ) internal {
        // Remove the required amount of liquidity
        if (_liquidityToRemovePct > 0) {
            _removeLiquidity(_liquidityToRemovePct);
        }

        // in some cases (e.g. deposits) we will just want to add liquidity and not
        // issue a swap to move the active trading position within the pool. Before or after a
        // deposit or as a standalone call the strategist might issue a rebalance to move the
        // active trading price to a more desired position.
        if (_amountToSwap > 0) {
            // In case liquidity has been removed and there is still not enough WETH owned by the
            // strategy contract remove additional required amount of WETH.
            if (_swapWeth) _ensureWETHBalance(_amountToSwap);

            _swapToDesiredPosition(_amountToSwap, _swapWeth, _minTokenReceived);
        }

        // calling check liquidity early so we don't get unexpected errors when adding liquidity
        // in the later stages of this function
        _checkForExpectedPoolPrice(true);

        _addLiquidity();

        // this call shouldn't be necessary, since adding liquidity shouldn't affect the active
        // trading price. It is a defensive programming measure.
        (, uint256 _wethSharePct) = _checkForExpectedPoolPrice(true);

        // revert if protocol insolvent
        _solvencyAssert();

        emit PoolRebalanced(_wethSharePct);
    }

    // slither-disable-end reentrancy-no-eth

    /**
     * @dev Perform a swap so that after the swap the tick has the desired WETH to OETH token share.
     */
    function _swapToDesiredPosition(
        uint256 _amountToSwap,
        bool _swapWeth,
        uint256 _minTokenReceived
    ) internal {
        IERC20 _tokenToSwap = IERC20(_swapWeth ? WETH : OETH);
        uint256 _balance = _tokenToSwap.balanceOf(address(this));

        if (_balance < _amountToSwap) {
            // This should never trigger since _ensureWETHBalance will already
            // throw an error if there is not enough WETH
            if (_swapWeth) {
                revert NotEnoughWethForSwap(_balance, _amountToSwap);
            }
            // if swapping OETH
            uint256 mintForSwap = _amountToSwap - _balance;
            IVault(vaultAddress).mintForStrategy(mintForSwap);
        }

        // SafeERC20 is used for IERC20 transfers. Not sure why slither complains
        // slither-disable-next-line unchecked-transfer
        _tokenToSwap.transfer(address(mPool), _amountToSwap);

        // tickLimit: the furthest tick a swap will execute in. If no limit is desired,
        // value should be set to type(int32).max for a tokenAIn (WETH) swap
        // and type(int32).min for a swap where tokenB (OETH) is the input

        IMaverickV2Pool.SwapParams memory swapParams = IMaverickV2Pool
        // exactOutput defines whether the amount specified is the output
        // or the input amount of the swap
            .SwapParams({
                amount: _amountToSwap,
                tokenAIn: _swapWeth,
                exactOutput: false,
                tickLimit: TICK_NUMBER
            });

        // swaps without a callback as the assets are already sent to the pool
        (, uint256 amountOut) = mPool.swap(
            address(this),
            swapParams,
            bytes("")
        );

        /**
         * There could be additional checks here for validating minTokenReceived is within the
         * expected range (e.g. 99% - 101% of the token sent in). Though that doesn't provide
         * any additional security. After the swap the `_checkForExpectedPoolPrice` validates
         * that the swap has moved the price into the expected tick (# -1).
         *
         * If the guardian forgets to set a `_minTokenReceived` and a sandwich attack bends
         * the pool before the swap the `_checkForExpectedPoolPrice` will fail the transaction.
         *
         * A check would not prevent a compromised guardian from stealing funds as multiple
         * transactions each loosing smaller amount of funds are still possible.
         */
        if (amountOut < _minTokenReceived) {
            revert SlippageCheck(amountOut);
        }

        /**
         * In the interest of each function in `_rebalance` to leave the contract state as
         * clean as possible the OETH tokens here are burned. This decreases the
         * dependence where `_swapToDesiredPosition` function relies on later functions
         * (`addLiquidity`) to burn the OETH. Reducing the risk of error introduction.
         */
        _burnOethOnTheContract();
    }

    /**
     * @dev This function removes the appropriate amount of liquidity to ensure that the required
     * amount of WETH is available on the contract
     *
     * @param _amount  WETH balance required on the contract
     */
    function _ensureWETHBalance(uint256 _amount) internal {
        uint256 _wethBalance = IERC20(WETH).balanceOf(address(this));
        if (_wethBalance >= _amount) {
            return;
        }

        require(tokenId != 0, "No liquidity available");
        uint256 _additionalWethRequired = _amount - _wethBalance;
        (uint256 _wethInThePool, ) = getPositionPrincipal();

        if (_wethInThePool < _additionalWethRequired) {
            revert NotEnoughWethLiquidity(
                _wethInThePool,
                _additionalWethRequired
            );
        }

        uint256 shareOfWethToRemove = _wethInThePool <= 1
            ? 1e18
            : Math_v5.min(
                /**
                 * When dealing with shares of liquidity to remove there is always some
                 * rounding involved. After extensive fuzz testing the below approach
                 * yielded the best results where the strategy overdraws the least and
                 * never removes insufficient amount of WETH.
                 */
                (_additionalWethRequired + 2).divPrecisely(_wethInThePool - 1) +
                    2,
                1e18
            );

        _removeLiquidity(shareOfWethToRemove);
    }

    /**
     * @dev Decrease partial or all liquidity from the pool.
     * @param _liquidityToDecrease The amount of liquidity to remove denominated in 1e18
     */
    function _removeLiquidity(uint256 _liquidityToDecrease) internal {
        require(_liquidityToDecrease > 0, "Must remove some liquidity");
        require(
            _liquidityToDecrease <= 1e18,
            "Can not remove more than 100% of liquidity"
        );

        // 0 indicates the first (and only) bin in the NFT LP position.
        IMaverickV2Pool.RemoveLiquidityParams memory params = maverickPosition
            .getRemoveParams(tokenId, 0, _liquidityToDecrease);
        (uint256 _amountWeth, uint256 _amountOeth) = maverickPosition
            .removeLiquidityToSender(tokenId, mPool, params);

        _burnOethOnTheContract();
        _updateUnderlyingAssets();

        // needs to be called after the _updateUnderlyingAssets so the updated amount is reflected
        // in the event
        emit LiquidityRemoved(
            _liquidityToDecrease,
            _amountWeth,
            _amountOeth,
            underlyingAssets
        );
    }

    /**
     * @dev Burns any OETH tokens remaining on the strategy contract if the balance is
     * above the action threshold.
     */
    function _burnOethOnTheContract() internal {
        uint256 _oethBalance = IERC20(OETH).balanceOf(address(this));
        IVault(vaultAddress).burnForStrategy(_oethBalance);
    }

    /**
     * @notice Returns the percentage of WETH liquidity in the configured ticker
     *         owned by this strategy contract.
     * @return uint256 1e18 denominated percentage expressing the share
     */
    function getWETHShare() external view returns (uint256) {
        uint256 _currentPrice = getPoolSqrtPrice();
        return _getWethShare(_currentPrice);
    }

    /**
     * @dev Returns the share of WETH in tick denominated in 1e18
     */
    function _getWethShare(uint256 _currentPrice)
        internal
        view
        returns (uint256)
    {
        (
            uint256 wethAmount,
            uint256 oethAmount
        ) = _reservesInTickForGivenPriceAndLiquidity(
                sqrtPriceTickLower,
                sqrtPriceTickHigher,
                _currentPrice,
                1e24
            );

        return wethAmount.divPrecisely(wethAmount + oethAmount);
    }

    /**
     * @notice Returns the current pool price in square root
     * @return Square root of the pool price
     */
    function getPoolSqrtPrice() public view returns (uint256) {
        return poolLens.getPoolSqrtPrice(mPool);
    }

    /**
     * @notice Returns the current active trading tick of the underlying pool
     * @return _currentTick Current pool trading tick
     */
    function getCurrentTradingTick() public view returns (int32 _currentTick) {
        _currentTick = mPool.getState().activeTick;
    }

    /**
     * @notice Mint the initial NFT position
     *
     * @dev This amount is "gifted" to the strategy contract and will count as a yield
     *      surplus.
     */
    // slither-disable-start reentrancy-no-eth
    function mintInitialPosition() external onlyGovernor nonReentrant {
        require(tokenId == 0, "Initial position already minted");
        (
            bytes memory packedSqrtPriceBreaks,
            bytes[] memory packedArgs,
            uint256 WETHRequired,
            uint256 OETHRequired
        ) = _getAddLiquidityParams(1e16, 1e16);

        // Mint rounded up OETH amount
        if (OETHRequired > 0) {
            IVault(vaultAddress).mintForStrategy(OETHRequired);
        }

        _approveTokenAmounts(WETHRequired, OETHRequired);

        // Store the tokenId before calling updateUnderlyingAssets as it relies on the tokenId
        // not being 0
        (, , , tokenId) = liquidityManager.mintPositionNftToSender(
            mPool,
            packedSqrtPriceBreaks,
            packedArgs
        );

        // burn remaining OETH
        _burnOethOnTheContract();
        _updateUnderlyingAssets();
    }

    // slither-disable-end reentrancy-no-eth

    /**
     * @notice Returns the balance of tokens the strategy holds in the LP position
     * @return _amountWeth Amount of WETH in position
     * @return _amountOeth Amount of OETH in position
     */
    function getPositionPrincipal()
        public
        view
        returns (uint256 _amountWeth, uint256 _amountOeth)
    {
        if (tokenId == 0) {
            return (0, 0);
        }

        (_amountWeth, _amountOeth, ) = _getPositionInformation();
    }

    /**
     * @dev Returns the balance of tokens the strategy holds in the LP position
     * @return _amountWeth Amount of WETH in position
     * @return _amountOeth Amount of OETH in position
     * @return liquidity Amount of liquidity in the position
     */
    function _getPositionInformation()
        internal
        view
        returns (
            uint256 _amountWeth,
            uint256 _amountOeth,
            uint256 liquidity
        )
    {
        IMaverickV2Position.PositionFullInformation
            memory positionInfo = maverickPosition.tokenIdPositionInformation(
                tokenId,
                0
            );

        require(
            positionInfo.liquidities.length == 1,
            "Unexpected liquidities length"
        );
        require(positionInfo.ticks.length == 1, "Unexpected ticks length");

        _amountWeth = positionInfo.amountA;
        _amountOeth = positionInfo.amountB;
        liquidity = positionInfo.liquidities[0];
    }

    /**
     * Checks that the protocol is solvent, protecting from a rogue Strategist / Guardian that can
     * keep rebalancing the pool in both directions making the protocol lose a tiny amount of
     * funds each time.
     *
     * Protocol must be at least SOLVENCY_THRESHOLD (99.8%) backed in order for the rebalances to
     * function.
     */
    function _solvencyAssert() internal view {
        uint256 _totalVaultValue = IVault(vaultAddress).totalValue();
        uint256 _totalOethSupply = IERC20(OETH).totalSupply();

        if (
            _totalVaultValue.divPrecisely(_totalOethSupply) < SOLVENCY_THRESHOLD
        ) {
            revert("Protocol insolvent");
        }
    }

    /**
     * @dev Collect Rooster reward token, and send it to the harvesterAddress
     */
    function collectRewardTokens()
        external
        override
        onlyHarvester
        nonReentrant
    {
        // Do nothing if there's no position minted
        if (tokenId > 0) {
            uint32[] memory binIds = new uint32[](1);
            IMaverickV2Pool.TickState memory tickState = mPool.getTick(
                TICK_NUMBER
            );
            // get the binId for the MAV_STATIC_BIN_KIND in tick TICK_NUMBER (-1)
            binIds[0] = tickState.binIdsByTick[0];

            uint256 lastEpoch = votingDistributor.lastEpoch();

            poolDistributor.claimLp(
                address(this),
                tokenId,
                mPool,
                binIds,
                lastEpoch
            );
        }

        // Run the internal inherited function
        _collectRewardTokens();
    }

    /***************************************
            Balances and Fees
    ****************************************/

    /**
     * @dev Get the total asset value held in the platform
     * @param _asset      Address of the asset
     * @return balance    Total value of the asset in the platform
     */
    function checkBalance(address _asset)
        external
        view
        override
        returns (uint256)
    {
        require(_asset == WETH, "Only WETH supported");

        // because of PoolLens inaccuracy there is usually some dust WETH left on the contract
        uint256 _wethBalance = IERC20(WETH).balanceOf(address(this));
        // just paranoia check, in case there is OETH in the strategy that for some reason hasn't
        // been burned yet. This should always be 0.
        uint256 _oethBalance = IERC20(OETH).balanceOf(address(this));
        return underlyingAssets + _wethBalance + _oethBalance;
    }

    /// @dev This function updates the amount of underlying assets with the approach of the least possible
    ///      total tokens extracted for the current liquidity in the pool.
    function _updateUnderlyingAssets() internal {
        /**
         * Our net value represent the smallest amount of tokens we are able to extract from the position
         * given our liquidity.
         *
         * The least amount of tokens ex-tractable from the position is where the active trading price is
         * at the edge between tick -1 & tick 0. There the pool is offering 1:1 trades between WETH & OETH.
         * At that moment the pool consists completely of WETH and no OETH.
         *
         * The more swaps from OETH -> WETH happen on the pool the more the price starts to move away from the tick 0
         * towards the middle of tick -1 making OETH (priced in WETH) cheaper.
         */

        uint256 _wethAmount = tokenId == 0 ? 0 : _balanceInPosition();

        underlyingAssets = _wethAmount;
        emit UnderlyingAssetsUpdated(_wethAmount);
    }

    /**
     * @dev Strategy reserves (which consist only of WETH in case of Rooster - Plume pool)
     * when the tick price is closest to parity - assuring the lowest amount of tokens
     * returned for the current position liquidity.
     */
    function _balanceInPosition() internal view returns (uint256 _wethBalance) {
        (, , uint256 liquidity) = _getPositionInformation();

        uint256 _oethBalance;

        (_wethBalance, _oethBalance) = _reservesInTickForGivenPriceAndLiquidity(
            sqrtPriceTickLower,
            sqrtPriceTickHigher,
            sqrtPriceAtParity,
            liquidity
        );

        require(_oethBalance == 0, "Non zero oethBalance");
    }

    /**
     * @notice Tick dominance   denominated in 1e18
     * @return _tickDominance   The share of liquidity in TICK_NUMBER tick owned
     *                          by the strategy contract denominated in 1e18
     */
    function tickDominance() public view returns (uint256 _tickDominance) {
        IMaverickV2Pool.TickState memory tickState = mPool.getTick(TICK_NUMBER);

        uint256 wethReserve = tickState.reserveA;
        uint256 oethReserve = tickState.reserveB;

        // prettier-ignore
        (uint256 _amountWeth, uint256 _amountOeth, ) = _getPositionInformation();

        if (wethReserve + oethReserve == 0) {
            return 0;
        }

        _tickDominance = (_amountWeth + _amountOeth).divPrecisely(
            wethReserve + oethReserve
        );
    }

    /***************************************
            Hidden functions
    ****************************************/
    /**
     * @dev Unsupported
     */
    function setPTokenAddress(address, address) external pure override {
        // The pool tokens can never change.
        revert("Unsupported method");
    }

    /**
     * @dev Unsupported
     */
    function removePToken(uint256) external pure override {
        // The pool tokens can never change.
        revert("Unsupported method");
    }

    /**
     * @dev Unsupported
     */
    function _abstractSetPToken(address, address) internal pure override {
        revert("Unsupported method");
    }

    /**
     * @dev Unsupported
     */
    function safeApproveAllTokens() external pure override {
        // all the amounts are approved at the time required
        revert("Unsupported method");
    }

    /***************************************
          Maverick liquidity utilities
    ****************************************/

    /// @notice Calculates deltaA = liquidity * (sqrt(upper) - sqrt(lower))
    ///  Calculates deltaB = liquidity / sqrt(lower) - liquidity / sqrt(upper),
    ///  i.e. liquidity * (sqrt(upper) - sqrt(lower)) / (sqrt(upper) * sqrt(lower))
    ///
    /// @dev refactored from here:
    // solhint-disable-next-line max-line-length
    /// https://github.com/rooster-protocol/rooster-contracts/blob/main/v2-supplemental/contracts/libraries/LiquidityUtilities.sol#L665-L695
    function _reservesInTickForGivenPriceAndLiquidity(
        uint256 _lowerSqrtPrice,
        uint256 _upperSqrtPrice,
        uint256 _newSqrtPrice,
        uint256 _liquidity
    ) internal pure returns (uint128 reserveA, uint128 reserveB) {
        if (_liquidity == 0) {
            (reserveA, reserveB) = (0, 0);
        } else {
            uint256 lowerEdge = MathRooster.max(_lowerSqrtPrice, _newSqrtPrice);

            reserveA = MathRooster
                .mulCeil(
                    _liquidity,
                    MathRooster.clip(
                        MathRooster.min(_upperSqrtPrice, _newSqrtPrice),
                        _lowerSqrtPrice
                    )
                )
                .toUint128();
            reserveB = MathRooster
                .mulDivCeil(
                    _liquidity,
                    1e18 * MathRooster.clip(_upperSqrtPrice, lowerEdge),
                    _upperSqrtPrice * lowerEdge
                )
                .toUint128();
        }
    }
}

contracts/strategies/sonic/SonicStakingS.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import { Math } from "@openzeppelin/contracts/utils/math/Math.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SonicValidatorDelegator } from "./SonicValidatorDelegator.sol";
import { IWrappedSonic } from "../../interfaces/sonic/IWrappedSonic.sol";

/**
 * @title Staking Strategy for Sonic's native S currency
 * @author Origin Protocol Inc
 */
contract SonicStakingStrategy is SonicValidatorDelegator {
    // For future use
    uint256[50] private __gap;

    constructor(
        BaseStrategyConfig memory _baseConfig,
        address _wrappedSonic,
        address _sfc
    ) SonicValidatorDelegator(_baseConfig, _wrappedSonic, _sfc) {}

    /// @notice Deposit wrapped S asset into the underlying platform.
    /// @param _asset Address of asset to deposit. Has to be Wrapped Sonic (wS).
    /// @param _amount Amount of assets that were transferred to the strategy by the vault.
    function deposit(address _asset, uint256 _amount)
        external
        override
        onlyVault
        nonReentrant
    {
        require(_asset == wrappedSonic, "Unsupported asset");
        _deposit(_asset, _amount);
    }

    /**
     * @notice Deposit Wrapped Sonic (wS) to this strategy and delegate to a validator.
     * @param _asset Address of Wrapped Sonic (wS) token
     * @param _amount Amount of Wrapped Sonic (wS) to deposit
     */
    function _deposit(address _asset, uint256 _amount) internal virtual {
        require(_amount > 0, "Must deposit something");

        _delegate(_amount);
        emit Deposit(_asset, address(0), _amount);
    }

    /**
     * @notice Deposit the entire balance of wrapped S in this strategy contract into
     * the underlying platform.
     */
    function depositAll() external virtual override onlyVault nonReentrant {
        uint256 wSBalance = IERC20(wrappedSonic).balanceOf(address(this));

        if (wSBalance > 0) {
            _deposit(wrappedSonic, wSBalance);
        }
    }

    /// @notice Withdraw Wrapped Sonic (wS) from this strategy contract.
    /// Used only if some wS is lingering on the contract.
    /// That can happen only when someone sends wS directly to this contract
    /// @param _recipient Address to receive withdrawn assets
    /// @param _asset Address of the Wrapped Sonic (wS) token
    /// @param _amount Amount of Wrapped Sonic (wS) to withdraw
    function withdraw(
        address _recipient,
        address _asset,
        uint256 _amount
    ) external override onlyVault nonReentrant {
        require(_asset == wrappedSonic, "Unsupported asset");
        _withdraw(_recipient, _asset, _amount);
    }

    function _withdraw(
        address _recipient,
        address _asset,
        uint256 _amount
    ) internal override {
        require(_amount > 0, "Must withdraw something");
        require(_recipient != address(0), "Must specify recipient");

        // slither-disable-next-line unchecked-transfer unused-return
        IERC20(_asset).transfer(_recipient, _amount);

        emit Withdrawal(wrappedSonic, address(0), _amount);
    }

    /// @notice Transfer all Wrapped Sonic (wS) deposits back to the vault.
    /// This does not withdraw from delegated validators. That has to be done separately with `undelegate`.
    /// Any native S in this strategy will be withdrawn.
    function withdrawAll() external override onlyVaultOrGovernor nonReentrant {
        uint256 balance = address(this).balance;
        if (balance > 0) {
            IWrappedSonic(wrappedSonic).deposit{ value: balance }();
        }
        uint256 wSBalance = IERC20(wrappedSonic).balanceOf(address(this));
        if (wSBalance > 0) {
            _withdraw(vaultAddress, wrappedSonic, wSBalance);
        }
    }

    /**
     * @dev Returns bool indicating whether asset is supported by strategy
     * @param _asset Address of the asset token
     */
    function supportsAsset(address _asset)
        public
        view
        virtual
        override
        returns (bool)
    {
        return _asset == wrappedSonic;
    }

    /**
     * @notice is not supported for this strategy as the
     * Wrapped Sonic (wS) token is set at deploy time.
     */
    function setPTokenAddress(address, address)
        external
        view
        override
        onlyGovernor
    {
        revert("unsupported function");
    }

    /// @notice is not used by this strategy as all staking rewards are restaked
    function collectRewardTokens() external override nonReentrant {
        revert("unsupported function");
    }

    /**
     * @notice is not supported for this strategy as the
     * Wrapped Sonic (wS) token is set at deploy time.
     */
    function removePToken(uint256) external view override onlyGovernor {
        revert("unsupported function");
    }

    /// @dev is not used by this strategy but must be implemented as it's abstract
    /// in the inherited `InitializableAbstractStrategy` contract.
    function _abstractSetPToken(address, address) internal virtual override {}

    /// @notice is not used by this strategy
    function safeApproveAllTokens() external override onlyGovernor {}
}

contracts/strategies/sonic/SonicSwapXAMO.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

/**
 * @title SwapX Algorithmic Market Maker (AMO) Strategy
 * @notice AMO strategy for the SwapX OS/wS stable pool
 * @author Origin Protocol Inc
 */
import { SafeCast } from "@openzeppelin/contracts/utils/math/SafeCast.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import { IERC20, InitializableAbstractStrategy } from "../../utils/InitializableAbstractStrategy.sol";
import { StableMath } from "../../utils/StableMath.sol";
import { sqrt } from "../../utils/PRBMath.sol";
import { IBasicToken } from "../../interfaces/IBasicToken.sol";
import { IPair } from "../../interfaces/sonic/ISwapXPair.sol";
import { IGauge } from "../../interfaces/sonic/ISwapXGauge.sol";
import { IVault } from "../../interfaces/IVault.sol";

contract SonicSwapXAMOStrategy is InitializableAbstractStrategy {
    using SafeERC20 for IERC20;
    using StableMath for uint256;
    using SafeCast for uint256;

    /**
     * @notice a threshold under which the contract no longer allows for the protocol to manually rebalance.
     *      Guarding against a strategist / guardian being taken over and with multiple transactions
     *      draining the protocol funds.
     */
    uint256 public constant SOLVENCY_THRESHOLD = 0.998 ether;

    /// @notice Precision for the SwapX Stable AMM (sAMM) invariant k.
    uint256 public constant PRECISION = 1e18;

    /// @notice Address of the Wrapped S (wS) token.
    address public immutable ws;

    /// @notice Address of the OS token contract.
    address public immutable os;

    /// @notice Address of the SwapX Stable pool contract.
    address public immutable pool;

    /// @notice Address of the SwapX Gauge contract.
    address public immutable gauge;

    /// @notice The max amount the OS/wS price can deviate from peg (1e18)
    /// before deposits are reverted scaled to 18 decimals.
    /// eg 0.01e18 or 1e16 is 1% which is 100 basis points.
    /// This is the amount below and above peg so a 50 basis point deviation (0.005e18)
    /// allows a price range from 0.995 to 1.005.
    uint256 public maxDepeg;

    event SwapOTokensToPool(
        uint256 osMinted,
        uint256 wsDepositAmount,
        uint256 osDepositAmount,
        uint256 lpTokens
    );
    event SwapAssetsToPool(
        uint256 wsSwapped,
        uint256 lpTokens,
        uint256 osBurnt
    );
    event MaxDepegUpdated(uint256 maxDepeg);

    /**
     * @dev Verifies that the caller is the Strategist of the Vault.
     */
    modifier onlyStrategist() {
        require(
            msg.sender == IVault(vaultAddress).strategistAddr(),
            "Caller is not the Strategist"
        );
        _;
    }

    /**
     * @dev Skim the SwapX pool in case any extra wS or OS tokens were added
     */
    modifier skimPool() {
        IPair(pool).skim(address(this));
        _;
    }

    /**
     * @dev Checks the pool is balanced enough to allow deposits.
     */
    modifier nearBalancedPool() {
        // OS/wS price = wS / OS
        // Get the OS/wS price for selling 1 OS for wS
        // As OS is 1, the wS amount is the OS/wS price
        uint256 sellPrice = IPair(pool).getAmountOut(1e18, os);

        // Get the amount of OS received from selling 1 wS. This is buying OS.
        uint256 osAmount = IPair(pool).getAmountOut(1e18, ws);
        // Convert to a OS/wS price = wS / OS
        uint256 buyPrice = 1e36 / osAmount;

        uint256 pegPrice = 1e18;

        require(
            sellPrice >= pegPrice - maxDepeg && buyPrice <= pegPrice + maxDepeg,
            "price out of range"
        );
        _;
    }

    /**
     * @dev Checks the pool's balances have improved and the balances
     * have not tipped to the other side.
     * This modifier is only applied to functions that do swaps against the pool.
     * Deposits and withdrawals are proportional to the pool's balances hence don't need this check.
     */
    modifier improvePoolBalance() {
        // Get the asset and OToken balances in the pool
        (uint256 wsReservesBefore, uint256 osReservesBefore, ) = IPair(pool)
            .getReserves();
        // diff = wS balance - OS balance
        int256 diffBefore = wsReservesBefore.toInt256() -
            osReservesBefore.toInt256();

        _;

        // Get the asset and OToken balances in the pool
        (uint256 wsReservesAfter, uint256 osReservesAfter, ) = IPair(pool)
            .getReserves();
        // diff = wS balance - OS balance
        int256 diffAfter = wsReservesAfter.toInt256() -
            osReservesAfter.toInt256();

        if (diffBefore == 0) {
            require(diffAfter == 0, "Position balance is worsened");
        } else if (diffBefore < 0) {
            // If the pool was originally imbalanced in favor of OS, then
            // we want to check that the pool is now more balanced
            require(diffAfter <= 0, "Assets overshot peg");
            require(diffBefore < diffAfter, "OTokens balance worse");
        } else if (diffBefore > 0) {
            // If the pool was originally imbalanced in favor of wS, then
            // we want to check that the pool is now more balanced
            require(diffAfter >= 0, "OTokens overshot peg");
            require(diffAfter < diffBefore, "Assets balance worse");
        }
    }

    /**
     * @param _baseConfig The `platformAddress` is the address of the SwapX pool.
     * The `vaultAddress` is the address of the Origin Sonic Vault.
     * @param _os Address of the OS token.
     * @param _ws Address of the Wrapped S (wS) token.
     * @param _gauge Address of the SwapX gauge for the pool.
     */
    constructor(
        BaseStrategyConfig memory _baseConfig,
        address _os,
        address _ws,
        address _gauge
    ) InitializableAbstractStrategy(_baseConfig) {
        // Check the pool tokens are correct
        require(
            IPair(_baseConfig.platformAddress).token0() == _ws &&
                IPair(_baseConfig.platformAddress).token1() == _os,
            "Incorrect pool tokens"
        );
        // Checked both tokens are to 18 decimals
        require(
            IBasicToken(_ws).decimals() == 18 &&
                IBasicToken(_os).decimals() == 18,
            "Incorrect token decimals"
        );
        // Check the SwapX pool is a Stable AMM (sAMM)
        require(
            IPair(_baseConfig.platformAddress).isStable() == true,
            "Pool not stable"
        );
        // Check the gauge is for the pool
        require(
            IGauge(_gauge).TOKEN() == _baseConfig.platformAddress,
            "Incorrect gauge"
        );

        // Set the immutable variables
        os = _os;
        ws = _ws;
        pool = _baseConfig.platformAddress;
        gauge = _gauge;

        // This is an implementation contract. The governor is set in the proxy contract.
        _setGovernor(address(0));
    }

    /**
     * Initializer for setting up strategy internal state. This overrides the
     * InitializableAbstractStrategy initializer as SwapX strategies don't fit
     * well within that abstraction.
     * @param _rewardTokenAddresses Array containing SWPx token address
     * @param _maxDepeg The max amount the OS/wS price can deviate from peg (1e18) before deposits are reverted.
     */
    function initialize(
        address[] calldata _rewardTokenAddresses,
        uint256 _maxDepeg
    ) external onlyGovernor initializer {
        address[] memory pTokens = new address[](1);
        pTokens[0] = pool;

        address[] memory _assets = new address[](1);
        _assets[0] = ws;

        InitializableAbstractStrategy._initialize(
            _rewardTokenAddresses,
            _assets,
            pTokens
        );

        maxDepeg = _maxDepeg;

        _approveBase();
    }

    /***************************************
                    Deposit
    ****************************************/

    /**
     * @notice Deposit an amount of Wrapped S (wS) into the SwapX pool.
     * Mint OS in proportion to the pool's wS and OS reserves,
     * transfer Wrapped S (wS) and OS to the pool,
     * mint the pool's LP token and deposit in the gauge.
     * @dev This tx must be wrapped by the VaultValueChecker.
     * To minimize loses, the pool should be rebalanced before depositing.
     * The pool's OS/wS price must be within the maxDepeg range.
     * @param _asset Address of Wrapped S (wS) token.
     * @param _wsAmount Amount of Wrapped S (wS) tokens to deposit.
     */
    function deposit(address _asset, uint256 _wsAmount)
        external
        override
        onlyVault
        nonReentrant
        skimPool
        nearBalancedPool
    {
        require(_asset == ws, "Unsupported asset");
        require(_wsAmount > 0, "Must deposit something");

        (uint256 osDepositAmount, ) = _deposit(_wsAmount);

        // Ensure solvency of the vault
        _solvencyAssert();

        // Emit event for the deposited wS tokens
        emit Deposit(ws, pool, _wsAmount);
        // Emit event for the minted OS tokens
        emit Deposit(os, pool, osDepositAmount);
    }

    /**
     * @notice Deposit all the strategy's Wrapped S (wS) tokens into the SwapX pool.
     * Mint OS in proportion to the pool's wS and OS reserves,
     * transfer Wrapped S (wS) and OS to the pool,
     * mint the pool's LP token and deposit in the gauge.
     * @dev This tx must be wrapped by the VaultValueChecker.
     * To minimize loses, the pool should be rebalanced before depositing.
     * The pool's OS/wS price must be within the maxDepeg range.
     */
    function depositAll()
        external
        override
        onlyVault
        nonReentrant
        skimPool
        nearBalancedPool
    {
        uint256 wsBalance = IERC20(ws).balanceOf(address(this));
        if (wsBalance > 0) {
            (uint256 osDepositAmount, ) = _deposit(wsBalance);

            // Ensure solvency of the vault
            _solvencyAssert();

            // Emit event for the deposited wS tokens
            emit Deposit(ws, pool, wsBalance);
            // Emit event for the minted OS tokens
            emit Deposit(os, pool, osDepositAmount);
        }
    }

    /**
     * @dev Mint OS in proportion to the pool's wS and OS reserves,
     * transfer Wrapped S (wS) and OS to the pool,
     * mint the pool's LP token and deposit in the gauge.
     * @param _wsAmount Amount of Wrapped S (wS) tokens to deposit.
     * @return osDepositAmount Amount of OS tokens minted and deposited into the pool.
     * @return lpTokens Amount of SwapX pool LP tokens minted and deposited into the gauge.
     */
    function _deposit(uint256 _wsAmount)
        internal
        returns (uint256 osDepositAmount, uint256 lpTokens)
    {
        // Calculate the required amount of OS to mint based on the wS amount.
        osDepositAmount = _calcTokensToMint(_wsAmount);

        // Mint the required OS tokens to this strategy
        IVault(vaultAddress).mintForStrategy(osDepositAmount);

        // Add wS and OS liquidity to the pool and stake in gauge
        lpTokens = _depositToPoolAndGauge(_wsAmount, osDepositAmount);
    }

    /***************************************
                    Withdraw
    ****************************************/

    /**
     * @notice Withdraw wS and OS from the SwapX pool, burn the OS,
     * and transfer the wS to the recipient.
     * @param _recipient Address of the Vault.
     * @param _asset Address of the Wrapped S (wS) contract.
     * @param _wsAmount Amount of Wrapped S (wS) to withdraw.
     */
    function withdraw(
        address _recipient,
        address _asset,
        uint256 _wsAmount
    ) external override onlyVault nonReentrant skimPool {
        require(_wsAmount > 0, "Must withdraw something");
        require(_asset == ws, "Unsupported asset");
        // This strategy can't be set as a default strategy for wS in the Vault.
        // This means the recipient must always be the Vault.
        require(_recipient == vaultAddress, "Only withdraw to vault allowed");

        // Calculate how much pool LP tokens to burn to get the required amount of wS tokens back
        uint256 lpTokens = _calcTokensToBurn(_wsAmount);

        // Withdraw pool LP tokens from the gauge and remove assets from from the pool
        _withdrawFromGaugeAndPool(lpTokens);

        // Burn all the removed OS and any that was left in the strategy
        uint256 osToBurn = IERC20(os).balanceOf(address(this));
        IVault(vaultAddress).burnForStrategy(osToBurn);

        // Transfer wS to the recipient
        // Note there can be a dust amount of wS left in the strategy as
        // the burn of the pool's LP tokens is rounded up
        require(
            IERC20(ws).balanceOf(address(this)) >= _wsAmount,
            "Not enough wS removed from pool"
        );
        IERC20(ws).safeTransfer(_recipient, _wsAmount);

        // Ensure solvency of the vault
        _solvencyAssert();

        // Emit event for the withdrawn wS tokens
        emit Withdrawal(ws, pool, _wsAmount);
        // Emit event for the burnt OS tokens
        emit Withdrawal(os, pool, osToBurn);
    }

    /**
     * @notice Withdraw all pool LP tokens from the gauge,
     * remove all wS and OS from the SwapX pool,
     * burn all the OS tokens,
     * and transfer all the wS to the Vault contract.
     * @dev There is no solvency check here as withdrawAll can be called to
     * quickly secure assets to the Vault in emergencies.
     */
    function withdrawAll()
        external
        override
        onlyVaultOrGovernor
        nonReentrant
        skimPool
    {
        // Get all the pool LP tokens the strategy has staked in the gauge
        uint256 lpTokens = IGauge(gauge).balanceOf(address(this));
        // Can not withdraw zero LP tokens from the gauge
        if (lpTokens == 0) return;

        if (IGauge(gauge).emergency()) {
            // The gauge is in emergency mode
            _emergencyWithdrawFromGaugeAndPool();
        } else {
            // Withdraw pool LP tokens from the gauge and remove assets from from the pool
            _withdrawFromGaugeAndPool(lpTokens);
        }

        // Burn all OS in this strategy contract
        uint256 osToBurn = IERC20(os).balanceOf(address(this));
        IVault(vaultAddress).burnForStrategy(osToBurn);

        // Get the strategy contract's wS balance.
        // This includes all that was removed from the SwapX pool and
        // any that was sitting in the strategy contract before the removal.
        uint256 wsBalance = IERC20(ws).balanceOf(address(this));
        IERC20(ws).safeTransfer(vaultAddress, wsBalance);

        // Emit event for the withdrawn wS tokens
        emit Withdrawal(ws, pool, wsBalance);
        // Emit event for the burnt OS tokens
        emit Withdrawal(os, pool, osToBurn);
    }

    /***************************************
                Pool Rebalancing
    ****************************************/

    /** @notice Used when there is more OS than wS in the pool.
     * wS and OS is removed from the pool, the received wS is swapped for OS
     * and the left over OS in the strategy is burnt.
     * The OS/wS price is < 1.0 so OS is being bought at a discount.
     * @param _wsAmount Amount of Wrapped S (wS) to swap into the pool.
     */
    function swapAssetsToPool(uint256 _wsAmount)
        external
        onlyStrategist
        nonReentrant
        improvePoolBalance
        skimPool
    {
        require(_wsAmount > 0, "Must swap something");

        // 1. Partially remove liquidity so theres enough wS for the swap

        // Calculate how much pool LP tokens to burn to get the required amount of wS tokens back
        uint256 lpTokens = _calcTokensToBurn(_wsAmount);
        require(lpTokens > 0, "No LP tokens to burn");

        _withdrawFromGaugeAndPool(lpTokens);

        // 2. Swap wS for OS against the pool
        // Swap exact amount of wS for OS against the pool
        // There can be a dust amount of wS left in the strategy as the burn of the pool's LP tokens is rounded up
        _swapExactTokensForTokens(_wsAmount, ws, os);

        // 3. Burn all the OS left in the strategy from the remove liquidity and swap
        uint256 osToBurn = IERC20(os).balanceOf(address(this));
        IVault(vaultAddress).burnForStrategy(osToBurn);

        // Ensure solvency of the vault
        _solvencyAssert();

        // Emit event for the burnt OS tokens
        emit Withdrawal(os, pool, osToBurn);
        // Emit event for the swap
        emit SwapAssetsToPool(_wsAmount, lpTokens, osToBurn);
    }

    /**
     * @notice Used when there is more wS than OS in the pool.
     * OS is minted and swapped for wS against the pool,
     * more OS is minted and added back into the pool with the swapped out wS.
     * The OS/wS price is > 1.0 so OS is being sold at a premium.
     * @param _osAmount Amount of OS to swap into the pool.
     */
    function swapOTokensToPool(uint256 _osAmount)
        external
        onlyStrategist
        nonReentrant
        improvePoolBalance
        skimPool
    {
        require(_osAmount > 0, "Must swap something");

        // 1. Mint OS so it can be swapped into the pool

        // There can be OS in the strategy from skimming the pool
        uint256 osInStrategy = IERC20(os).balanceOf(address(this));
        require(_osAmount >= osInStrategy, "Too much OS in strategy");
        uint256 osToMint = _osAmount - osInStrategy;

        // Mint the required OS tokens to this strategy
        IVault(vaultAddress).mintForStrategy(osToMint);

        // 2. Swap OS for wS against the pool
        _swapExactTokensForTokens(_osAmount, os, ws);

        // The wS is from the swap and any wS that was sitting in the strategy
        uint256 wsDepositAmount = IERC20(ws).balanceOf(address(this));

        // 3. Add wS and OS back to the pool in proportion to the pool's reserves
        (uint256 osDepositAmount, uint256 lpTokens) = _deposit(wsDepositAmount);

        // Ensure solvency of the vault
        _solvencyAssert();

        // Emit event for the minted OS tokens
        emit Deposit(os, pool, osToMint + osDepositAmount);
        // Emit event for the swap
        emit SwapOTokensToPool(
            osToMint,
            wsDepositAmount,
            osDepositAmount,
            lpTokens
        );
    }

    /***************************************
                Assets and Rewards
    ****************************************/

    /**
     * @notice Get the wS value of assets in the strategy and SwapX pool.
     * The value of the assets in the pool is calculated assuming the pool is balanced.
     * This way the value can not be manipulated by changing the pool's token balances.
     * @param _asset      Address of the Wrapped S (wS) token
     * @return balance    Total value in wS.
     */
    function checkBalance(address _asset)
        external
        view
        override
        returns (uint256 balance)
    {
        require(_asset == ws, "Unsupported asset");

        // wS balance needed here for the balance check that happens from vault during depositing.
        balance = IERC20(ws).balanceOf(address(this));

        // This assumes 1 gauge LP token = 1 pool LP token
        uint256 lpTokens = IGauge(gauge).balanceOf(address(this));
        if (lpTokens == 0) return balance;

        // Add the strategys share of the wS and OS tokens in the SwapX pool if the pool was balanced.
        balance += _lpValue(lpTokens);
    }

    /**
     * @notice Returns bool indicating whether asset is supported by strategy
     * @param _asset Address of the asset
     */
    function supportsAsset(address _asset) public view override returns (bool) {
        return _asset == ws;
    }

    /**
     * @notice Collect accumulated SWPx (and other) rewards and send to the Harvester.
     */
    function collectRewardTokens()
        external
        override
        onlyHarvester
        nonReentrant
    {
        // Collect SWPx rewards from the gauge
        IGauge(gauge).getReward();

        _collectRewardTokens();
    }

    /***************************************
        Internal SwapX Pool and Gauge Functions
    ****************************************/

    /**
     * @dev Calculate the required amount of OS to mint based on the wS amount.
     * This ensures the proportion of OS tokens being added to the pool matches the proportion of wS tokens.
     * For example, if the added wS tokens is 10% of existing wS tokens in the pool,
     * then the OS tokens being added should also be 10% of the OS tokens in the pool.
     * @param _wsAmount Amount of Wrapped S (wS) to be added to the pool.
     * @return osAmount Amount of OS to be minted and added to the pool.
     */
    function _calcTokensToMint(uint256 _wsAmount)
        internal
        view
        returns (uint256 osAmount)
    {
        (uint256 wsReserves, uint256 osReserves, ) = IPair(pool).getReserves();
        require(wsReserves > 0, "Empty pool");

        // OS to add = (wS being added * OS in pool) / wS in pool
        osAmount = (_wsAmount * osReserves) / wsReserves;
    }

    /**
     * @dev Calculate how much pool LP tokens to burn to get the required amount of wS tokens back
     * from the pool.
     * @param _wsAmount Amount of Wrapped S (wS) to be removed from the pool.
     * @return lpTokens Amount of SwapX pool LP tokens to burn.
     */
    function _calcTokensToBurn(uint256 _wsAmount)
        internal
        view
        returns (uint256 lpTokens)
    {
        /* The SwapX pool proportionally returns the reserve tokens when removing liquidity.
         * First, calculate the proportion of required wS tokens against the pools wS reserves.
         * That same proportion is used to calculate the required amount of pool LP tokens.
         * For example, if the required wS tokens is 10% of the pool's wS reserves,
         * then 10% of the pool's LP supply needs to be burned.
         *
         * Because we are doing balanced removal we should be making profit when removing liquidity in a
         * pool tilted to either side.
         *
         * Important: A downside is that the Strategist / Governor needs to be
         * cognizant of not removing too much liquidity. And while the proposal to remove liquidity
         * is being voted on, the pool tilt might change so much that the proposal that has been valid while
         * created is no longer valid.
         */

        (uint256 wsReserves, , ) = IPair(pool).getReserves();
        require(wsReserves > 0, "Empty pool");

        lpTokens = (_wsAmount * IPair(pool).totalSupply()) / wsReserves;
        lpTokens += 1; // Add 1 to ensure we get enough LP tokens with rounding
    }

    /**
     * @dev Deposit Wrapped S (wS) and OS liquidity to the SwapX pool
     * and stake the pool's LP token in the gauge.
     * @param _wsAmount Amount of Wrapped S (wS) to deposit.
     * @param _osAmount Amount of OS to deposit.
     * @return lpTokens Amount of SwapX pool LP tokens minted.
     */
    function _depositToPoolAndGauge(uint256 _wsAmount, uint256 _osAmount)
        internal
        returns (uint256 lpTokens)
    {
        // Transfer wS to the pool
        IERC20(ws).safeTransfer(pool, _wsAmount);
        // Transfer OS to the pool
        IERC20(os).safeTransfer(pool, _osAmount);

        // Mint LP tokens from the pool
        lpTokens = IPair(pool).mint(address(this));

        // Deposit the pool's LP tokens into the gauge
        IGauge(gauge).deposit(lpTokens);
    }

    /**
     * @dev Withdraw pool LP tokens from the gauge and remove wS and OS from the pool.
     * @param _lpTokens Amount of SwapX pool LP tokens to withdraw from the gauge
     */
    function _withdrawFromGaugeAndPool(uint256 _lpTokens) internal {
        require(
            IGauge(gauge).balanceOf(address(this)) >= _lpTokens,
            "Not enough LP tokens in gauge"
        );

        // Withdraw pool LP tokens from the gauge
        IGauge(gauge).withdraw(_lpTokens);

        // Transfer the pool LP tokens to the pool
        IERC20(pool).safeTransfer(pool, _lpTokens);

        // Burn the LP tokens and transfer the wS and OS back to the strategy
        IPair(pool).burn(address(this));
    }

    /**
     * @dev Withdraw all pool LP tokens from the gauge when it's in emergency mode
     * and remove wS and OS from the pool.
     */
    function _emergencyWithdrawFromGaugeAndPool() internal {
        // Withdraw all pool LP tokens from the gauge
        IGauge(gauge).emergencyWithdraw();

        // Get the pool LP tokens in strategy
        uint256 _lpTokens = IERC20(pool).balanceOf(address(this));

        // Transfer the pool LP tokens to the pool
        IERC20(pool).safeTransfer(pool, _lpTokens);

        // Burn the LP tokens and transfer the wS and OS back to the strategy
        IPair(pool).burn(address(this));
    }

    /**
     * @dev Swap exact amount of tokens for another token against the pool.
     * @param _amountIn Amount of tokens to swap into the pool.
     * @param _tokenIn Address of the token going into the pool.
     * @param _tokenOut Address of the token being swapped out of the pool.
     */
    function _swapExactTokensForTokens(
        uint256 _amountIn,
        address _tokenIn,
        address _tokenOut
    ) internal {
        // Transfer in tokens to the pool
        IERC20(_tokenIn).safeTransfer(pool, _amountIn);

        // Calculate how much out tokens we get from the swap
        uint256 amountOut = IPair(pool).getAmountOut(_amountIn, _tokenIn);

        // Safety check that we are dealing with the correct pool tokens
        require(
            (_tokenIn == ws && _tokenOut == os) ||
                (_tokenIn == os && _tokenOut == ws),
            "Unsupported swap"
        );

        // Work out the correct order of the amounts for the pool
        (uint256 amount0, uint256 amount1) = _tokenIn == ws
            ? (uint256(0), amountOut)
            : (amountOut, 0);

        // Perform the swap on the pool
        IPair(pool).swap(amount0, amount1, address(this), new bytes(0));

        // The slippage protection against the amount out is indirectly done
        // via the improvePoolBalance
    }

    /// @dev Calculate the value of a LP position in a SwapX stable pool
    /// if the pool was balanced.
    /// @param _lpTokens Amount of LP tokens in the SwapX pool
    /// @return value The wS value of the LP tokens when the pool is balanced
    function _lpValue(uint256 _lpTokens) internal view returns (uint256 value) {
        // Get total supply of LP tokens
        uint256 totalSupply = IPair(pool).totalSupply();
        if (totalSupply == 0) return 0;

        // Get the current reserves of the pool
        (uint256 wsReserves, uint256 osReserves, ) = IPair(pool).getReserves();

        // Calculate the invariant of the pool assuming both tokens have 18 decimals.
        // k is scaled to 18 decimals.
        uint256 k = _invariant(wsReserves, osReserves);

        // If x = y, lets denote x = y = z (where z is the common reserve value)
        // Substitute z into the invariant:
        // k = z^3 * z + z * z^3
        // k = 2 * z^4
        // Going back the other way to calculate the common reserve value z
        // z = (k / 2) ^ (1/4)
        // the total value of the pool when x = y is 2 * z, which is 2 * (k / 2) ^ (1/4)
        uint256 zSquared = sqrt((k * 1e18) / 2); // 18 + 18 = 36 decimals becomes 18 decimals after sqrt
        uint256 z = sqrt(zSquared * 1e18); //  18 + 18 = 36 decimals becomes 18 decimals after sqrt
        uint256 totalValueOfPool = 2 * z;

        // lp value = lp tokens * value of pool  / total supply
        value = (_lpTokens * totalValueOfPool) / totalSupply;
    }

    /**
     * @dev Compute the invariant for a SwapX stable pool.
     * This assumed both x and y tokens are to 18 decimals which is checked in the constructor.
     * invariant: k = x^3 * y + x * y^3
     * @dev This implementation is copied from SwapX's Pair contract.
     * @param _x The amount of Wrapped S (wS) tokens in the pool
     * @param _y The amount of the OS tokens in the pool
     * @return k The invariant of the SwapX stable pool
     */
    function _invariant(uint256 _x, uint256 _y)
        internal
        pure
        returns (uint256 k)
    {
        uint256 _a = (_x * _y) / PRECISION;
        uint256 _b = ((_x * _x) / PRECISION + (_y * _y) / PRECISION);
        // slither-disable-next-line divide-before-multiply
        k = (_a * _b) / PRECISION;
    }

    /**
     * @dev Checks that the protocol is solvent, protecting from a rogue Strategist / Guardian that can
     * keep rebalancing the pool in both directions making the protocol lose a tiny amount of
     * funds each time.
     *
     * Protocol must be at least SOLVENCY_THRESHOLD (99,8 %) backed in order for the rebalances to
     * function.
     */
    function _solvencyAssert() internal view {
        uint256 _totalVaultValue = IVault(vaultAddress).totalValue();
        uint256 _totalSupply = IERC20(os).totalSupply();

        if (
            _totalSupply > 0 &&
            _totalVaultValue.divPrecisely(_totalSupply) < SOLVENCY_THRESHOLD
        ) {
            revert("Protocol insolvent");
        }
    }

    /***************************************
                    Setters
    ****************************************/

    /**
     * @notice Set the maximum deviation from the OS/wS peg (1e18) before deposits are reverted.
     * @param _maxDepeg the OS/wS price from peg (1e18) in 18 decimals.
     * eg 0.01e18 or 1e16 is 1% which is 100 basis points.
     */
    function setMaxDepeg(uint256 _maxDepeg) external onlyGovernor {
        maxDepeg = _maxDepeg;

        emit MaxDepegUpdated(_maxDepeg);
    }

    /***************************************
                    Approvals
    ****************************************/

    /**
     * @notice Approve the spending of all assets by their corresponding pool tokens,
     *      if for some reason is it necessary.
     */
    function safeApproveAllTokens()
        external
        override
        onlyGovernor
        nonReentrant
    {
        _approveBase();
    }

    // solhint-disable-next-line no-unused-vars
    function _abstractSetPToken(address _asset, address _pToken)
        internal
        override
    {}

    function _approveBase() internal {
        // Approve SwapX gauge contract to transfer SwapX pool LP tokens
        // This is needed for deposits of SwapX pool LP tokens into the gauge.
        // slither-disable-next-line unused-return
        IPair(pool).approve(address(gauge), type(uint256).max);
    }
}

contracts/strategies/sonic/SonicValidato.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import { IERC20, InitializableAbstractStrategy } from "../../utils/InitializableAbstractStrategy.sol";
import { IVault } from "../../interfaces/IVault.sol";
import { ISFC } from "../../interfaces/sonic/ISFC.sol";
import { IWrappedSonic } from "../../interfaces/sonic/IWrappedSonic.sol";

/**
 * @title Manages delegation to Sonic validators
 * @notice This contract implements all the required functionality to delegate to,
   undelegate from and withdraw from validators.
 * @author Origin Protocol Inc
 */
abstract contract SonicValidatorDelegator is InitializableAbstractStrategy {
    /// @notice Address of Sonic's wrapped S token
    address public immutable wrappedSonic;
    /// @notice Sonic's Special Fee Contract (SFC)
    ISFC public immutable sfc;

    /// @notice a unique ID for each withdrawal request
    uint256 public nextWithdrawId;
    /// @notice Sonic (S) that is pending withdrawal after undelegating
    uint256 public pendingWithdrawals;

    /// @notice List of supported validator IDs that can be delegated to
    uint256[] public supportedValidators;

    /// @notice Default validator id to deposit to
    uint256 public defaultValidatorId;

    struct WithdrawRequest {
        uint256 validatorId;
        uint256 undelegatedAmount;
        uint256 timestamp;
    }
    /// @notice Mapping of withdrawIds to validatorIds and undelegatedAmounts
    mapping(uint256 => WithdrawRequest) public withdrawals;

    /// @notice Address of the registrator - allowed to register, exit and remove validators
    address public validatorRegistrator;

    // For future use
    uint256[44] private __gap;

    event Delegated(uint256 indexed validatorId, uint256 delegatedAmount);
    event Undelegated(
        uint256 indexed withdrawId,
        uint256 indexed validatorId,
        uint256 undelegatedAmount
    );
    event Withdrawn(
        uint256 indexed withdrawId,
        uint256 indexed validatorId,
        uint256 undelegatedAmount,
        uint256 withdrawnAmount
    );
    event RegistratorChanged(address indexed newAddress);
    event SupportedValidator(uint256 indexed validatorId);
    event UnsupportedValidator(uint256 indexed validatorId);
    event DefaultValidatorIdChanged(uint256 indexed validatorId);

    /// @dev Throws if called by any account other than the Registrator or Strategist
    modifier onlyRegistratorOrStrategist() {
        require(
            msg.sender == validatorRegistrator ||
                msg.sender == IVault(vaultAddress).strategistAddr(),
            "Caller is not the Registrator or Strategist"
        );
        _;
    }

    constructor(
        BaseStrategyConfig memory _baseConfig,
        address _wrappedSonic,
        address _sfc
    ) InitializableAbstractStrategy(_baseConfig) {
        wrappedSonic = _wrappedSonic;
        sfc = ISFC(_sfc);
    }

    function initialize() external virtual onlyGovernor initializer {
        address[] memory rewardTokens = new address[](0);
        address[] memory assets = new address[](1);
        address[] memory pTokens = new address[](1);

        assets[0] = address(wrappedSonic);
        pTokens[0] = address(platformAddress);

        InitializableAbstractStrategy._initialize(
            rewardTokens,
            assets,
            pTokens
        );
    }

    /// @notice Returns the total value of Sonic (S) that is delegated validators.
    /// Wrapped Sonic (wS) deposits that are still to be delegated and any undelegated amounts
    /// still pending a withdrawal.
    /// @param _asset      Address of Wrapped Sonic (wS) token
    /// @return balance    Total value managed by the strategy
    function checkBalance(address _asset)
        external
        view
        virtual
        override
        returns (uint256 balance)
    {
        require(_asset == wrappedSonic, "Unsupported asset");

        // add the Wrapped Sonic (wS) in the strategy from deposits that are still to be delegated
        // and any undelegated amounts still pending a withdrawal
        balance =
            IERC20(wrappedSonic).balanceOf(address(this)) +
            pendingWithdrawals;

        // For each supported validator, get the staked amount and pending rewards
        uint256 validatorLen = supportedValidators.length;
        for (uint256 i = 0; i < validatorLen; i++) {
            uint256 validator = supportedValidators[i];
            balance += sfc.getStake(address(this), validator);
            balance += sfc.pendingRewards(address(this), validator);
        }
    }

    /**
     * @dev Delegate from this strategy to a specific Sonic validator. Called
     * automatically on asset deposit
     * @param _amount the amount of Sonic (S) to delegate.
     */
    function _delegate(uint256 _amount) internal {
        require(
            isSupportedValidator(defaultValidatorId),
            "Validator not supported"
        );

        // unwrap Wrapped Sonic (wS) to native Sonic (S)
        IWrappedSonic(wrappedSonic).withdraw(_amount);

        //slither-disable-next-line arbitrary-send-eth
        sfc.delegate{ value: _amount }(defaultValidatorId);

        emit Delegated(defaultValidatorId, _amount);
    }

    /**
     * @notice Undelegate from a specific Sonic validator.
     * This needs to be followed by a `withdrawFromSFC` two weeks later.
     * @param _validatorId The Sonic validator ID to undelegate from.
     * @param _undelegateAmount the amount of Sonic (S) to undelegate.
     * @return withdrawId The unique ID of the withdrawal request.
     */
    function undelegate(uint256 _validatorId, uint256 _undelegateAmount)
        external
        onlyRegistratorOrStrategist
        nonReentrant
        returns (uint256 withdrawId)
    {
        withdrawId = _undelegate(_validatorId, _undelegateAmount);
    }

    function _undelegate(uint256 _validatorId, uint256 _undelegateAmount)
        internal
        returns (uint256 withdrawId)
    {
        // Can still undelegate even if the validator is no longer supported
        require(_undelegateAmount > 0, "Must undelegate something");

        uint256 amountDelegated = sfc.getStake(address(this), _validatorId);
        require(
            _undelegateAmount <= amountDelegated,
            "Insufficient delegation"
        );

        withdrawId = nextWithdrawId++;

        withdrawals[withdrawId] = WithdrawRequest(
            _validatorId,
            _undelegateAmount,
            block.timestamp
        );
        pendingWithdrawals += _undelegateAmount;

        sfc.undelegate(_validatorId, withdrawId, _undelegateAmount);

        emit Undelegated(withdrawId, _validatorId, _undelegateAmount);
    }

    /**
     * @notice Withdraw native S from a previously undelegated validator.
     * The native S is wrapped wS and transferred to the Vault.
     * @param _withdrawId The unique withdraw ID used to `undelegate`
     * @return withdrawnAmount The amount of Sonic (S) withdrawn.
     * This can be less than the undelegated amount in the event of slashing.
     */
    function withdrawFromSFC(uint256 _withdrawId)
        external
        onlyRegistratorOrStrategist
        nonReentrant
        returns (uint256 withdrawnAmount)
    {
        require(_withdrawId < nextWithdrawId, "Invalid withdrawId");

        // Can still withdraw even if the validator is no longer supported
        // Load the withdrawal from storage into memory
        WithdrawRequest memory withdrawal = withdrawals[_withdrawId];
        require(!isWithdrawnFromSFC(_withdrawId), "Already withdrawn");

        withdrawals[_withdrawId].undelegatedAmount = 0;
        pendingWithdrawals -= withdrawal.undelegatedAmount;

        uint256 sBalanceBefore = address(this).balance;

        // Try to withdraw from SFC
        try sfc.withdraw(withdrawal.validatorId, _withdrawId) {
            // continue below
        } catch (bytes memory err) {
            bytes4 errorSelector = bytes4(err);

            // If the validator has been fully slashed, SFC's withdraw function will
            // revert with a StakeIsFullySlashed custom error.
            if (errorSelector == ISFC.StakeIsFullySlashed.selector) {
                // The validator was fully slashed, so all the delegated amounts were lost.
                // Will swallow the error as we still want to update the
                // withdrawals and pendingWithdrawals storage variables.

                // The return param defaults to zero but lets set it explicitly so it's clear
                withdrawnAmount = 0;

                emit Withdrawn(
                    _withdrawId,
                    withdrawal.validatorId,
                    withdrawal.undelegatedAmount,
                    withdrawnAmount
                );

                // Exit here as there is nothing to transfer to the Vault
                return withdrawnAmount;
            } else {
                // Bubble up any other SFC custom errors.
                // Inline assembly is currently the only way to generically rethrow the exact same custom error
                // from the raw bytes err in a catch block while preserving its original selector and parameters.
                // solhint-disable-next-line no-inline-assembly
                assembly {
                    revert(add(32, err), mload(err))
                }
            }
        }

        // Set return parameter
        withdrawnAmount = address(this).balance - sBalanceBefore;

        // Wrap Sonic (S) to Wrapped Sonic (wS)
        IWrappedSonic(wrappedSonic).deposit{ value: withdrawnAmount }();

        // Transfer the Wrapped Sonic (wS) to the Vault
        _withdraw(vaultAddress, wrappedSonic, withdrawnAmount);

        // withdrawal.undelegatedAmount & withdrawnAmount can differ in case of slashing
        emit Withdrawn(
            _withdrawId,
            withdrawal.validatorId,
            withdrawal.undelegatedAmount,
            withdrawnAmount
        );
    }

    /// @notice returns a bool whether a withdrawalId has already been withdrawn or not
    /// @param _withdrawId The unique withdraw ID used to `undelegate`
    function isWithdrawnFromSFC(uint256 _withdrawId)
        public
        view
        returns (bool)
    {
        WithdrawRequest memory withdrawal = withdrawals[_withdrawId];
        require(withdrawal.validatorId > 0, "Invalid withdrawId");
        return withdrawal.undelegatedAmount == 0;
    }

    /**
     * @notice Restake any pending validator rewards for all supported validators
     * @param _validatorIds List of Sonic validator IDs to restake rewards
     */
    function restakeRewards(uint256[] calldata _validatorIds)
        external
        nonReentrant
    {
        for (uint256 i = 0; i < _validatorIds.length; ++i) {
            require(
                isSupportedValidator(_validatorIds[i]),
                "Validator not supported"
            );

            uint256 rewards = sfc.pendingRewards(
                address(this),
                _validatorIds[i]
            );

            if (rewards > 0) {
                sfc.restakeRewards(_validatorIds[i]);
            }
        }

        // The SFC contract will emit Delegated and RestakedRewards events.
        // The checkBalance function should not change as the pending rewards will moved to the staked amount.
    }

    /**
     * @notice Claim any pending rewards from validators
     * @param _validatorIds List of Sonic validator IDs to claim rewards
     */
    function collectRewards(uint256[] calldata _validatorIds)
        external
        onlyRegistratorOrStrategist
        nonReentrant
    {
        uint256 sBalanceBefore = address(this).balance;

        for (uint256 i = 0; i < _validatorIds.length; ++i) {
            uint256 rewards = sfc.pendingRewards(
                address(this),
                _validatorIds[i]
            );

            if (rewards > 0) {
                // The SFC contract will emit ClaimedRewards(delegator (this), validatorId, rewards)
                sfc.claimRewards(_validatorIds[i]);
            }
        }

        uint256 rewardsAmount = address(this).balance - sBalanceBefore;

        // Wrap Sonic (S) to Wrapped Sonic (wS)
        IWrappedSonic(wrappedSonic).deposit{ value: rewardsAmount }();

        // Transfer the Wrapped Sonic (wS) to the Vault
        _withdraw(vaultAddress, wrappedSonic, rewardsAmount);
    }

    /**
     * @notice To receive native S from SFC and Wrapped Sonic (wS)
     *
     * @dev This does not prevent donating S tokens to the contract
     * as wrappedSonic has a `withdrawTo` function where a third party
     * owner of wrappedSonic can withdraw to this contract.
     */
    receive() external payable {
        require(
            msg.sender == address(sfc) || msg.sender == wrappedSonic,
            "S not from allowed contracts"
        );
    }

    /***************************************
                Admin functions
    ****************************************/

    /// @notice Set the address of the Registrator which can undelegate, withdraw and collect rewards
    /// @param _validatorRegistrator The address of the Registrator
    function setRegistrator(address _validatorRegistrator)
        external
        onlyGovernor
    {
        validatorRegistrator = _validatorRegistrator;
        emit RegistratorChanged(_validatorRegistrator);
    }

    /// @notice Set the default validatorId to delegate to on deposit
    /// @param _validatorId The validator identifier. eg 18
    function setDefaultValidatorId(uint256 _validatorId)
        external
        onlyRegistratorOrStrategist
    {
        require(isSupportedValidator(_validatorId), "Validator not supported");
        defaultValidatorId = _validatorId;
        emit DefaultValidatorIdChanged(_validatorId);
    }

    /// @notice Allows a validator to be delegated to by the Registrator
    /// @param _validatorId The validator identifier. eg 18
    function supportValidator(uint256 _validatorId) external onlyGovernor {
        require(
            !isSupportedValidator(_validatorId),
            "Validator already supported"
        );

        supportedValidators.push(_validatorId);

        emit SupportedValidator(_validatorId);
    }

    /// @notice Removes a validator from the supported list.
    /// Unsupported validators can still be undelegated from, withdrawn from and rewards collected.
    /// @param _validatorId The validator identifier. eg 18
    function unsupportValidator(uint256 _validatorId) external onlyGovernor {
        require(isSupportedValidator(_validatorId), "Validator not supported");

        uint256 validatorLen = supportedValidators.length;
        for (uint256 i = 0; i < validatorLen; ++i) {
            if (supportedValidators[i] == _validatorId) {
                supportedValidators[i] = supportedValidators[validatorLen - 1];
                supportedValidators.pop();
                break;
            }
        }

        uint256 stake = sfc.getStake(address(this), _validatorId);

        // undelegate if validator still has funds staked
        if (stake > 0) {
            _undelegate(_validatorId, stake);
        }
        emit UnsupportedValidator(_validatorId);
    }

    /// @notice Returns the length of the supportedValidators array
    function supportedValidatorsLength() external view returns (uint256) {
        return supportedValidators.length;
    }

    /// @notice Returns whether a validator is supported by this strategy
    /// @param _validatorId The validator identifier
    function isSupportedValidator(uint256 _validatorId)
        public
        view
        returns (bool)
    {
        uint256 validatorLen = supportedValidators.length;
        for (uint256 i = 0; i < validatorLen; ++i) {
            if (supportedValidators[i] == _validatorId) {
                return true;
            }
        }
        return false;
    }

    function _withdraw(
        address _recipient,
        address _asset,
        uint256 _amount
    ) internal virtual;
}

