","keccak256":"0xf9eea30160579cc46f5255db068cf7bfd4a29008b19827b63038d5fbe6994c99","license":"MIT"},"contracts/strategies/balancer/BalancerMetaPoolStrategy.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
 * @title OETH Balancer MetaStablePool Strategy
 * @author Origin Protocol Inc
 */
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { BaseAuraStrategy, BaseBalancerStrategy } from "./BaseAuraStrategy.sol";
import { IBalancerVault } from "../../interfaces/balancer/IBalancerVault.sol";
import { IRateProvider } from "../../interfaces/balancer/IRateProvider.sol";
import { IMetaStablePool } from "../../interfaces/balancer/IMetaStablePool.sol";
import { IERC20, InitializableAbstractStrategy } from "../../utils/InitializableAbstractStrategy.sol";
import { StableMath } from "../../utils/StableMath.sol";

contract BalancerMetaPoolStrategy is BaseAuraStrategy {
    using SafeERC20 for IERC20;
    using StableMath for uint256;

    constructor(
        BaseStrategyConfig memory _stratConfig,
        BaseBalancerConfig memory _balancerConfig,
        address _auraRewardPoolAddress
    )
        InitializableAbstractStrategy(_stratConfig)
        BaseBalancerStrategy(_balancerConfig)
        BaseAuraStrategy(_auraRewardPoolAddress)
    {}

    /**
     * @notice There are no plans to configure BalancerMetaPool as a default
     * asset strategy. For that reason there is no need to support this
     * functionality.
     */
    function deposit(address, uint256)
        external
        override
        onlyVault
        nonReentrant
    {
        revert("Not supported");
    }

    /**
     * @notice There are no plans to configure BalancerMetaPool as a default
     * asset strategy. For that reason there is no need to support this
     * functionality.
     */
    function deposit(address[] calldata, uint256[] calldata)
        external
        onlyVault
        nonReentrant
    {
        revert("Not supported");
    }

    /**
     * @notice Deposits all supported assets in this strategy contract to the Balancer pool.
     */
    function depositAll() external override onlyVault nonReentrant {
        uint256 assetsLength = assetsMapped.length;
        address[] memory strategyAssets = new address[](assetsLength);
        uint256[] memory strategyAmounts = new uint256[](assetsLength);

        // For each vault collateral asset
        for (uint256 i = 0; i < assetsLength; ++i) {
            strategyAssets[i] = assetsMapped[i];
            // Get the asset balance in this strategy contract
            strategyAmounts[i] = IERC20(strategyAssets[i]).balanceOf(
                address(this)
            );
        }
        _deposit(strategyAssets, strategyAmounts);
    }

    /*
     * _deposit doesn't require a read-only re-entrancy protection since during the deposit
     * the function enters the Balancer Vault Context. If this function were called as part of
     * the attacking contract (while intercepting execution flow upon receiving ETH) the read-only
     * protection of the Balancer Vault would be triggered. Since the attacking contract would
     * already be in the Balancer Vault context and wouldn't be able to enter it again.
     */
    function _deposit(
        address[] memory _strategyAssets,
        uint256[] memory _strategyAmounts
    ) internal {
        require(
            _strategyAssets.length == _strategyAmounts.length,
            "Array length missmatch"
        );

        (IERC20[] memory tokens, , ) = balancerVault.getPoolTokens(
            balancerPoolId
        );

        uint256[] memory strategyAssetAmountsToPoolAssetAmounts = new uint256[](
            _strategyAssets.length
        );
        address[] memory strategyAssetsToPoolAssets = new address[](
            _strategyAssets.length
        );

        for (uint256 i = 0; i < _strategyAssets.length; ++i) {
            address strategyAsset = _strategyAssets[i];
            uint256 strategyAmount = _strategyAmounts[i];

            require(
                assetToPToken[strategyAsset] != address(0),
                "Unsupported asset"
            );
            strategyAssetsToPoolAssets[i] = _toPoolAsset(strategyAsset);

            if (strategyAmount > 0) {
                emit Deposit(strategyAsset, platformAddress, strategyAmount);

                // wrap rebasing assets like stETH and frxETH to wstETH and sfrxETH
                (, strategyAssetAmountsToPoolAssetAmounts[i]) = _wrapPoolAsset(
                    strategyAsset,
                    strategyAmount
                );
            }
        }

        uint256[] memory amountsIn = new uint256[](tokens.length);
        address[] memory poolAssets = new address[](tokens.length);
        for (uint256 i = 0; i < tokens.length; ++i) {
            // Convert IERC20 type to address
            poolAssets[i] = address(tokens[i]);

            // For each of the mapped assets
            for (uint256 j = 0; j < strategyAssetsToPoolAssets.length; ++j) {
                // If the pool asset is the same as the mapped asset
                if (poolAssets[i] == strategyAssetsToPoolAssets[j]) {
                    amountsIn[i] = strategyAssetAmountsToPoolAssetAmounts[j];
                }
            }
        }

        uint256 minBPT = _getBPTExpected(
            strategyAssetsToPoolAssets,
            strategyAssetAmountsToPoolAssetAmounts
        );
        uint256 minBPTwDeviation = minBPT.mulTruncate(
            1e18 - maxDepositDeviation
        );

        /* EXACT_TOKENS_IN_FOR_BPT_OUT:
         * User sends precise quantities of tokens, and receives an
         * estimated but unknown (computed at run time) quantity of BPT.
         *
         * ['uint256', 'uint256[]', 'uint256']
         * [EXACT_TOKENS_IN_FOR_BPT_OUT, amountsIn, minimumBPT]
         */
        bytes memory userData = abi.encode(
            IBalancerVault.WeightedPoolJoinKind.EXACT_TOKENS_IN_FOR_BPT_OUT,
            amountsIn,
            minBPTwDeviation
        );

        IBalancerVault.JoinPoolRequest memory request = IBalancerVault
            .JoinPoolRequest(poolAssets, amountsIn, userData, false);

        // Add the pool assets in this strategy to the balancer pool
        balancerVault.joinPool(
            balancerPoolId,
            address(this),
            address(this),
            request
        );

        // Deposit the Balancer Pool Tokens (BPT) into Aura
        _lpDepositAll();
    }

    /**
     * @notice Withdraw a Vault collateral asset from the Balancer pool.
     * @param _recipient Address to receive the Vault collateral assets. Typically is the Vault.
     * @param _strategyAsset Address of the Vault collateral asset
     * @param _strategyAmount The amount of Vault collateral assets to withdraw
     */
    function withdraw(
        address _recipient,
        address _strategyAsset,
        uint256 _strategyAmount
    ) external override onlyVault nonReentrant {
        address[] memory strategyAssets = new address[](1);
        uint256[] memory strategyAmounts = new uint256[](1);
        strategyAssets[0] = _strategyAsset;
        strategyAmounts[0] = _strategyAmount;

        _withdraw(_recipient, strategyAssets, strategyAmounts);
    }

    /**
     * @notice Withdraw multiple Vault collateral asset from the Balancer pool.
     * @param _recipient Address to receive the Vault collateral assets. Typically is the Vault.
     * @param _strategyAssets Addresses of the Vault collateral assets
     * @param _strategyAmounts The amounts of Vault collateral assets to withdraw
     */
    function withdraw(
        address _recipient,
        address[] calldata _strategyAssets,
        uint256[] calldata _strategyAmounts
    ) external onlyVault nonReentrant {
        _withdraw(_recipient, _strategyAssets, _strategyAmounts);
    }

    /**
     * @dev Withdraw multiple Vault collateral asset from the Balancer pool.
     * @param _recipient Address to receive the Vault collateral assets. Typically is the Vault.
     * @param _strategyAssets Addresses of the Vault collateral assets
     * @param _strategyAmounts The amounts of Vault collateral assets to withdraw
     *
     * _withdrawal doesn't require a read-only re-entrancy protection since during the withdrawal
     * the function enters the Balancer Vault Context. If this function were called as part of
     * the attacking contract (while intercepting execution flow upon receiving ETH) the read-only
     * protection of the Balancer Vault would be triggered. Since the attacking contract would
     * already be in the Balancer Vault context and wouldn't be able to enter it again.
     */
    function _withdraw(
        address _recipient,
        address[] memory _strategyAssets,
        uint256[] memory _strategyAmounts
    ) internal {
        require(
            _strategyAssets.length == _strategyAmounts.length,
            "Invalid input arrays"
        );

        for (uint256 i = 0; i < _strategyAssets.length; ++i) {
            require(
                assetToPToken[_strategyAssets[i]] != address(0),
                "Unsupported asset"
            );
        }

        // STEP 1 - Calculate the Balancer pool assets and amounts from the vault collateral assets

        // Get all the supported balancer pool assets
        (IERC20[] memory tokens, , ) = balancerVault.getPoolTokens(
            balancerPoolId
        );
        // Calculate the balancer pool assets and amounts to withdraw
        uint256[] memory poolAssetsAmountsOut = new uint256[](tokens.length);
        address[] memory poolAssets = new address[](tokens.length);
        // Is the wrapped asset amount indexed by the assets array, not the order of the Balancer pool tokens
        // eg wstETH and sfrxETH amounts, not the stETH and frxETH amounts
        uint256[] memory strategyAssetsToPoolAssetsAmounts = new uint256[](
            _strategyAssets.length
        );

        // For each of the Balancer pool assets
        for (uint256 i = 0; i < tokens.length; ++i) {
            poolAssets[i] = address(tokens[i]);

            // Convert the Balancer pool asset back to a vault collateral asset
            address strategyAsset = _fromPoolAsset(poolAssets[i]);

            // for each of the vault assets
            for (uint256 j = 0; j < _strategyAssets.length; ++j) {
                // If the vault asset equals the vault asset mapped from the Balancer pool asset
                if (_strategyAssets[j] == strategyAsset) {
                    (, poolAssetsAmountsOut[i]) = _toPoolAsset(
                        strategyAsset,
                        _strategyAmounts[j]
                    );
                    strategyAssetsToPoolAssetsAmounts[j] = poolAssetsAmountsOut[
                        i
                    ];

                    /* Because of the potential Balancer rounding error mentioned below
                     * the contract might receive 1-2 WEI smaller amount than required
                     * in the withdraw user data encoding. If slightly lesser token amount
                     * is received the strategy can not unwrap the pool asset as it is
                     * smaller than expected.
                     *
                     * For that reason we `overshoot` the required tokens expected to
                     * circumvent the error
                     */
                    if (poolAssetsAmountsOut[i] > 0) {
                        poolAssetsAmountsOut[i] += 2;
                    }
                }
            }
        }

        // STEP 2 - Calculate the max about of Balancer Pool Tokens (BPT) to withdraw

        // Estimate the required amount of Balancer Pool Tokens (BPT) for the assets
        uint256 maxBPTtoWithdraw = _getBPTExpected(
            poolAssets,
            /* all non 0 values are overshot by 2 WEI and with the expected mainnet
             * ~1% withdrawal deviation, the 2 WEI aren't important
             */
            poolAssetsAmountsOut
        );
        // Increase BPTs by the max allowed deviation
        // Any excess BPTs will be left in this strategy contract
        maxBPTtoWithdraw = maxBPTtoWithdraw.mulTruncate(
            1e18 + maxWithdrawalDeviation
        );

        // STEP 3  - Withdraw the Balancer Pool Tokens (BPT) from Aura to this strategy contract

        // Withdraw BPT from Aura allowing for BPTs left in this strategy contract from previous withdrawals
        _lpWithdraw(
            maxBPTtoWithdraw - IERC20(platformAddress).balanceOf(address(this))
        );

        // STEP 4 - Withdraw the balancer pool assets from the pool

        /* Custom asset exit: BPT_IN_FOR_EXACT_TOKENS_OUT:
         * User sends an estimated but unknown (computed at run time) quantity of BPT,
         * and receives precise quantities of specified tokens.
         *
         * ['uint256', 'uint256[]', 'uint256']
         * [BPT_IN_FOR_EXACT_TOKENS_OUT, amountsOut, maxBPTAmountIn]
         */
        bytes memory userData = abi.encode(
            IBalancerVault.WeightedPoolExitKind.BPT_IN_FOR_EXACT_TOKENS_OUT,
            poolAssetsAmountsOut,
            maxBPTtoWithdraw
        );

        IBalancerVault.ExitPoolRequest memory request = IBalancerVault
            .ExitPoolRequest(
                poolAssets,
                /* We specify the exact amount of a tokens we are expecting in the encoded
                 * userData, for that reason we don't need to specify the amountsOut here.
                 *
                 * Also Balancer has a rounding issue that can make a transaction fail:
                 * https://github.com/balancer/balancer-v2-monorepo/issues/2541
                 * which is an extra reason why this field is empty.
                 */
                new uint256[](tokens.length),
                userData,
                false
            );

        balancerVault.exitPool(
            balancerPoolId,
            address(this),
            /* Payable keyword is required because of the IBalancerVault interface even though
             * this strategy shall never be receiving native ETH
             */
            payable(address(this)),
            request
        );

        // STEP 5 - Re-deposit any left over BPT tokens back into Aura
        /* When concluding how much of BPT we need to withdraw from Aura we overshoot by
         * roughly around 1% (initial mainnet setting of maxWithdrawalDeviation). After exiting
         * the pool strategy could have left over BPT tokens that are not earning boosted yield.
         * We re-deploy those back in.
         */
        _lpDepositAll();

        // STEP 6 - Unswap balancer pool assets to vault collateral assets and send to the vault.

        // For each of the specified assets
        for (uint256 i = 0; i < _strategyAssets.length; ++i) {
            // Unwrap assets like wstETH and sfrxETH to rebasing assets stETH and frxETH
            if (strategyAssetsToPoolAssetsAmounts[i] > 0) {
                _unwrapPoolAsset(
                    _strategyAssets[i],
                    strategyAssetsToPoolAssetsAmounts[i]
                );
            }

            // Transfer the vault collateral assets to the recipient, which is typically the vault
            if (_strategyAmounts[i] > 0) {
                IERC20(_strategyAssets[i]).safeTransfer(
                    _recipient,
                    _strategyAmounts[i]
                );

                emit Withdrawal(
                    _strategyAssets[i],
                    platformAddress,
                    _strategyAmounts[i]
                );
            }
        }
    }

    /**
     * @notice Withdraws all supported Vault collateral assets from the Balancer pool
     * and send to the OToken's Vault.
     *
     * Is only executable by the OToken's Vault or the Governor.
     */
    function withdrawAll() external override onlyVaultOrGovernor nonReentrant {
        // STEP 1 - Withdraw all Balancer Pool Tokens (BPT) from Aura to this strategy contract

        _lpWithdrawAll();
        // Get the BPTs withdrawn from Aura plus any that were already in this strategy contract
        uint256 BPTtoWithdraw = IERC20(platformAddress).balanceOf(
            address(this)
        );
        // Get the balancer pool assets and their total balances
        (IERC20[] memory tokens, , ) = balancerVault.getPoolTokens(
            balancerPoolId
        );
        uint256[] memory minAmountsOut = new uint256[](tokens.length);
        address[] memory poolAssets = new address[](tokens.length);
        for (uint256 i = 0; i < tokens.length; ++i) {
            poolAssets[i] = address(tokens[i]);
        }

        // STEP 2 - Withdraw the Balancer pool assets from the pool
        /* Proportional exit: EXACT_BPT_IN_FOR_TOKENS_OUT:
         * User sends a precise quantity of BPT, and receives an estimated but unknown
         * (computed at run time) quantity of a single token
         *
         * ['uint256', 'uint256']
         * [EXACT_BPT_IN_FOR_TOKENS_OUT, bptAmountIn]
         *
         * It is ok to pass an empty minAmountsOut since tilting the pool in any direction
         * when doing a proportional exit can only be beneficial to the strategy. Since
         * it will receive more of the underlying tokens for the BPT traded in.
         */
        bytes memory userData = abi.encode(
            IBalancerVault.WeightedPoolExitKind.EXACT_BPT_IN_FOR_TOKENS_OUT,
            BPTtoWithdraw
        );

        IBalancerVault.ExitPoolRequest memory request = IBalancerVault
            .ExitPoolRequest(poolAssets, minAmountsOut, userData, false);

        balancerVault.exitPool(
            balancerPoolId,
            address(this),
            /* Payable keyword is required because of the IBalancerVault interface even though
             * this strategy shall never be receiving native ETH
             */
            payable(address(this)),
            request
        );

        // STEP 3 - Convert the balancer pool assets to the vault collateral assets and send to the vault
        // For each of the Balancer pool assets
        for (uint256 i = 0; i < tokens.length; ++i) {
            address poolAsset = address(tokens[i]);
            // Convert the balancer pool asset to the strategy asset
            address strategyAsset = _fromPoolAsset(poolAsset);
            // Get the balancer pool assets withdraw from the pool plus any that were already in this strategy contract
            uint256 poolAssetAmount = IERC20(poolAsset).balanceOf(
                address(this)
            );

            // Unwrap assets like wstETH and sfrxETH to rebasing assets stETH and frxETH
            uint256 unwrappedAmount = 0;
            if (poolAssetAmount > 0) {
                unwrappedAmount = _unwrapPoolAsset(
                    strategyAsset,
                    poolAssetAmount
                );
            }

            // Transfer the vault collateral assets to the vault
            if (unwrappedAmount > 0) {
                IERC20(strategyAsset).safeTransfer(
                    vaultAddress,
                    unwrappedAmount
                );
                emit Withdrawal(
                    strategyAsset,
                    platformAddress,
                    unwrappedAmount
                );
            }
        }
    }

    /**
     * @notice Approves the Balancer Vault to transfer poolAsset counterparts
     * of all of the supported assets from this strategy. E.g. stETH is a supported
     * strategy and Balancer Vault gets unlimited approval to transfer wstETH.
     *
     * If Balancer pool uses a wrapped version of a supported asset then also approve
     * unlimited usage of an asset to the contract responsible for wrapping.
     *
     * Approve unlimited spending by Balancer Vault and Aura reward pool of the
     * pool BPT tokens.
     *
     * Is only executable by the Governor.
     */
    function safeApproveAllTokens()
        external
        override
        onlyGovernor
        nonReentrant
    {
        uint256 assetCount = assetsMapped.length;
        for (uint256 i = 0; i < assetCount; ++i) {
            _abstractSetPToken(assetsMapped[i], platformAddress);
        }
        _approveBase();
    }

    // solhint-disable-next-line no-unused-vars
    function _abstractSetPToken(address _asset, address) internal override {
        address poolAsset = _toPoolAsset(_asset);
        if (_asset == stETH) {
            // slither-disable-next-line unused-return
            IERC20(stETH).approve(wstETH, type(uint256).max);
        } else if (_asset == frxETH) {
            // slither-disable-next-line unused-return
            IERC20(frxETH).approve(sfrxETH, type(uint256).max);
        }
        _approveAsset(poolAsset);
    }

    /**
     * @dev Approves the Balancer Vault to transfer an asset from
     * this strategy. The assets could be a Vault collateral asset
     * like WETH or rETH; or a Balancer pool asset that wraps the vault asset
     * like wstETH or sfrxETH.
     */
    function _approveAsset(address _asset) internal {
        IERC20 asset = IERC20(_asset);
        // slither-disable-next-line unused-return
        asset.approve(address(balancerVault), type(uint256).max);
    }

    /**
     * @notice Returns the rate supplied by the Balancer configured rate
     * provider. Rate is used to normalize the token to common underlying
     * pool denominator. (ETH for ETH Liquid staking derivatives)
     *
     * @param _asset Address of the Balancer pool asset
     * @return rate of the corresponding asset
     */
    function _getRateProviderRate(address _asset)
        internal
        view
        override
        returns (uint256)
    {
        IMetaStablePool pool = IMetaStablePool(platformAddress);
        IRateProvider[] memory providers = pool.getRateProviders();
        (IERC20[] memory tokens, , ) = balancerVault.getPoolTokens(
            balancerPoolId
        );

        uint256 providersLength = providers.length;
        for (uint256 i = 0; i < providersLength; ++i) {
            // _assets and corresponding rate providers are all in the same order
            if (address(tokens[i]) == _asset) {
                // rate provider doesn't exist, defaults to 1e18
                if (address(providers[i]) == address(0)) {
                    return 1e18;
                }
                return providers[i].getRate();
            }
        }

        // should never happen
        assert(false);
    }
}
","keccak256":"0x5932eef6052255795cee1cafad75d0018260c006b5cc299995f29460229d6382","license":"MIT"},"contracts/strategies/balancer/BaseAuraStrategy.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
 * @title OETH Base Balancer Abstract Strategy
 * @author Origin Protocol Inc
 */

import { BaseBalancerStrategy } from "./BaseBalancerStrategy.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20 } from "../../utils/InitializableAbstractStrategy.sol";
import { IERC4626 } from "../../../lib/openzeppelin/interfaces/IERC4626.sol";
import { StableMath } from "../../utils/StableMath.sol";
import { IRewardStaking } from "../IRewardStaking.sol";

abstract contract BaseAuraStrategy is BaseBalancerStrategy {
    using SafeERC20 for IERC20;
    using StableMath for uint256;

    /// @notice Address of the Aura rewards pool
    address public immutable auraRewardPoolAddress;

    // renamed from __reserved to not shadow BaseBalancerStrategy.__reserved,
    int256[50] private __reserved_baseAuraStrategy;

    constructor(address _auraRewardPoolAddress) {
        auraRewardPoolAddress = _auraRewardPoolAddress;
    }

    /**
     * @dev Deposit all Balancer Pool Tokens (BPT) in this strategy contract
     * to the Aura rewards pool.
     */
    function _lpDepositAll() internal virtual override {
        uint256 bptBalance = IERC20(platformAddress).balanceOf(address(this));
        uint256 auraLp = IERC4626(auraRewardPoolAddress).deposit(
            bptBalance,
            address(this)
        );
        require(bptBalance == auraLp, "Aura LP != BPT");
    }

    /**
     * @dev Withdraw `numBPTTokens` Balancer Pool Tokens (BPT) from
     * the Aura rewards pool to this strategy contract.
     * @param numBPTTokens Number of Balancer Pool Tokens (BPT) to withdraw
     */
    function _lpWithdraw(uint256 numBPTTokens) internal virtual override {
        IRewardStaking(auraRewardPoolAddress).withdrawAndUnwrap(
            numBPTTokens,
            true // also claim reward tokens
        );
    }

    /**
     * @dev Withdraw all Balancer Pool Tokens (BPT) from
     * the Aura rewards pool to this strategy contract.
     */
    function _lpWithdrawAll() internal virtual override {
        // Get all the strategy's BPTs in Aura
        // maxRedeem is implemented as balanceOf(address) in Aura
        uint256 bptBalance = IERC4626(auraRewardPoolAddress).maxRedeem(
            address(this)
        );

        IRewardStaking(auraRewardPoolAddress).withdrawAndUnwrap(
            bptBalance,
            true // also claim reward tokens
        );
    }

    /**
     * @notice Collects BAL and AURA tokens from the rewards pool.
     */
    function collectRewardTokens()
        external
        virtual
        override
        onlyHarvester
        nonReentrant
    {
        /* Similar to Convex, calling this function collects both of the
         * accrued BAL and AURA tokens.
         */
        IRewardStaking(auraRewardPoolAddress).getReward();
        _collectRewardTokens();
    }

    /// @notice Balancer Pool Tokens (BPT) in the Balancer pool and the Aura rewards pool.
    function _getBalancerPoolTokens()
        internal
        view
        override
        returns (uint256 balancerPoolTokens)
    {
        balancerPoolTokens =
            IERC20(platformAddress).balanceOf(address(this)) +
            // maxRedeem is implemented as balanceOf(address) in Aura
            IERC4626(auraRewardPoolAddress).maxRedeem(address(this));
    }

    function _approveBase() internal virtual override {
        super._approveBase();

        IERC20 pToken = IERC20(platformAddress);
        pToken.safeApprove(auraRewardPoolAddress, type(uint256).max);
    }
}
","keccak256":"0x05d2992e46040e4c3363761a4615dc290de27c3cdbcc3e0052ec970e3ca1d41d","license":"MIT"},"contracts/strategies/balancer/BaseBalancerStrategy.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
 * @title OETH Base Balancer Abstract Strategy
 * @author Origin Protocol Inc
 */
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20, InitializableAbstractStrategy } from "../../utils/InitializableAbstractStrategy.sol";
import { IBalancerVault } from "../../interfaces/balancer/IBalancerVault.sol";
import { IRateProvider } from "../../interfaces/balancer/IRateProvider.sol";
import { VaultReentrancyLib } from "./VaultReentrancyLib.sol";
import { IOracle } from "../../interfaces/IOracle.sol";
import { IWstETH } from "../../interfaces/IWstETH.sol";
import { IERC4626 } from "../../../lib/openzeppelin/interfaces/IERC4626.sol";
import { StableMath } from "../../utils/StableMath.sol";

abstract contract BaseBalancerStrategy is InitializableAbstractStrategy {
    using SafeERC20 for IERC20;
    using StableMath for uint256;

    address public immutable rETH;
    address public immutable stETH;
    address public immutable wstETH;
    address public immutable frxETH;
    address public immutable sfrxETH;

    /// @notice Address of the Balancer vault
    IBalancerVault public immutable balancerVault;
    /// @notice Balancer pool identifier
    bytes32 public immutable balancerPoolId;

    // Max withdrawal deviation denominated in 1e18 (1e18 == 100%)
    uint256 public maxWithdrawalDeviation;
    // Max deposit deviation denominated in 1e18 (1e18 == 100%)
    uint256 public maxDepositDeviation;

    int256[48] private __reserved;

    struct BaseBalancerConfig {
        address rEthAddress; // Address of the rETH token
        address stEthAddress; // Address of the stETH token
        address wstEthAddress; // Address of the wstETH token
        address frxEthAddress; // Address of the frxEth token
        address sfrxEthAddress; // Address of the sfrxEth token
        address balancerVaultAddress; // Address of the Balancer vault
        bytes32 balancerPoolId; // Balancer pool identifier
    }

    event MaxWithdrawalDeviationUpdated(
        uint256 _prevMaxDeviationPercentage,
        uint256 _newMaxDeviationPercentage
    );
    event MaxDepositDeviationUpdated(
        uint256 _prevMaxDeviationPercentage,
        uint256 _newMaxDeviationPercentage
    );

    /**
     * @dev Ensure we are not in a Vault context when this function is called, by attempting a no-op internal
     * balance operation. If we are already in a Vault transaction (e.g., a swap, join, or exit), the Vault's
     * reentrancy protection will cause this function to revert.
     *
     * Use this modifier with any function that can cause a state change in a pool and is either public itself,
     * or called by a public function *outside* a Vault operation (e.g., join, exit, or swap).
     *
     * This is to protect against Balancer's read-only re-entrancy vulnerability:
     * https://www.notion.so/originprotocol/Balancer-read-only-reentrancy-c686e72c82414ef18fa34312bb02e11b
     */
    modifier whenNotInBalancerVaultContext() {
        VaultReentrancyLib.ensureNotInVaultContext(balancerVault);
        _;
    }

    constructor(BaseBalancerConfig memory _balancerConfig) {
        rETH = _balancerConfig.rEthAddress;
        stETH = _balancerConfig.stEthAddress;
        wstETH = _balancerConfig.wstEthAddress;
        frxETH = _balancerConfig.frxEthAddress;
        sfrxETH = _balancerConfig.sfrxEthAddress;

        balancerVault = IBalancerVault(_balancerConfig.balancerVaultAddress);
        balancerPoolId = _balancerConfig.balancerPoolId;
    }

    /**
     * Initializer for setting up strategy internal state. This overrides the
     * InitializableAbstractStrategy initializer as Balancer's strategies don't fit
     * well within that abstraction.
     * @param _rewardTokenAddresses Address of BAL & AURA
     * @param _assets Addresses of supported assets. MUST be passed in the same
     *                order as returned by coins on the pool contract, i.e.
     *                WETH, stETH
     * @param _pTokens Platform Token corresponding addresses
     */
    function initialize(
        address[] calldata _rewardTokenAddresses, // BAL & AURA
        address[] calldata _assets,
        address[] calldata _pTokens
    ) external override onlyGovernor initializer {
        maxWithdrawalDeviation = 1e16;
        maxDepositDeviation = 1e16;

        emit MaxWithdrawalDeviationUpdated(0, maxWithdrawalDeviation);
        emit MaxDepositDeviationUpdated(0, maxDepositDeviation);

        IERC20[] memory poolAssets = _getPoolAssets();
        require(
            poolAssets.length == _assets.length,
            "Pool assets length mismatch"
        );
        for (uint256 i = 0; i < _assets.length; ++i) {
            address asset = _fromPoolAsset(address(poolAssets[i]));
            require(_assets[i] == asset, "Pool assets mismatch");
        }

        super._initialize(_rewardTokenAddresses, _assets, _pTokens);
        _approveBase();
    }

    /**
     * @notice Returns bool indicating whether asset is supported by strategy
     * @param _asset Address of the asset
     */
    function supportsAsset(address _asset)
        external
        view
        override
        returns (bool)
    {
        return assetToPToken[_asset] != address(0);
    }

    /**
     * @notice Get strategy's share of an assets in the Balancer pool.
     * This is not denominated in OUSD/ETH value of the assets in the Balancer pool.
     * @param _asset  Address of the Vault collateral asset
     * @return amount  the amount of vault collateral assets
     *
     * IMPORTANT if this function is overridden it needs to have a whenNotInBalancerVaultContext
     * modifier on it or it is susceptible to read-only re-entrancy attack
     *
     * @dev it is important that this function is not affected by reporting inflated
     * values of assets in case of any pool manipulation. Such a manipulation could easily
     * exploit the protocol by:
     *  - minting OETH
     *  - tilting Balancer pool to report higher balances of assets
     *  - rebasing() -> all that extra token balances get distributed to OETH holders
     *  - tilting pool back
     *  - redeeming OETH
     */
    function checkBalance(address _asset)
        external
        view
        virtual
        override
        whenNotInBalancerVaultContext
        returns (uint256 amount)
    {
        require(assetToPToken[_asset] != address(0), "Unsupported asset");

        uint256 bptBalance = _getBalancerPoolTokens();

        /* To calculate the worth of queried asset:
         *  - assume that all tokens normalized to their ETH value have an equal split balance
         *    in the pool when it is balanced
         *  - multiply the BPT amount with the bpt rate to get the ETH denominated amount
         *    of strategy's holdings
         *  - divide that by the number of tokens we support in the pool to get ETH denominated
         *    amount that is applicable to each supported token in the pool.
         *
         *    It would be possible to support only 1 asset in the pool (and be exposed to all
         *    the assets while holding BPT tokens) and deposit/withdraw/checkBalance using only
         *    that asset. TBD: changes to other functions still required if we ever decide to
         *    go with such configuration.
         */
        amount = (bptBalance.mulTruncate(
            IRateProvider(platformAddress).getRate()
        ) / assetsMapped.length);

        /* If the pool asset is equal to (strategy )_asset it means that a rate
         * provider for that asset exists and that asset is not necessarily
         * pegged to a unit (ETH).
         *
         * Because this function returns the balance of the asset and is not denominated in
         * ETH units we need to convert the ETH denominated amount to asset amount.
         */
        if (_toPoolAsset(_asset) == _asset) {
            amount = amount.divPrecisely(_getRateProviderRate(_asset));
        }
    }

    /**
     * @notice Returns the value of all assets managed by this strategy.
     * Uses the Balancer pool's rate (virtual price) to convert the strategy's
     * Balancer Pool Tokens (BPT) to ETH value.
     * @return value The ETH value
     *
     * IMPORTANT if this function is overridden it needs to have a whenNotInBalancerVaultContext
     * modifier on it or it is susceptible to read-only re-entrancy attack
     */
    function checkBalance()
        external
        view
        virtual
        whenNotInBalancerVaultContext
        returns (uint256 value)
    {
        uint256 bptBalance = _getBalancerPoolTokens();

        // Convert BPT to ETH value
        value = bptBalance.mulTruncate(
            IRateProvider(platformAddress).getRate()
        );
    }

    /// @notice Balancer Pool Tokens (BPT) in the Balancer pool.
    function _getBalancerPoolTokens()
        internal
        view
        virtual
        returns (uint256 balancerPoolTokens)
    {
        balancerPoolTokens = IERC20(platformAddress).balanceOf(address(this));
    }

    /* solhint-disable max-line-length */
    /**
     * @notice BPT price is calculated by taking the rate from the rateProvider of the asset in
     * question. If one does not exist it defaults to 1e18. To get the final BPT expected that
     * is multiplied by the underlying asset amount divided by BPT token rate. BPT token rate is
     * similar to Curve's virtual_price and expresses how much has the price of BPT appreciated
     * (e.g. due to swap fees) in relation to the underlying assets
     *
     * Using the above approach makes the strategy vulnerable to a possible MEV attack using
     * flash loan to manipulate the pool before a deposit/withdrawal since the function ignores
     * market values of the assets being priced in BPT.
     *
     * At the time of writing there is no safe on-chain approach to pricing BPT in a way that it
     * would make it invulnerable to MEV pool manipulation. See recent Balancer exploit:
     * https://www.notion.so/originprotocol/Balancer-OETH-strategy-9becdea132704e588782a919d7d471eb?pvs=4#1cf07de12fc64f1888072321e0644348
     *
     * To mitigate MEV possibilities during deposits and withdraws, the VaultValueChecker will use checkBalance before and after the move
     * to ensure the expected changes took place.
     *
     * @param _asset Address of the Balancer pool asset
     * @param _amount Amount of the Balancer pool asset
     * @return bptExpected of BPT expected in exchange for the asset
     *
     * @dev
     * bptAssetPrice = 1e18 (asset peg) * pool_asset_rate
     *
     * bptExpected = bptAssetPrice * asset_amount / BPT_token_rate
     *
     * bptExpected = 1e18 (asset peg) * pool_asset_rate * asset_amount / BPT_token_rate
     * bptExpected = asset_amount * pool_asset_rate / BPT_token_rate
     *
     * further information available here:
     * https://www.notion.so/originprotocol/Balancer-OETH-strategy-9becdea132704e588782a919d7d471eb?pvs=4#ce01495ae70346d8971f5dced809fb83
     */
    /* solhint-enable max-line-length */
    function _getBPTExpected(address _asset, uint256 _amount)
        internal
        view
        virtual
        returns (uint256 bptExpected)
    {
        uint256 bptRate = IRateProvider(platformAddress).getRate();
        uint256 poolAssetRate = _getRateProviderRate(_asset);
        bptExpected = _amount.mulTruncate(poolAssetRate).divPrecisely(bptRate);
    }

    function _getBPTExpected(
        address[] memory _assets,
        uint256[] memory _amounts
    ) internal view virtual returns (uint256 bptExpected) {
        require(_assets.length == _amounts.length, "Assets & amounts mismatch");

        for (uint256 i = 0; i < _assets.length; ++i) {
            uint256 poolAssetRate = _getRateProviderRate(_assets[i]);
            // convert asset amount to ETH amount
            bptExpected += _amounts[i].mulTruncate(poolAssetRate);
        }

        uint256 bptRate = IRateProvider(platformAddress).getRate();
        // Convert ETH amount to BPT amount
        bptExpected = bptExpected.divPrecisely(bptRate);
    }

    function _lpDepositAll() internal virtual;

    function _lpWithdraw(uint256 numBPTTokens) internal virtual;

    function _lpWithdrawAll() internal virtual;

    /**
     * @notice Balancer returns assets and rateProviders for corresponding assets ordered
     * by numerical order.
     */
    function _getPoolAssets() internal view returns (IERC20[] memory assets) {
        (assets, , ) = balancerVault.getPoolTokens(balancerPoolId);
    }

    /**
     * @dev If an asset is rebasing the Balancer pools have a wrapped versions of assets
     * that the strategy supports. This function converts the pool(wrapped) asset
     * and corresponding amount to strategy asset.
     */
    function _toPoolAsset(address asset, uint256 amount)
        internal
        view
        returns (address poolAsset, uint256 poolAmount)
    {
        if (asset == stETH) {
            poolAsset = wstETH;
            if (amount > 0) {
                poolAmount = IWstETH(wstETH).getWstETHByStETH(amount);
            }
        } else if (asset == frxETH) {
            poolAsset = sfrxETH;
            if (amount > 0) {
                poolAmount = IERC4626(sfrxETH).convertToShares(amount);
            }
        } else {
            poolAsset = asset;
            poolAmount = amount;
        }
    }

    /**
     * @dev Converts a Vault collateral asset to a Balancer pool asset.
     * stETH becomes wstETH, frxETH becomes sfrxETH and everything else stays the same.
     * @param asset Address of the Vault collateral asset.
     * @return Address of the Balancer pool asset.
     */
    function _toPoolAsset(address asset) internal view returns (address) {
        if (asset == stETH) {
            return wstETH;
        } else if (asset == frxETH) {
            return sfrxETH;
        }
        return asset;
    }

    /**
     * @dev Converts rebasing asset to its wrapped counterpart.
     */
    function _wrapPoolAsset(address asset, uint256 amount)
        internal
        returns (address wrappedAsset, uint256 wrappedAmount)
    {
        if (asset == stETH) {
            wrappedAsset = wstETH;
            if (amount > 0) {
                wrappedAmount = IWstETH(wstETH).wrap(amount);
            }
        } else if (asset == frxETH) {
            wrappedAsset = sfrxETH;
            if (amount > 0) {
                wrappedAmount = IERC4626(sfrxETH).deposit(
                    amount,
                    address(this)
                );
            }
        } else {
            wrappedAsset = asset;
            wrappedAmount = amount;
        }
    }

    /**
     * @dev Converts wrapped asset to its rebasing counterpart.
     */
    function _unwrapPoolAsset(address asset, uint256 amount)
        internal
        returns (uint256 unwrappedAmount)
    {
        if (asset == stETH) {
            unwrappedAmount = IWstETH(wstETH).unwrap(amount);
        } else if (asset == frxETH) {
            unwrappedAmount = IERC4626(sfrxETH).withdraw(
                amount,
                address(this),
                address(this)
            );
        } else {
            unwrappedAmount = amount;
        }
    }

    /**
     * @dev If an asset is rebasing the Balancer pools have a wrapped versions of assets
     * that the strategy supports. This function converts the rebasing strategy asset
     * and corresponding amount to wrapped(pool) asset.
     */
    function _fromPoolAsset(address poolAsset, uint256 poolAmount)
        internal
        view
        returns (address asset, uint256 amount)
    {
        if (poolAsset == wstETH) {
            asset = stETH;
            if (poolAmount > 0) {
                amount = IWstETH(wstETH).getStETHByWstETH(poolAmount);
            }
        } else if (poolAsset == sfrxETH) {
            asset = frxETH;
            if (poolAmount > 0) {
                amount = IERC4626(sfrxETH).convertToAssets(poolAmount);
            }
        } else {
            asset = poolAsset;
            amount = poolAmount;
        }
    }

    function _fromPoolAsset(address poolAsset)
        internal
        view
        returns (address asset)
    {
        if (poolAsset == wstETH) {
            asset = stETH;
        } else if (poolAsset == sfrxETH) {
            asset = frxETH;
        } else {
            asset = poolAsset;
        }
    }

    /**
     * @notice Sets max withdrawal deviation that is considered when removing
     * liquidity from Balancer pools.
     * @param _maxWithdrawalDeviation Max withdrawal deviation denominated in
     *        wad (number with 18 decimals): 1e18 == 100%, 1e16 == 1%
     *
     * IMPORTANT Minimum maxWithdrawalDeviation will be 1% (1e16) for production
     * usage. Vault value checker in combination with checkBalance will
     * catch any unexpected manipulation.
     */
    function setMaxWithdrawalDeviation(uint256 _maxWithdrawalDeviation)
        external
        onlyVaultOrGovernorOrStrategist
    {
        require(
            _maxWithdrawalDeviation <= 1e18,
            "Withdrawal dev. out of bounds"
        );
        emit MaxWithdrawalDeviationUpdated(
            maxWithdrawalDeviation,
            _maxWithdrawalDeviation
        );
        maxWithdrawalDeviation = _maxWithdrawalDeviation;
    }

    /**
     * @notice Sets max deposit deviation that is considered when adding
     * liquidity to Balancer pools.
     * @param _maxDepositDeviation Max deposit deviation denominated in
     *        wad (number with 18 decimals): 1e18 == 100%, 1e16 == 1%
     *
     * IMPORTANT Minimum maxDepositDeviation will default to 1% (1e16)
     * for production usage. Vault value checker in combination with
     * checkBalance will catch any unexpected manipulation.
     */
    function setMaxDepositDeviation(uint256 _maxDepositDeviation)
        external
        onlyVaultOrGovernorOrStrategist
    {
        require(_maxDepositDeviation <= 1e18, "Deposit dev. out of bounds");
        emit MaxDepositDeviationUpdated(
            maxDepositDeviation,
            _maxDepositDeviation
        );
        maxDepositDeviation = _maxDepositDeviation;
    }

    function _approveBase() internal virtual {
        IERC20 pToken = IERC20(platformAddress);
        // Balancer vault for BPT token (required for removing liquidity)
        pToken.safeApprove(address(balancerVault), type(uint256).max);
    }

    function _getRateProviderRate(address _asset)
        internal
        view
        virtual
        returns (uint256);
}
