contracts/oracle/ChainlinkOracle.sol.content:
pragma solidity 0.5.11;

/**
 * @title OUSD ChainlinkOracle Contract
 * @author Origin Protocol Inc
 */
import "./AggregatorV3Interface.sol";
import { IEthUsdOracle } from "../interfaces/IEthUsdOracle.sol";
import {
    InitializableGovernable
} from "../governance/InitializableGovernable.sol";

contract ChainlinkOracle is IEthUsdOracle, InitializableGovernable {
    address ethFeed;

    struct FeedConfig {
        address feed;
        uint8 decimals;
        bool directToUsd;
    }

    mapping(bytes32 => FeedConfig) feeds;

    uint8 ethDecimals;

    string constant ethSymbol = "ETH";
    bytes32 constant ethHash = keccak256(abi.encodePacked(ethSymbol));

    constructor(address ethFeed_) public {
        ethFeed = ethFeed_;
        ethDecimals = AggregatorV3Interface(ethFeed_).decimals();
    }

    function registerFeed(
        address feed,
        string memory symbol,
        bool directToUsd
    ) public onlyGovernor {
        FeedConfig storage config = feeds[keccak256(abi.encodePacked(symbol))];

        config.feed = feed;
        config.decimals = AggregatorV3Interface(feed).decimals();
        config.directToUsd = directToUsd;
    }

    function getLatestPrice(address feed) internal view returns (int256) {
        (
            uint80 roundID,
            int256 price,
            uint256 startedAt,
            uint256 timeStamp,
            uint80 answeredInRound
        ) = AggregatorV3Interface(feed).latestRoundData();
        // silence
        roundID;
        startedAt;
        timeStamp;
        answeredInRound;
        return price;
    }

    function ethUsdPrice() external view returns (uint256) {
        return (uint256(getLatestPrice(ethFeed)) /
            (uint256(10)**(ethDecimals - 6)));
    }

    function tokUsdPrice(string calldata symbol)
        external
        view
        returns (uint256)
    {
        bytes32 tokenSymbolHash = keccak256(abi.encodePacked(symbol));
        FeedConfig storage config = feeds[tokenSymbolHash];
        int256 tPrice = getLatestPrice(config.feed);

        require(config.directToUsd, "Price is not direct to usd");
        require(tPrice > 0, "Price must be greater than zero");
        return uint256(tPrice);
    }

    function tokEthPrice(string calldata symbol) external returns (uint256) {
        bytes32 tokenSymbolHash = keccak256(abi.encodePacked(symbol));
        FeedConfig storage config = feeds[tokenSymbolHash];
        int256 tPrice = getLatestPrice(config.feed);

        require(!config.directToUsd, "Price is not in terms of ETH");
        require(tPrice > 0, "Price must be greater than zero");
        //attempt to return 8 digit precision here
        return uint256(tPrice) / (uint256(10)**(config.decimals - 8));
    }

    // This actually calculate the latest price from outside oracles
    // It's a view but substantially more costly in terms of calculation
    function price(string calldata symbol) external view returns (uint256) {
        bytes32 tokenSymbolHash = keccak256(abi.encodePacked(symbol));

        if (ethHash == tokenSymbolHash) {
            return (uint256(getLatestPrice(ethFeed)) /
                (uint256(10)**(ethDecimals - 6)));
        } else {
            FeedConfig storage config = feeds[tokenSymbolHash];
            int256 tPrice = getLatestPrice(config.feed);

            if (config.directToUsd) {
                require(tPrice > 0, "Price must be greater than zero");
                return uint256(tPrice);
            } else {
                int256 ethPrice = getLatestPrice(ethFeed); // grab the eth price from the open oracle
                require(
                    tPrice > 0 && ethPrice > 0,
                    "Both eth and price must be greater than zero"
                );
                //not actually sure why it's 6 units here, this is just to match with openoracle for now
                return
                    mul(uint256(tPrice), uint256(ethPrice)) /
                    (uint256(10)**(ethDecimals + config.decimals - 6));
            }
        }
    }

    /// @dev Overflow proof multiplication
    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) return 0;
        uint256 c = a * b;
        require(c / a == b, "multiplication overflow");
        return c;
    }
}

contracts/oracle/MixOracle.sol.content:
pragma solidity 0.5.11;

/**
 * @title OUSD MixOracle Contract
 * @notice The MixOracle pulls exchange rate from multiple oracles and returns
 *         min and max values.
 * @author Origin Protocol Inc
 */
import { IPriceOracle } from "../interfaces/IPriceOracle.sol";
import { IEthUsdOracle } from "../interfaces/IEthUsdOracle.sol";
import { IMinMaxOracle } from "../interfaces/IMinMaxOracle.sol";
import {
    InitializableGovernable
} from "../governance/InitializableGovernable.sol";

contract MixOracle is IMinMaxOracle, InitializableGovernable {
    address[] public ethUsdOracles;

    struct MixConfig {
        address[] usdOracles;
        address[] ethOracles;
    }

    mapping(bytes32 => MixConfig) configs;

    uint256 constant MAX_INT = 2**256 - 1;
    uint256 public maxDrift;
    uint256 public minDrift;

    constructor(uint256 _maxDrift, uint256 _minDrift) public {
        maxDrift = _maxDrift;
        minDrift = _minDrift;
    }

    function setMinMaxDrift(uint256 _maxDrift, uint256 _minDrift)
        public
        onlyGovernor
    {
        maxDrift = _maxDrift;
        minDrift = _minDrift;
    }

    /**
     * @notice Adds an oracle to the list of oracles to pull data from.
     * @param oracle Address of an oracle that implements the IEthUsdOracle interface.
     **/
    function registerEthUsdOracle(address oracle) public onlyGovernor {
        for (uint256 i = 0; i < ethUsdOracles.length; i++) {
            require(ethUsdOracles[i] != oracle, "Oracle already registered.");
        }
        ethUsdOracles.push(oracle);
    }

    /**
     * @notice Removes an oracle to the list of oracles to pull data from.
     * @param oracle Address of an oracle that implements the IEthUsdOracle interface.
     **/
    function unregisterEthUsdOracle(address oracle) public onlyGovernor {
        for (uint256 i = 0; i < ethUsdOracles.length; i++) {
          if (ethUsdOracles[i] == oracle) {
            // swap with the last element of the array, and then delete last element (could be itself)
            ethUsdOracles[i] = ethUsdOracles[ethUsdOracles.length - 1];
            delete ethUsdOracles[ethUsdOracles.length - 1];
            ethUsdOracles.length--;
            return;
          }
        }
        revert("Oracle not found");
    }

    /**
     * @notice Adds an oracle to the list of oracles to pull data from.
     * @param ethOracles Addresses of oracles that implements the IEthUsdOracle interface and answers for this asset
     * @param usdOracles Addresses of oracles that implements the IPriceOracle interface and answers for this asset
     **/
    function registerTokenOracles(
        string calldata symbol,
        address[] calldata ethOracles,
        address[] calldata usdOracles
    ) external onlyGovernor {
        MixConfig storage config = configs[keccak256(abi.encodePacked(symbol))];
        config.ethOracles = ethOracles;
        config.usdOracles = usdOracles;
    }

    /**
     * @notice Returns the min price of an asset in USD.
     * @return symbol Asset symbol. Example: "DAI"
     * @return price Min price from all the oracles, in USD with 8 decimal digits.
     **/
    function priceMin(string calldata symbol) external returns (uint256 price) {
        MixConfig storage config = configs[keccak256(abi.encodePacked(symbol))];
        uint256 ep;
        uint256 p; //holder variables
        price = MAX_INT;
        if (config.ethOracles.length > 0) {
            ep = MAX_INT;
            for (uint256 i = 0; i < config.ethOracles.length; i++) {
                p = IEthUsdOracle(config.ethOracles[i]).tokEthPrice(symbol);
                if (ep > p) {
                    ep = p;
                }
            }
            price = ep;
            ep = MAX_INT;
            for (uint256 i = 0; i < ethUsdOracles.length; i++) {
                p = IEthUsdOracle(ethUsdOracles[i]).ethUsdPrice();
                if (ep > p) {
                    ep = p;
                }
            }
            if (price != MAX_INT && ep != MAX_INT) {
                // tokEthPrice has precision of 8 which ethUsdPrice has precision of 6
                // we want precision of 8
                price = (price * ep) / 1e6;
            }
        }

        if (config.usdOracles.length > 0) {
            for (uint256 i = 0; i < config.usdOracles.length; i++) {
                // upscale by 2 since price oracles are precision 6
                p = IPriceOracle(config.usdOracles[i]).price(symbol) * 1e2;
                if (price > p) {
                    price = p;
                }
            }
        }
        require(price < maxDrift, "Price exceeds max value.");
        require(price > minDrift, "Price lower than min value.");
        require(
            price != MAX_INT,
            "None of our oracles returned a valid min price!"
        );
    }

    /**
     * @notice Returns max price of an asset in USD.
     * @return symbol Asset symbol. Example: "DAI"
     * @return price Max price from all the oracles, in USD with 8 decimal digits.
     **/
    function priceMax(string calldata symbol) external returns (uint256 price) {
        MixConfig storage config = configs[keccak256(abi.encodePacked(symbol))];
        uint256 ep;
        uint256 p; //holder variables
        price = 0;
        if (config.ethOracles.length > 0) {
            ep = 0;
            for (uint256 i = 0; i < config.ethOracles.length; i++) {
                p = IEthUsdOracle(config.ethOracles[i]).tokEthPrice(symbol);
                if (ep < p) {
                    ep = p;
                }
            }
            price = ep;
            ep = 0;
            for (uint256 i = 0; i < ethUsdOracles.length; i++) {
                p = IEthUsdOracle(ethUsdOracles[i]).ethUsdPrice();
                if (ep < p) {
                    ep = p;
                }
            }
            if (price != 0 && ep != 0) {
                // tokEthPrice has precision of 8 which ethUsdPrice has precision of 6
                // we want precision of 8
                price = (price * ep) / 1e6;
            }
        }

        if (config.usdOracles.length > 0) {
            for (uint256 i = 0; i < config.usdOracles.length; i++) {
                // upscale by 2 since price oracles are precision 6
                p = IPriceOracle(config.usdOracles[i]).price(symbol) * 1e2;
                if (price < p) {
                    price = p;
                }
            }
        }

        require(price < maxDrift, "Price above max value.");
        require(price > minDrift, "Price below min value.");
        require(price != 0, "None of our oracles returned a valid max price!");
    }
}

contracts/oracle/OpenUniswapOracle.sol.content:
pragma solidity 0.5.11;
pragma experimental ABIEncoderV2;

/**
 * @title OUSD OpenUniswapOracle Contract
 * @author Origin Protocol Inc
 */
import "./UniswapLib.sol";
import { IPriceOracle } from "../interfaces/IPriceOracle.sol";
import { IEthUsdOracle } from "../interfaces/IEthUsdOracle.sol";
import {
    InitializableGovernable
} from "../governance/InitializableGovernable.sol";

contract OpenUniswapOracle is IEthUsdOracle, InitializableGovernable {
    using FixedPoint for *;
    uint256 public constant PERIOD = 2 minutes;

    struct SwapConfig {
        bool ethOnFirst; // whether the weth is the first in pair
        address swap; // address of the uniswap pair
        uint256 blockTimestampLast;
        uint256 latestBlockTimestampLast;
        uint256 priceCumulativeLast;
        uint256 latestPriceCumulativeLast;
        uint256 baseUnit;
    }

    mapping(bytes32 => SwapConfig) swaps;

    IPriceOracle public ethPriceOracle; //price oracle for getting the Eth->USD price OPEN oracle..
    address ethToken;
    string constant ethSymbol = "ETH";
    bytes32 constant ethHash = keccak256(abi.encodePacked(ethSymbol));

    constructor(address ethPriceOracle_, address ethToken_) public {
        ethPriceOracle = IPriceOracle(ethPriceOracle_);
        ethToken = ethToken_;
    }

    function registerEthPriceOracle(address ethPriceOracle_) public onlyGovernor {
        ethPriceOracle = IPriceOracle(ethPriceOracle_);
    }

    function registerPair(address pair_) public onlyGovernor {
        IUniswapV2Pair pair = IUniswapV2Pair(pair_);
        address token;
        bool ethOnFirst = true;
        if (pair.token0() == ethToken) {
            token = pair.token1();
        } else {
            token = pair.token0();
            ethOnFirst = false;
        }
        SymboledERC20 st = SymboledERC20(token);
        string memory symbol = st.symbol();
        SwapConfig storage config = swaps[keccak256(abi.encodePacked(symbol))];

        // is the first token the eth Token
        config.ethOnFirst = ethOnFirst;
        config.swap = pair_;
        config.baseUnit = uint256(10)**st.decimals();

        // we want everything relative to first
        config.priceCumulativeLast = currentCumulativePrice(config);
        config.blockTimestampLast = block.timestamp;
        config.latestBlockTimestampLast = config.blockTimestampLast;
        config.latestPriceCumulativeLast = config.priceCumulativeLast;
    }

    function currentCumulativePrice(SwapConfig storage config)
        internal
        view
        returns (uint256)
    {
        (
            uint256 cumulativePrice0,
            uint256 cumulativePrice1,

        ) = UniswapV2OracleLibrary.currentCumulativePrices(config.swap);
        if (config.ethOnFirst) {
            return cumulativePrice1;
        } else {
            return cumulativePrice0;
        }
    }

    // This needs to be called regularly to update the pricing window
    function pokePriceWindow(SwapConfig storage config)
        internal
        returns (
            uint256,
            uint256,
            uint256
        )
    {
        uint256 priceCumulative = currentCumulativePrice(config);

        uint256 timeElapsed = block.timestamp - config.latestBlockTimestampLast;

        if (timeElapsed >= PERIOD) {
            config.blockTimestampLast = config.latestBlockTimestampLast;
            config.priceCumulativeLast = config.latestPriceCumulativeLast;

            config.latestBlockTimestampLast = block.timestamp;
            config.latestPriceCumulativeLast = priceCumulative;
        }

        return (
            priceCumulative,
            config.priceCumulativeLast,
            config.blockTimestampLast
        );
    }

    // update to the latest window
    function updatePriceWindows(bytes32[] calldata symbolHashes) external {
        for (uint256 i = 0; i < symbolHashes.length; i++) {
            SwapConfig storage config = swaps[symbolHashes[i]];
            pokePriceWindow(config);
        }
    }

    //eth to usd price
    //precision from open is 6
    function ethUsdPrice() external view returns (uint256) {
        return ethPriceOracle.price(ethSymbol); // grab the eth price from the open oracle
    }

    //tok to Usd price
    //Note: for USDC and USDT this is fixed to 1 on openoracle
    // precision here is 8
    function tokUsdPrice(string calldata symbol)
        external
        view
        returns (uint256)
    {
        return ethPriceOracle.price(symbol); // grab the eth price from the open oracle
    }

    //tok to Eth price
    function tokEthPrice(string calldata symbol) external returns (uint256) {
        bytes32 tokenSymbolHash = keccak256(abi.encodePacked(symbol));
        SwapConfig storage config = swaps[tokenSymbolHash];
        (
            uint256 priceCumulative,
            uint256 priceCumulativeLast,
            uint256 blockTimestampLast
        ) = pokePriceWindow(config);

        require(
            priceCumulative > priceCumulativeLast,
            "There has been no cumulative change"
        );
        // This should be impossible, but better safe than sorry
        require(
            block.timestamp > blockTimestampLast,
            "now must come after before"
        );
        uint256 timeElapsed = block.timestamp - blockTimestampLast;

        // overflow is desired, casting never truncates
        // cumulative price is in (uq112x112 price * seconds) units so we simply wrap it after division by time elapsed
        FixedPoint.uq112x112 memory priceAverage = FixedPoint.uq112x112(
            uint224(
                (priceCumulative - config.priceCumulativeLast) / timeElapsed
            )
        );
        uint256 rawUniswapPriceMantissa = priceAverage.decode112with18();

        // Divide by 1e28 because it's decoded to 18 and then we want 8 decimal places of precision out so 18+18-8
        return mul(rawUniswapPriceMantissa, config.baseUnit) / 1e28;
    }

    // This actually calculate the latest price from outside oracles
    // It's a view but substantially more costly in terms of calculation
    function price(string calldata symbol) external view returns (uint256) {
        bytes32 tokenSymbolHash = keccak256(abi.encodePacked(symbol));
        uint256 ethPrice = ethPriceOracle.price(ethSymbol); // grab the eth price from the open oracle

        if (ethHash == tokenSymbolHash) {
            return ethPrice;
        } else {
            SwapConfig storage config = swaps[tokenSymbolHash];
            uint256 priceCumulative = currentCumulativePrice(config);

            require(
                priceCumulative > config.priceCumulativeLast,
                "There has been no cumulative change"
            );
            // This should be impossible, but better safe than sorry
            require(
                block.timestamp > config.blockTimestampLast,
                "now must come after before"
            );
            uint256 timeElapsed = block.timestamp - config.blockTimestampLast;

            // overflow is desired, casting never truncates
            // cumulative price is in (uq112x112 price * seconds) units so we simply wrap it after division by time elapsed
            FixedPoint.uq112x112 memory priceAverage = FixedPoint.uq112x112(
                uint224(
                    (priceCumulative - config.priceCumulativeLast) / timeElapsed
                )
            );
            uint256 rawUniswapPriceMantissa = priceAverage.decode112with18();

            uint256 unscaledPriceMantissa = mul(
                rawUniswapPriceMantissa,
                ethPrice
            );

            return mul(unscaledPriceMantissa, config.baseUnit) / 1e36;
        }
    }

    function debugPrice(string calldata symbol)
        external
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        bytes32 tokenSymbolHash = keccak256(abi.encodePacked(symbol));
        uint256 ethPrice = ethPriceOracle.price(ethSymbol); // grab the eth price from the open oracle

        SwapConfig storage config = swaps[tokenSymbolHash];
        uint256 priceCumulative = currentCumulativePrice(config);

        require(
            priceCumulative > config.priceCumulativeLast,
            "There has been no cumulative change"
        );
        // This should be impossible, but better safe than sorry
        require(
            block.timestamp > config.blockTimestampLast,
            "now must come after before"
        );
        uint256 timeElapsed = block.timestamp - config.blockTimestampLast;
        FixedPoint.uq112x112 memory priceAverage = FixedPoint.uq112x112(
            uint224(
                (priceCumulative - config.priceCumulativeLast) / timeElapsed
            )
        );
        uint256 rawUniswapPriceMantissa = priceAverage.decode112with18();

        uint256 unscaledPriceMantissa = mul(rawUniswapPriceMantissa, ethPrice);

        // overflow is desired, casting never truncates
        // cumulative price is in (uq112x112 price * seconds) units so we simply wrap it after division by time elapsed

        return (
            priceCumulative - config.priceCumulativeLast,
            timeElapsed,
            rawUniswapPriceMantissa,
            unscaledPriceMantissa
        );
    }

    function openPrice(string calldata symbol) external view returns (uint256) {
        return ethPriceOracle.price(symbol);
    }

    function getSwapConfig(string calldata symbol)
        external
        view
        returns (SwapConfig memory)
    {
        bytes32 tokenSymbolHash = keccak256(abi.encodePacked(symbol));
        return swaps[tokenSymbolHash];
    }

    /// @dev Overflow proof multiplication
    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) return 0;
        uint256 c = a * b;
        require(c / a == b, "multiplication overflow");
        return c;
    }
}

