contracts/automation/AbstractLZBridgeHel.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import { AccessControlEnumerable } from "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import { ISafe } from "../interfaces/ISafe.sol";

import { IOFT, SendParam } from "@layerzerolabs/oft-evm/contracts/interfaces/IOFT.sol";
import { MessagingFee } from "@layerzerolabs/oapp-evm/contracts/oapp/OAppSender.sol";
import { OptionsBuilder } from "@layerzerolabs/oapp-evm/contracts/oapp/libs/OptionsBuilder.sol";

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

abstract contract AbstractLZBridgeHelperModule is AccessControlEnumerable {
    using OptionsBuilder for bytes;

    ISafe public immutable safeContract;

    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");

    modifier onlyOperator() {
        require(
            hasRole(OPERATOR_ROLE, msg.sender),
            "Caller is not an operator"
        );
        _;
    }

    constructor(address _safeContract) {
        safeContract = ISafe(_safeContract);
        _grantRole(DEFAULT_ADMIN_ROLE, address(safeContract));
        _grantRole(OPERATOR_ROLE, address(safeContract));
    }

    /**
     * @dev Bridges token using LayerZero.
     * @param lzEndpointId LayerZero endpoint id.
     * @param token Token to bridge.
     * @param lzAdapter LZ Adapter to use.
     * @param amount Amount of token to bridge.
     * @param slippageBps Slippage in 10^4 basis points.
     * @param isNativeToken Whether the token is native token.
     */
    function _bridgeTokenWithLz(
        uint32 lzEndpointId,
        IERC20 token,
        IOFT lzAdapter,
        uint256 amount,
        uint256 slippageBps,
        bool isNativeToken
    ) internal {
        bool success;

        if (!isNativeToken) {
            // Approve LZ Adapter to move the token
            success = safeContract.execTransactionFromModule(
                address(token),
                0, // Value
                abi.encodeWithSelector(
                    token.approve.selector,
                    address(lzAdapter),
                    amount
                ),
                0 // Call
            );
            require(success, "Failed to approve token");
        }

        // Calculate minimum amount to receive
        uint256 minAmount = (amount * (10000 - slippageBps)) / 10000;

        // Hardcoded gaslimit of 400k
        bytes memory options = OptionsBuilder
            .newOptions()
            .addExecutorLzReceiveOption(400000, 0);

        // Build send param
        SendParam memory sendParam = SendParam({
            dstEid: lzEndpointId,
            to: bytes32(uint256(uint160(address(safeContract)))),
            amountLD: amount,
            minAmountLD: minAmount,
            extraOptions: options,
            composeMsg: bytes(""),
            oftCmd: bytes("")
        });

        // Compute fees
        MessagingFee memory msgFee = lzAdapter.quoteSend(sendParam, false);

        uint256 value = isNativeToken
            ? amount + msgFee.nativeFee
            : msgFee.nativeFee;

        // Execute transaction
        success = safeContract.execTransactionFromModule(
            address(lzAdapter),
            value,
            abi.encodeWithSelector(
                lzAdapter.send.selector,
                sendParam,
                msgFee,
                address(safeContract)
            ),
            0
        );
        require(success, "Failed to bridge token");
    }

    /**
     * @dev Helps recovering any tokens accidentally sent to this module.
     * @param token Token to transfer. 0x0 to transfer Native token.
     * @param amount Amount to transfer. 0 to transfer all balance.
     */
    function transferTokens(address token, uint256 amount)
        external
        onlyOperator
    {
        if (address(token) == address(0)) {
            // Move ETH
            amount = amount > 0 ? amount : address(this).balance;
            payable(address(safeContract)).transfer(amount);
            return;
        }

        // Move all balance if amount set to 0
        amount = amount > 0 ? amount : IERC20(token).balanceOf(address(this));

        // Transfer to Safe contract
        // slither-disable-next-line unchecked-transfer unused-return
        IERC20(token).transfer(address(safeContract), amount);
    }

    receive() external payable {
        // Accept ETH to pay for bridge fees
    }
}

contracts/automation/EthereumBridgeHelpe.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import { AbstractLZBridgeHelperModule } from "./AbstractLZBridgeHelperModule.sol";

import { AccessControlEnumerable } from "@openzeppelin/contracts/access/AccessControlEnumerable.sol";

import { IOFT } from "@layerzerolabs/oft-evm/contracts/interfaces/IOFT.sol";

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { IERC4626 } from "../../lib/openzeppelin/interfaces/IERC4626.sol";
import { IWETH9 } from "../interfaces/IWETH9.sol";
import { IVault } from "../interfaces/IVault.sol";

contract EthereumBridgeHelperModule is
    AccessControlEnumerable,
    AbstractLZBridgeHelperModule
{
    IVault public constant vault =
        IVault(0x39254033945AA2E4809Cc2977E7087BEE48bd7Ab);
    IWETH9 public constant weth =
        IWETH9(0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2);
    IERC20 public constant oeth =
        IERC20(0x856c4Efb76C1D1AE02e20CEB03A2A6a08b0b8dC3);
    IERC4626 public constant woeth =
        IERC4626(0xDcEe70654261AF21C44c093C300eD3Bb97b78192);

    uint32 public constant LZ_PLUME_ENDPOINT_ID = 30370;
    IOFT public constant LZ_WOETH_OMNICHAIN_ADAPTER =
        IOFT(0x7d1bEa5807e6af125826d56ff477745BB89972b8);
    IOFT public constant LZ_ETH_OMNICHAIN_ADAPTER =
        IOFT(0x77b2043768d28E9C9aB44E1aBfC95944bcE57931);

    constructor(address _safeContract)
        AbstractLZBridgeHelperModule(_safeContract)
    {}

    /**
     * @dev Bridges wOETH to Plume.
     * @param woethAmount Amount of wOETH to bridge.
     * @param slippageBps Slippage in 10^4 basis points.
     */
    function bridgeWOETHToPlume(uint256 woethAmount, uint256 slippageBps)
        public
        payable
        onlyOperator
    {
        _bridgeTokenWithLz(
            LZ_PLUME_ENDPOINT_ID,
            woeth,
            LZ_WOETH_OMNICHAIN_ADAPTER,
            woethAmount,
            slippageBps,
            false
        );
    }

    /**
     * @dev Bridges wETH to Plume.
     * @param wethAmount Amount of wETH to bridge.
     * @param slippageBps Slippage in 10^4 basis points.
     */
    function bridgeWETHToPlume(uint256 wethAmount, uint256 slippageBps)
        public
        payable
        onlyOperator
    {
        // Unwrap into ETH
        safeContract.execTransactionFromModule(
            address(weth),
            0, // Value
            abi.encodeWithSelector(weth.withdraw.selector, wethAmount),
            0 // Call
        );

        _bridgeTokenWithLz(
            LZ_PLUME_ENDPOINT_ID,
            IERC20(address(weth)),
            LZ_ETH_OMNICHAIN_ADAPTER,
            wethAmount,
            slippageBps,
            true
        );
    }

    /**
     * @dev Mints OETH and wraps it into wOETH.
     * @param wethAmount Amount of WETH to mint.
     * @return Amount of wOETH minted.
     */
    function mintAndWrap(uint256 wethAmount)
        external
        onlyOperator
        returns (uint256)
    {
        return _mintAndWrap(wethAmount);
    }

    /**
     * @dev Mints OETH and wraps it into wOETH.
     * @param wethAmount Amount of WETH to mint.
     * @return Amount of wOETH minted.
     */
    function _mintAndWrap(uint256 wethAmount) internal returns (uint256) {
        // Approve Vault to move WETH
        bool success = safeContract.execTransactionFromModule(
            address(weth),
            0, // Value
            abi.encodeWithSelector(
                weth.approve.selector,
                address(vault),
                wethAmount
            ),
            0 // Call
        );
        require(success, "Failed to approve WETH");

        // Mint OETH
        success = safeContract.execTransactionFromModule(
            address(vault),
            0, // Value
            abi.encodeWithSelector(
                vault.mint.selector,
                address(weth),
                wethAmount,
                wethAmount
            ),
            0 // Call
        );
        require(success, "Failed to mint OETH");

        // Approve wOETH to move OETH
        success = safeContract.execTransactionFromModule(
            address(oeth),
            0, // Value
            abi.encodeWithSelector(
                oeth.approve.selector,
                address(woeth),
                wethAmount
            ),
            0 // Call
        );
        require(success, "Failed to approve OETH");

        uint256 woethAmount = woeth.balanceOf(address(safeContract));

        // Wrap OETH into wOETH
        success = safeContract.execTransactionFromModule(
            address(woeth),
            0, // Value
            abi.encodeWithSelector(
                woeth.deposit.selector,
                wethAmount,
                address(safeContract)
            ),
            0 // Call
        );
        require(success, "Failed to wrap OETH");

        // Compute amount of wOETH minted
        return woeth.balanceOf(address(safeContract)) - woethAmount;
    }

    /**
     * @dev Mints OETH and wraps it into wOETH, then bridges it to Plume.
     * @param wethAmount Amount of WETH to mint.
     * @param slippageBps Bridge slippage in 10^4 basis points.
     */
    function mintWrapAndBridgeToPlume(uint256 wethAmount, uint256 slippageBps)
        external
        payable
        onlyOperator
    {
        uint256 woethAmount = _mintAndWrap(wethAmount);
        bridgeWOETHToPlume(woethAmount, slippageBps);
    }

    /**
     * @dev Unwraps wOETH and redeems it to get WETH.
     * @param woethAmount Amount of wOETH to unwrap.
     * @return Amount of WETH received.
     */
    function unwrapAndRedeem(uint256 woethAmount)
        external
        onlyOperator
        returns (uint256)
    {
        return _unwrapAndRedeem(woethAmount);
    }

    /**
     * @dev Unwraps wOETH and redeems it to get WETH.
     * @param woethAmount Amount of wOETH to unwrap.
     * @return Amount of WETH received.
     */
    function _unwrapAndRedeem(uint256 woethAmount) internal returns (uint256) {
        uint256 oethAmount = oeth.balanceOf(address(safeContract));

        // Unwrap wOETH
        bool success = safeContract.execTransactionFromModule(
            address(woeth),
            0, // Value
            abi.encodeWithSelector(
                woeth.redeem.selector,
                woethAmount,
                address(safeContract),
                address(safeContract)
            ),
            0 // Call
        );
        require(success, "Failed to unwrap wOETH");

        oethAmount = oeth.balanceOf(address(safeContract)) - oethAmount;

        // Redeem OETH using Vault to get WETH
        success = safeContract.execTransactionFromModule(
            address(vault),
            0, // Value
            abi.encodeWithSelector(
                vault.redeem.selector,
                oethAmount,
                oethAmount
            ),
            0 // Call
        );
        require(success, "Failed to redeem OETH");

        return oethAmount;
    }

    /**
     * @dev Unwraps wOETH and redeems it to get WETH, then bridges it to Plume.
     * @param woethAmount Amount of wOETH to unwrap.
     * @param slippageBps Bridge slippage in 10^4 basis points.
     */
    function unwrapRedeemAndBridgeToPlume(
        uint256 woethAmount,
        uint256 slippageBps
    ) external payable onlyOperator {
        uint256 wethAmount = _unwrapAndRedeem(woethAmount);
        // Unwrap into ETH
        safeContract.execTransactionFromModule(
            address(weth),
            0, // Value
            abi.encodeWithSelector(weth.withdraw.selector, wethAmount),
            0 // Call
        );

        bridgeWETHToPlume(wethAmount, slippageBps);
    }
}

contracts/automation/PlumeBridgeHelperMo.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import { AbstractLZBridgeHelperModule } from "./AbstractLZBridgeHelperModule.sol";

import { AccessControlEnumerable } from "@openzeppelin/contracts/access/AccessControlEnumerable.sol";

import { IOFT } from "@layerzerolabs/oft-evm/contracts/interfaces/IOFT.sol";

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { IERC4626 } from "../../lib/openzeppelin/interfaces/IERC4626.sol";
import { IWETH9 } from "../interfaces/IWETH9.sol";
import { IVault } from "../interfaces/IVault.sol";

import { BridgedWOETHStrategy } from "../strategies/BridgedWOETHStrategy.sol";

contract PlumeBridgeHelperModule is
    AccessControlEnumerable,
    AbstractLZBridgeHelperModule
{
    IVault public constant vault =
        IVault(0xc8c8F8bEA5631A8AF26440AF32a55002138cB76a);
    IWETH9 public constant weth =
        IWETH9(0xca59cA09E5602fAe8B629DeE83FfA819741f14be);
    IERC20 public constant oethp =
        IERC20(0xFCbe50DbE43bF7E5C88C6F6Fb9ef432D4165406E);
    IERC4626 public constant bridgedWOETH =
        IERC4626(0xD8724322f44E5c58D7A815F542036fb17DbbF839);

    uint32 public constant LZ_ETHEREUM_ENDPOINT_ID = 30101;
    IOFT public constant LZ_WOETH_OMNICHAIN_ADAPTER =
        IOFT(0x592CB6A596E7919930bF49a27AdAeCA7C055e4DB);
    IOFT public constant LZ_ETH_OMNICHAIN_ADAPTER =
        IOFT(0x4683CE822272CD66CEa73F5F1f9f5cBcaEF4F066);

    BridgedWOETHStrategy public constant bridgedWOETHStrategy =
        BridgedWOETHStrategy(0x1E3EdD5e019207D6355Ea77F724b1F1BF639B569);

    constructor(address _safeContract)
        AbstractLZBridgeHelperModule(_safeContract)
    {}

    /**
     * @dev Bridges wOETH to Ethereum.
     * @param woethAmount Amount of wOETH to bridge.
     * @param slippageBps Slippage in 10^4 basis points.
     */
    function bridgeWOETHToEthereum(uint256 woethAmount, uint256 slippageBps)
        public
        payable
        onlyOperator
    {
        _bridgeTokenWithLz(
            LZ_ETHEREUM_ENDPOINT_ID,
            IERC20(address(bridgedWOETH)),
            LZ_WOETH_OMNICHAIN_ADAPTER,
            woethAmount,
            slippageBps,
            false
        );
    }

    /**
     * @dev Bridges wETH to Ethereum.
     * @param wethAmount Amount of wETH to bridge.
     * @param slippageBps Slippage in 10^4 basis points.
     */
    function bridgeWETHToEthereum(uint256 wethAmount, uint256 slippageBps)
        public
        payable
        onlyOperator
    {
        _bridgeTokenWithLz(
            LZ_ETHEREUM_ENDPOINT_ID,
            IERC20(address(weth)),
            LZ_ETH_OMNICHAIN_ADAPTER,
            wethAmount,
            slippageBps,
            false
        );
    }

    /**
     * @dev Deposits wOETH into the bridgedWOETH strategy.
     * @param woethAmount Amount of wOETH to deposit.
     * @param redeemWithVault Whether to redeem with Vault.
     * @return Amount of OETHp received.
     */
    function depositWOETH(uint256 woethAmount, bool redeemWithVault)
        external
        onlyOperator
        returns (uint256)
    {
        return _depositWOETH(woethAmount, redeemWithVault);
    }

    /**
     * @dev Deposits wOETH into the bridgedWOETH strategy and bridges it to Ethereum.
     * @param woethAmount Amount of wOETH to deposit.
     * @param slippageBps Slippage in 10^4 basis points.
     * @return Amount of WETH received.
     */
    function depositWOETHAndBridgeWETH(uint256 woethAmount, uint256 slippageBps)
        external
        payable
        onlyOperator
        returns (uint256)
    {
        uint256 wethAmount = _depositWOETH(woethAmount, true);
        bridgeWETHToEthereum(wethAmount, slippageBps);
        return wethAmount;
    }

    /**
     * @dev Deposits wOETH into the bridgedWOETH strategy.
     * @param woethAmount Amount of wOETH to deposit.
     * @param redeemWithVault Whether to redeem with Vault.
     * @return Amount of OETHp received.
     */
    function _depositWOETH(uint256 woethAmount, bool redeemWithVault)
        internal
        returns (uint256)
    {
        // Update oracle price
        bridgedWOETHStrategy.updateWOETHOraclePrice();

        // Rebase to account for any yields from price update
        vault.rebase();

        uint256 oethpAmount = oethp.balanceOf(address(safeContract));

        // Approve bridgedWOETH strategy to move wOETH
        bool success = safeContract.execTransactionFromModule(
            address(bridgedWOETH),
            0, // Value
            abi.encodeWithSelector(
                bridgedWOETH.approve.selector,
                address(bridgedWOETHStrategy),
                woethAmount
            ),
            0 // Call
        );

        // Deposit to bridgedWOETH strategy
        success = safeContract.execTransactionFromModule(
            address(bridgedWOETHStrategy),
            0, // Value
            abi.encodeWithSelector(
                bridgedWOETHStrategy.depositBridgedWOETH.selector,
                woethAmount
            ),
            0 // Call
        );
        require(success, "Failed to deposit bridged WOETH");

        oethpAmount = oethp.balanceOf(address(safeContract)) - oethpAmount;

        // Rebase to account for any yields from price update
        // and backing asset change from deposit
        vault.rebase();

        if (!redeemWithVault) {
            return oethpAmount;
        }

        // Redeem for WETH using Vault
        success = safeContract.execTransactionFromModule(
            address(vault),
            0, // Value
            abi.encodeWithSelector(
                vault.redeem.selector,
                oethpAmount,
                oethpAmount
            ),
            0 // Call
        );
        require(success, "Failed to redeem OETHp");

        return oethpAmount;
    }

    /**
     * @dev Deposits wETH into the vault.
     * @param wethAmount Amount of wETH to deposit.
     * @return Amount of OETHp received.
     */
    function depositWETHAndRedeemWOETH(uint256 wethAmount)
        external
        onlyOperator
        returns (uint256)
    {
        return _withdrawWOETH(wethAmount);
    }

    /**
     * @dev Deposits wETH into the vault and bridges it to Ethereum.
     * @param wethAmount Amount of wETH to deposit.
     * @param slippageBps Slippage in 10^4 basis points.
     * @return Amount of WOETH received.
     */
    function depositWETHAndBridgeWOETH(uint256 wethAmount, uint256 slippageBps)
        external
        payable
        onlyOperator
        returns (uint256)
    {
        uint256 woethAmount = _withdrawWOETH(wethAmount);
        bridgeWOETHToEthereum(woethAmount, slippageBps);
        return woethAmount;
    }

    /**
     * @dev Withdraws wOETH from the bridgedWOETH strategy.
     * @param wethAmount Amount of WETH to use to withdraw.
     * @return Amount of wOETH received.
     */
    function _withdrawWOETH(uint256 wethAmount) internal returns (uint256) {
        // Approve Vault to move WETH
        bool success = safeContract.execTransactionFromModule(
            address(weth),
            0, // Value
            abi.encodeWithSelector(
                weth.approve.selector,
                address(vault),
                wethAmount
            ),
            0 // Call
        );
        require(success, "Failed to approve WETH");

        // Mint OETHp with WETH
        success = safeContract.execTransactionFromModule(
            address(vault),
            0, // Value
            abi.encodeWithSelector(
                vault.mint.selector,
                address(weth),
                wethAmount,
                wethAmount
            ),
            0 // Call
        );
        require(success, "Failed to mint OETHp");

        // Approve bridgedWOETH strategy to move OETHp
        success = safeContract.execTransactionFromModule(
            address(oethp),
            0, // Value
            abi.encodeWithSelector(
                oethp.approve.selector,
                address(bridgedWOETHStrategy),
                wethAmount
            ),
            0 // Call
        );
        require(success, "Failed to approve OETHp");

        uint256 woethAmount = bridgedWOETH.balanceOf(address(safeContract));

        // Withdraw from bridgedWOETH strategy
        success = safeContract.execTransactionFromModule(
            address(bridgedWOETHStrategy),
            0, // Value
            abi.encodeWithSelector(
                bridgedWOETHStrategy.withdrawBridgedWOETH.selector,
                wethAmount
            ),
            0 // Call
        );
        require(success, "Failed to withdraw bridged WOETH");

        woethAmount =
            bridgedWOETH.balanceOf(address(safeContract)) -
            woethAmount;

        return woethAmount;
    }
}

contracts/governance/Governable.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

/**
 * @title Base for contracts that are managed by the Origin Protocol's Governor.
 * @dev Copy of the openzeppelin Ownable.sol contract with nomenclature change
 *      from owner to governor and renounce methods removed. Does not use
 *      Context.sol like Ownable.sol does for simplification.
 * @author Origin Protocol Inc
 */
abstract contract Governable {
    // Storage position of the owner and pendingOwner of the contract
    // keccak256("OUSD.governor");
    bytes32 private constant governorPosition =
        0x7bea13895fa79d2831e0a9e28edede30099005a50d652d8957cf8a607ee6ca4a;

    // keccak256("OUSD.pending.governor");
    bytes32 private constant pendingGovernorPosition =
        0x44c4d30b2eaad5130ad70c3ba6972730566f3e6359ab83e800d905c61b1c51db;

    // keccak256("OUSD.reentry.status");
    bytes32 private constant reentryStatusPosition =
        0x53bf423e48ed90e97d02ab0ebab13b2a235a6bfbe9c321847d5c175333ac4535;

    // See OpenZeppelin ReentrancyGuard implementation
    uint256 constant _NOT_ENTERED = 1;
    uint256 constant _ENTERED = 2;

    event PendingGovernorshipTransfer(
        address indexed previousGovernor,
        address indexed newGovernor
    );

    event GovernorshipTransferred(
        address indexed previousGovernor,
        address indexed newGovernor
    );

    /**
     * @notice Returns the address of the current Governor.
     */
    function governor() public view returns (address) {
        return _governor();
    }

    /**
     * @dev Returns the address of the current Governor.
     */
    function _governor() internal view returns (address governorOut) {
        bytes32 position = governorPosition;
        // solhint-disable-next-line no-inline-assembly
        assembly {
            governorOut := sload(position)
        }
    }

    /**
     * @dev Returns the address of the pending Governor.
     */
    function _pendingGovernor()
        internal
        view
        returns (address pendingGovernor)
    {
        bytes32 position = pendingGovernorPosition;
        // solhint-disable-next-line no-inline-assembly
        assembly {
            pendingGovernor := sload(position)
        }
    }

    /**
     * @dev Throws if called by any account other than the Governor.
     */
    modifier onlyGovernor() {
        require(isGovernor(), "Caller is not the Governor");
        _;
    }

    /**
     * @notice Returns true if the caller is the current Governor.
     */
    function isGovernor() public view returns (bool) {
        return msg.sender == _governor();
    }

    function _setGovernor(address newGovernor) internal {
        emit GovernorshipTransferred(_governor(), newGovernor);

        bytes32 position = governorPosition;
        // solhint-disable-next-line no-inline-assembly
        assembly {
            sstore(position, newGovernor)
        }
    }

    /**
     * @dev Prevents a contract from calling itself, directly or indirectly.
     * Calling a `nonReentrant` function from another `nonReentrant`
     * function is not supported. It is possible to prevent this from happening
     * by making the `nonReentrant` function external, and make it call a
     * `private` function that does the actual work.
     */
    modifier nonReentrant() {
        bytes32 position = reentryStatusPosition;
        uint256 _reentry_status;
        // solhint-disable-next-line no-inline-assembly
        assembly {
            _reentry_status := sload(position)
        }

        // On the first call to nonReentrant, _notEntered will be true
        require(_reentry_status != _ENTERED, "Reentrant call");

        // Any calls to nonReentrant after this point will fail
        // solhint-disable-next-line no-inline-assembly
        assembly {
            sstore(position, _ENTERED)
        }

        _;

        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        // solhint-disable-next-line no-inline-assembly
        assembly {
            sstore(position, _NOT_ENTERED)
        }
    }

    function _setPendingGovernor(address newGovernor) internal {
        bytes32 position = pendingGovernorPosition;
        // solhint-disable-next-line no-inline-assembly
        assembly {
            sstore(position, newGovernor)
        }
    }

    /**
     * @notice Transfers Governance of the contract to a new account (`newGovernor`).
     * Can only be called by the current Governor. Must be claimed for this to complete
     * @param _newGovernor Address of the new Governor
     */
    function transferGovernance(address _newGovernor) external onlyGovernor {
        _setPendingGovernor(_newGovernor);
        emit PendingGovernorshipTransfer(_governor(), _newGovernor);
    }

    /**
     * @notice Claim Governance of the contract to a new account (`newGovernor`).
     * Can only be called by the new Governor.
     */
    function claimGovernance() external {
        require(
            msg.sender == _pendingGovernor(),
            "Only the pending Governor can complete the claim"
        );
        _changeGovernor(msg.sender);
    }

    /**
     * @dev Change Governance of the contract to a new account (`newGovernor`).
     * @param _newGovernor Address of the new Governor
     */
    function _changeGovernor(address _newGovernor) internal {
        require(_newGovernor != address(0), "New Governor is address(0)");
        _setGovernor(_newGovernor);
    }
}

